--
-- Cấu trúc bảng cho bảng `product`
--

CREATE TABLE `product` (
  `id` int(11) NOT NULL,
  `name` varchar(100) DEFAULT NULL,
  `slug` varchar(100) DEFAULT NULL,
  `price` int(11) DEFAULT NULL,
  `sale` tinyint(4) DEFAULT 0,
  `pay` int(11) DEFAULT 0,
  `thumbar` varchar(100) DEFAULT NULL,
  `category_id` int(11) DEFAULT NULL,
  `content` text DEFAULT NULL,
  `number` int(11) DEFAULT 0,
  `head` int(11) DEFAULT 0,
  `short_description` text DEFAULT NULL,
  `view` int(11) DEFAULT 0,
  `hot` tinyint(4) DEFAULT 0,
  `created_at` timestamp NULL DEFAULT current_timestamp(),
  `updated_at` timestamp NULL DEFAULT current_timestamp() ON UPDATE current_timestamp(),
  `home` tinyint(4) DEFAULT 1
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_general_ci ROW_FORMAT=COMPACT;

--
-- Đang đổ dữ liệu cho bảng `product`
--

INSERT INTO `product` (`id`, `name`, `slug`, `price`, `sale`, `pay`, `thumbar`, `category_id`, `content`, `number`, `head`, `short_description`, `view`, `hot`, `created_at`, `updated_at`, `home`) VALUES
(17, 'iPhone 14 Pro Max 128G', 'iphone-14-pro-max-128g', 29990000, 0, 0, 'iphone14.jpg', 8, '<h3><a title=\"Tham khảo điện thoại đang kinh doanh tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd/iphone-14-pro-max\" target=\"_blank\" rel=\"noopener\">iPhone 14 Pro Max</a> một siêu phẩm trong giới smartphone được nhà Táo tung ra thị trường vào tháng 09/2022. Máy trang bị con chip Apple A16 Bionic vô cùng mạnh mẽ, đi kèm theo đó là thiết kế hình màn hình mới, hứa hẹn mang lại những trải nghiệm đầy mới mẻ cho người dùng iPhone.</h3>\r\n<h3>Thiết kế cao cấp bền bỉ</h3>\r\n<p>iPhone năm nay sẽ được thừa hưởng nét đặc trưng từ người anh <a title=\"Tham khảo iPhone 13 Pro Max tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd/iphone-13-pro-max-1tb\" target=\"_blank\" rel=\"noopener\">iPhone 13 Pro Max</a>, vẫn sẽ là khung thép không gỉ và mặt lưng kính cường lực kết hợp với tạo hình vuông vức hiện đại thông qua cách tạo hình phẳng ở các cạnh và phần mặt lưng.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103221.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Thiết kế sang trọng - iPhone 14 Pro Max 128GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103221.jpg\" alt=\"Thiết kế sang trọng - iPhone 14 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103221.jpg\" /></a></p>\r\n<p>Dòng Pro năm nay được ra mắt với nhiều tùy chọn màu sắc và trong đó nổi bật nhất có lẽ là phiên bản Deep Purple (tím), bởi vì đây là một màu mới nên mang lại độ nhận diện khá cao.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103227.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Màu sắc mới mẻ - iPhone 14 Pro Max 128GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103227.jpg\" alt=\"Màu sắc mới mẻ - iPhone 14 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103227.jpg\" /></a></p>\r\n<h3>Nổi bật với thiết kế màn hình mới</h3>\r\n<p>Điểm ấn tượng nhất trên <a title=\"Xem thêm iPhone chính hãng đang kinh doanh tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd-apple-iphone\" target=\"_blank\" rel=\"noopener\">điện thoại iPhone</a> năm nay nằm ở thiết kế màn hình, có thể dễ dàng nhận thấy được là hãng cũng đã loại bỏ cụm tai thỏ truyền thống qua bao đời iPhone bằng một hình dáng mới vô cùng lạ mắt.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103229.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Thiết kế hình notch mới - iPhone 14 Pro Max 128GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103229.jpg\" alt=\"Thiết kế hình notch mới - iPhone 14 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103229.jpg\" /></a></p>\r\n<p>So với cụm tai thỏ hình notch năm nay đã có phần tiết kiệm diện tích tương đối tốt, nhưng khi so với các kiểu màn hình dạng “nốt ruồi” thì đây vẫn chưa thực sự là một điều quá tối ưu cho phần màn hình. Thế nhưng Apple lại rất biết cách tận dụng những nhược điểm để biến chúng trở thành ưu điểm một cách ngoạn mục bằng cách phát minh nhiều hiệu ứng thú vị.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103232.jpg\"><img class=\" lazyloaded\" title=\"Kích thước nhỏ gọn - iPhone 14 Pro Max 128GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103232.jpg\" alt=\"Kích thước nhỏ gọn - iPhone 14 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103232.jpg\" /></a></p>\r\n<p>Để làm cho chúng trở nên bắt mắt hơn Apple cũng đã giới thiệu nhiều hiệu ứng chuyển động nhằm làm tăng sự thích thú cho người dùng, điều này được kích hoạt trong lúc mình ấn giữ phần hình notch khi đang dùng các phần mềm hỗ trợ như: Nghe nhạc, đồng hồ hẹn giờ, ghi âm,...</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103235.jpg\"><img class=\" lazyloaded\" title=\"Hiệu ứng sinh động - iPhone 14 Pro Max 128GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103235.jpg\" alt=\"Hiệu ứng sinh động - iPhone 14 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103235.jpg\" /></a></p>\r\n<h3>Hiển thị nội dung chân thực hơn thông qua màn hình chất lượng</h3>\r\n<p>Từ trước đến nay, tấm nền OLED luôn được cộng đồng người dùng đánh giá rất cao khi xuất hiện trên các dòng sản phẩm của Apple, với chiếc iPhone 14 Pro Max điều này cũng không là ngoại lệ khi hình ảnh mà máy mang lại hết sức chân thực.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103237.jpg\"><img class=\" lazyloaded\" title=\"Màn hình cao cấp - iPhone 14 Pro Max 128GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103237.jpg\" alt=\"Màn hình cao cấp - iPhone 14 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103237.jpg\" /></a></p>\r\n<p>Nhìn vào màn hình ta sẽ thấy được một độ trong trẻo vô cùng thích mắt, nội dung hiển thị có chiều sâu, cảm giác khi xem phim hay chơi game cũng trở nên sống động hơn.</p>\r\n<p>Điểm vượt trội nhất mà các loại tấm nền khác khó mà đạt được là độ chuẩn các về màu sắc cao, mình cũng đã sử dụng iPhone 14 Pro Max để thiết kế một vài ấn phẩm như poster hay sticker đơn giản và thành quả lúc in ra sau khi đối chiếu lại với ảnh trên <a title=\"Xem thêm Điện thoại chính hãng, giá rẻ đang kinh doanh tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" rel=\"noopener\">điện thoại</a> thì gần như là bằng nhau, độ sai lệch không quá đáng kể giúp mình an tâm hơn trong việc thiết kế hình ảnh.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103240.jpg\"><img class=\" lazyloaded\" title=\"Độ chuẩn xác về màu cao - iPhone 14 Pro Max 128GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103240.jpg\" alt=\"Độ chuẩn xác về màu cao - iPhone 14 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103240.jpg\" /></a></p>\r\n<p>Độ phân giải màn hình đạt mức 2796 x 1290 Pixels nên việc tái tạo hình ảnh có độ chi tiết cao là điều hết sức dễ dàng, hiện tượng như rỗ màn hình là điều mà mình chưa từng thấy xuất hiện trên điện thoại trong 2 tuần trải nghiệm.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103242.jpg\"><img class=\" lazyloaded\" title=\"Độ chi tiết cực lớn - iPhone 14 Pro Max 128GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103242.jpg\" alt=\"Độ chi tiết cực lớn - iPhone 14 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103242.jpg\" /></a></p>\r\n<h4><strong>Hỗ trợ tính năng Always On Display</strong></h4>\r\n<p>Bên cạnh sự đổi mới về hình notch, năm nay Apple còn trang bị cho dòng Pro và Pro Max thêm công nghệ màn hình Always On Display, điều này cho phép thiết bị không tắt màn hình hẳn mà vẫn hiển thị màn hình khóa với một độ sáng khá nhỏ, đủ để mình có thể đọc thông báo nhanh xem ngày giờ khá thuận tiện.</p>\r\n<p>Xem thêm: <a href=\"https://www.thegioididong.com/hoi-dap/always-on-display-la-gi-co-hao-pin-khong-huong-dan-1169208\" target=\"_blank\" rel=\"noopener\">Always On Display là gì? Có tốn pin không? Cách bật, tắt</a></p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103239.jpg\"><img class=\" lazyloaded\" title=\"TÍnh năng trên màn hình - iPhone 14 Pro Max 128GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103239.jpg\" alt=\"TÍnh năng trên màn hình - iPhone 14 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103239.jpg\" /></a></p>\r\n<p>Tuy nhiên màn hình lúc này khá tối nên dùng ngoài trời sẽ hơi khó nhìn đôi chút. Bên cạnh đó, tính năng này cũng sẽ giảm tuổi thọ pin hơn so với thông thường, để điện thoại qua đêm khoảng 7 tiếng ở chế độ Always On Display thì máy mất khoảng 3 - 4% dung lượng pin.</p>\r\n<h4><strong>Tần số quét 120 Hz</strong></h4>\r\n<p>iPhone 14 Pro Max có tần số quét rất cao vậy nên mọi thao tác hằng ngày của mình đều được phản hồi nhanh chóng, cảm giác vuốt chạm cũng trở nên thích hơn, hiện tượng khựng hay độ trễ trong lúc vuốt đã được cải thiện đáng kể so với những chuẩn màn hình 60 Hz.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103244.jpg\"><img class=\" lazyloaded\" title=\"Vuốt chạm mượt mà - iPhone 14 Pro Max 128GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103244.jpg\" alt=\"Vuốt chạm mượt mà - iPhone 14 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103244.jpg\" /></a></p>\r\n<p>Ngoài ra, Apple còn bổ sung tùy chọn để giúp bạn có thể dễ dàng điều chỉnh tần số quét lên 120 Hz hay xuống 60 Hz tùy vào nhu cầu sử dụng, phù hợp để dùng trong những tình huống khẩn cấp như đang muốn tiết kiệm pin nhằm đảm bảo thiết bị có thể duy trì sử dụng dài lâu.</p>\r\n<h4><strong>Độ sáng 2000 nits</strong></h4>\r\n<p>So với những đối thủ khác trong phân khúc thì độ sáng 2000 nits được xem là ưu điểm vượt trội mà ít có hãng nào có thể sánh kịp, thế liệu nó giúp ích được gì và có thực sự hiệu quả hay không chắc hẳn là điều được rất nhiều người thắc mắc.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103246.jpg\"><img class=\" lazyloaded\" title=\"Độ sáng màn hình cao - iPhone 14 Pro Max 128GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103246.jpg\" alt=\"Độ sáng màn hình cao - iPhone 14 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103246.jpg\" /></a></p>\r\n<p>Cụ thể là màn hình có độ sáng cao đã giúp ích cho mình rất nhiều trong mọi nhu cầu sử dụng hằng ngày của mình, kể từ khi sử dụng iPhone 14 Pro Max việc xem bản đồ khi đang ở ngoài đường lúc trời nắng gắt cũng trở nên dễ dàng, màn hình lúc này không quá sập tối hay lóa, các thông báo chỉ dẫn đường đi cũng đều được thể hiện rõ ràng.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103249.jpg\"><img class=\" lazyloaded\" title=\"Độ sáng màn hình cao - iPhone 14 Pro Max 128GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103249.jpg\" alt=\"Độ sáng màn hình cao - iPhone 14 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103249.jpg\" /></a></p>\r\n<p>Bên cạnh đó, khi chụp ảnh ở ngoài trời nắng độ sáng màn hình cao cũng sẽ là một lợi thế, nó hữu ích trong lúc mình căn chỉnh góc chụp khi mọi đường lưới đều được hiển thị rõ, chủ thể đã được lấy nét hay chưa thì mình cũng có thể dễ dàng quan sát trực tiếp ngay trên màn hình mà không cần dùng tay để che chắn ánh sáng chiếu vào như phần lớn các dòng điện thoại khác.</p>\r\n<h4><strong>Mặt kính cường lực Ceramic Shield</strong></h4>\r\n<p>Ceramic Shield có lẽ không còn là thuật ngữ quá xa lạ đối với phần lớn người dùng khi đây được xem là loại công nghệ đã từng xuất hiện ở thế hệ iPhone 12, chuẩn này nổi tiếng với độ bền bỉ cực cao và khả năng kháng lại vết xước rất tốt.</p>\r\n<p>Thực tế, mình đã kiểm tra độ bền của mặt kính này thông qua việc xây xát các loại vật dụng khác dễ tiếp xúc như: Móc khóa, đầu dây sạc, bút bi và cả gọng kính. Kết quả mà mình nhận được cũng hết sức ấn tượng, màn hình gần như không bị tổn hại khi không thấy xuất hiện của bất kỳ một vết xước nào cả.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103250.jpg\"><img class=\" lazyloaded\" title=\"Mặt kính kháng xước - iPhone 14 Pro Max 128GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103250.jpg\" alt=\"Mặt kính kháng xước - iPhone 14 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103250.jpg\" /></a></p>\r\n<p>Tuy nhiên đây cũng là một bài kiểm tra nên lực dùng để xây xát là không quá lớn, vì thế người dùng cũng không nên quá chủ quan trước những tình huống làm rơi. Điều này chỉ giúp mình an tâm hơn trong việc đặt úp điện thoại lên mặt phẳng hay để cùng với các loại vật dụng khác mà không lo bị trầy xước.</p>\r\n<h3>Bắt trọn mọi khoảnh khắc thông qua camera chính 48 MP</h3>\r\n<h4><strong>Ảnh chụp chế độ thường</strong></h4>\r\n<p>Trước khi đi vào phần đánh giá trải nghiệm mình cùng điểm sơ qua thông số camera của iPhone 14 Pro Max, thiết bị năm nay vẫn sẽ được bố trí với cụm 3 camera, trong đó cảm biến chính có độ phân giải 48 MP, cảm biến góc rộng 12 MP và cuối cùng là ống kính tele 12 MP. Với thông số ấn tượng như vậy nên máy hứa hẹn sẽ trở thành <a title=\"Tham khảo điện thoại chụp ảnh, quay phim tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd-chup-anh-quay-phim\" target=\"_blank\" rel=\"noopener\">điện thoại chụp ảnh, quay phim</a> hàng đầu trong năm 2022.</p>\r\n<p>Ảnh chụp từ điều kiện thường cho ra kết quả làm mình rất ưng ý, hình ảnh được tái tạo với độ chi tiết cao, màu sắc hiển thị rất chân thực, ảnh khi zoom sẽ không bị bể quá nhiều giúp cho tác vụ phóng to hay thay đổi khung hình không bị thay đổi quá nhiều về mặt chất lượng.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103252.jpg\"><img class=\" lazyloaded\" title=\"Ảnh chụp từ camera - iPhone 14 Pro Max 128GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103252.jpg\" alt=\"Ảnh chụp từ camera - iPhone 14 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103252.jpg\" /></a></p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103256.jpg\"><img class=\" lazyloaded\" title=\"Ảnh chụp từ camera - iPhone 14 Pro Max 128GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103256.jpg\" alt=\"Ảnh chụp từ camera - iPhone 14 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103256.jpg\" /></a></p>\r\n<p>Điểm ưng ý nhất về phần camera chính là về khả năng cân bằng màu cực tốt, chất lượng ảnh cho ra có gam màu trung tính, không xảy ra hiện tượng ám vàng hay ám xanh như trên một vài chiếc smartphone của những đối thủ khác trên thị trường. Điều này giúp việc chỉnh sửa ảnh trở nên dễ dàng hơn bởi mình không cần tinh chỉnh sắc độ để đưa chúng về màu ban đầu nữa.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103259.jpg\"><img class=\" lazyloaded\" title=\"Ảnh chụp từ camera - iPhone 14 Pro Max 128GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103259.jpg\" alt=\"Ảnh chụp từ camera - iPhone 14 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103259.jpg\" /></a></p>\r\n<h4><strong>Ảnh chụp góc siêu rộng</strong></h4>\r\n<p>Còn đối với chế độ chụp góc siêu rộng thông qua cảm biến phụ 12 MP kết quả thu được làm mình rất ưng ý, bức ảnh chụp vẫn có độ chi tiết cao, mọi khung cảnh trước mắt mình gần như được đưa hết vào trong bức ảnh, những vùng mở rộng cũng không hề bị bóp méo hay giảm chất lượng ảnh giúp cho tổng thể bức hình trở nên rất chất lượng.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103301.jpg\"><img class=\" lazyloaded\" title=\"Ảnh chụp từ camera - iPhone 14 Pro Max 128GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103301.jpg\" alt=\"Ảnh chụp từ camera - iPhone 14 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103301.jpg\" /></a></p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103303.jpg\"><img class=\" lazyloaded\" title=\"Ảnh chụp từ camera - iPhone 14 Pro Max 128GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103303.jpg\" alt=\"Ảnh chụp từ camera - iPhone 14 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103303.jpg\" /></a></p>\r\n<h4><strong>Ảnh chụp ngược sáng</strong></h4>\r\n<p>Bị tối mặt hay lóa sáng được xem là tình huống phổ biến đối với các smartphone thông thường, thế nhưng trên chiếc iPhone 14 Pro Max thì điều này đã được cải thiện đi đáng kể thông qua tính năng Smart HDR 4.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103306.jpg\"><img class=\" lazyloaded\" title=\"Ảnh chụp từ camera - iPhone 14 Pro Max 128GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103306.jpg\" alt=\"Ảnh chụp từ camera - iPhone 14 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103306.jpg\" /></a></p>\r\n<p>Khi đối mặt với những nguồn có độ sáng cao điện thoại sẽ tự động nhận diện chủ thể và khung cảnh, từ đó tăng sáng những vùng cần lấy nét tập trung và giảm độ sáng cho những vị trí còn lại để giúp cho bức ảnh của mình trở nên dễ nhìn hơn, ngoài ra nó còn giúp cho bức ảnh của mình được tăng thêm phần nghệ thuật.</p>\r\n<h4><strong>Ảnh chụp tele</strong></h4>\r\n<p>Ở chế độ chụp tele điện thoại hỗ trợ cho chúng ta khá nhiều mức độ điều chỉnh từ 2X, 3X cho đến 5X và 10X,...Tuy nhiên máy sẽ chỉ hỗ trợ zoom quang học tối đa lên đến 3X và với các mức độ lớn hơn thì điện thoại sẽ chuyển sang chế độ zoom kỹ thuật số.</p>\r\n<p>Ảnh chụp từ chế độ zoom quang học máy sẽ sử dụng một camera riêng biệt có độ phân giải 12 MP để lấy nét, chất lượng ảnh cho ra vẫn sẽ có độ chi tiết cao, mọi thứ được lưu giữ gần như nguyên bản và không mấy khác biệt so với những bức ảnh chụp từ chế độ mặc định, tuy nhiên để tránh hiểu lầm thì đây cũng chỉ là một ví dụ so sánh nếu như không soi xét quá kỹ về tỷ lệ độ phân giải của cả hai.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103308.jpg\"><img class=\" lazyloaded\" title=\"Ảnh chụp từ camera - iPhone 14 Pro Max 128GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103308.jpg\" alt=\"Ảnh chụp từ camera - iPhone 14 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103308.jpg\" /></a></p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103310.jpg\"><img class=\" lazyloaded\" title=\"Ảnh chụp từ camera - iPhone 14 Pro Max 128GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103310.jpg\" alt=\"Ảnh chụp từ camera - iPhone 14 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103310.jpg\" /></a></p>\r\n<p>Còn ở bức ảnh chụp từ tính năng zoom kỹ thuật số bức ảnh mà mình có chụp được vẫn khá ổn, ở đây camera của điện thoại sẽ làm việc với nguyên lý tương tự như bạn đang crop bức ảnh của mình với khung hình và tỷ lệ nhỏ hơn, vì thế chất lượng ảnh sẽ có phần kém hơn so với chụp mặc định.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103312.jpg\"><img class=\" lazyloaded\" title=\"Ảnh chụp từ camera - iPhone 14 Pro Max 128GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103312.jpg\" alt=\"Ảnh chụp từ camera - iPhone 14 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103312.jpg\" /></a></p>\r\n<p>Nhưng nhờ camera chính có độ phân giải lớn và độ chi tiết thu được khá cao, cho nên khi chụp bằng chế độ zoom kỹ thuật số thì ảnh vẫn có độ chi tiết tốt, mọi vật thể và khung cảnh đều được thu lại tương đối rõ ràng.</p>\r\n<h4><strong>Ảnh chụp khung cảnh nhiều màu sắc</strong></h4>\r\n<p>Năm nay thì Apple truyền thông khá nhiều về các tính năng phụ cũng như loại công nghệ trên camera của iPhone 14 Pro Max, được nhắc đến nhiều có lẽ là những cái tên như: Deep Fusion, Smart HDR 4 và Dolby Vision HDR.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103314.jpg\"><img class=\" lazyloaded\" title=\"Ảnh chụp từ camera - iPhone 14 Pro Max 128GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103314.jpg\" alt=\"Ảnh chụp từ camera - iPhone 14 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103314.jpg\" /></a></p>\r\n<p>Đây được xem là những loại công nghệ gần như chưa có mặt trên bất kỳ một chiếc điện thoại Android nào cả, vậy nên đây chính là lợi thế vượt trội mà mình đánh giá rất cao trên camera của iPhone 14 Pro Max.</p>\r\n<p>Thực tế thì ảnh chụp của điện thoại cho ra có màu rất giống so với ngoài đời thật, độ chi tiết cực cao, những vùng có màu sắc riêng biệt không thấy xảy ra tình trạng pha trộn lẫn nhau, tổng thể bức ảnh làm cho mình có cảm giác nhìn rất đã mắt.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103316.jpg\"><img class=\" lazyloaded\" title=\"Ảnh chụp từ camera - iPhone 14 Pro Max 128GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103316.jpg\" alt=\"Ảnh chụp từ camera - iPhone 14 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103316.jpg\" /></a></p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103318.jpg\"><img class=\" lazyloaded\" title=\"Ảnh chụp từ camera - iPhone 14 Pro Max 128GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103318.jpg\" alt=\"Ảnh chụp từ camera - iPhone 14 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103318.jpg\" /></a></p>\r\n<p>Bởi vì được hỗ trợ tính năng Deep Fusion nên máy có khả năng chụp tổng cộng là 9 tấm trong quá trình mình chụp một bức hình, từ đó máy sẽ tự động phân tích để đưa ra bức hình có độ chi tiết cũng như chất lượng tốt nhất để hiển thị. Vậy nên trong khúc chụp mình có xảy ra tình trạng run tay nhẹ thì ảnh cho ra vẫn không hề bị nhòe hay mất nét.</p>\r\n<h3>Hiệu năng đáng kinh ngạc với chip set Apple A16 Bionic</h3>\r\n<p>Theo thông lệ từ trước đến nay của Apple thì mỗi lần ra mắt dòng điện thoại mới là nhà táo lại trình làng thêm một con chip với hiệu năng cực khủng và ở thế hệ điện iPhone 14 Series sẽ là con chip Apple A16 Bionic 6 nhân với xung nhịp tối đa lên đến 3.46 GHz.</p>\r\n<p>Xem thêm: <a href=\"https://www.thegioididong.com/hoi-dap/chip-a16-bionic-giup-iphone-14-lan-at-hieu-nang-1461587\" target=\"_blank\" rel=\"noopener\">Đánh giá chip Apple A16 Bionic chi tiết về thông số và hiệu năng</a></p>\r\n<p>Theo như thông số mình đo được qua các phần mềm con chip Apple A16 Bionic đạt được mức điểm như sau: 1887 (đơn nhân), 5354 (đa nhân) trên Benchmark và 15508 điểm đánh giá tổng thể. Đây quả thực là những con số quá khủng khiếp, dường như chip đã vượt mặt gần như toàn bộ các vi xử lý hiện đang có mặt trên thị trường di động trong năm 2022.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103320.jpg\"><img class=\" lazyloaded\" title=\"Ảnh chụp từ camera - iPhone 14 Pro Max 128GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103320.jpg\" alt=\"Hiệu năng mạnh mẽ - iPhone 14 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103320.jpg\" /></a></p>\r\n<p>Bởi vì cầm trên tay một chiếc máy có hiệu năng vô đối như vậy cho nên mình cũng đã tự tin mở ngay Genshin Impact với mức đồ họa cao nhất để trải nghiệm, chất lượng hình ảnh cho ra phải nói là vô cùng đẹp mắt, mọi hiệu ứng đồ họa trong game đều được tái hiện rõ nét, các pha hành động như đánh nhau, tung chiêu đều được thể hiện mượt mà và ổn định.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103323.jpg\"><img class=\" lazyloaded\" title=\"Ảnh chụp từ camera - iPhone 14 Pro Max 128GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103323.jpg\" alt=\"Hiệu năng mạnh mẽ - iPhone 14 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103323.jpg\" /></a></p>\r\n<p>Bên cạnh đó, đây còn là một chiếc <a title=\"Tham khảo điện thoại đang kinh doanh tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd-ram-6gb\" target=\"_blank\" rel=\"noopener\">điện thoại RAM 6 GB</a> cho nên các thao tác về phần đa nhiệm đều được máy xử lý dễ dàng, dùng đồng thời nhiều ứng dụng cũng không hề xảy ra tình trạng khựng hay load lại từ đầu mỗi khi mình mở lại.</p>\r\n<p>Ở phiên bản này điện thoại sẽ được tích hợp sẵn hệ điều hành iOS 16 kể từ khi kích hoạt máy, được bổ sung hàng loạt các tính năng mới mẻ nên mình cảm thấy rất là hứng khởi trong quá trình trải nghiệm máy.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103324.jpg\"><img class=\" lazyloaded\" title=\"Hiệu năng mạnh mẽ - iPhone 14 Pro Max 128GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103324.jpg\" alt=\"Hiệu năng mạnh mẽ - iPhone 14 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103324.jpg\" /></a></p>\r\n<p>Những tính năng nổi bật và cực kỳ hữu ích trên iOS 16 có thể kể đến như: Tùy chỉnh màn hình khóa, thêm widget mới cho màn hình khóa, Live Text, tách nền ảnh,... đều được mình sử dụng hằng ngày một cách tiện lợi cho công việc và kể cả những nhu cầu giải trí.</p>\r\n<p>Bên trong điện thoại sẽ được tích hợp viên pin có dung lượng lên tới 4323 mAh nên mình cũng đã an tâm hơn trong việc sử dụng máy phục vụ các tác vụ cơ bản cả ngày. Cụ thể là trong một ngày trải nghiệm máy mình đã mất khoảng 7 giờ 23 phút sử dụng liên tục để có thể dùng hết 100% pin trên điện thoại.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103327.jpg\"><img class=\" lazyloaded\" title=\"Pin trâu dùng lâu - iPhone 14 Pro Max 128GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103327.jpg\" alt=\"Pin trâu dùng lâu - iPhone 14 Pro Max 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/251192/iphone-14-pro-max-041222-103327.jpg\" /></a></p>\r\n<p>Với những bạn đang mong muốn có cho mình một thiết bị có ngoại hình đẹp, hiệu năng cao và kể cả chụp ảnh quay phim chuyên nghiệm thì iPhone 14 Pro Max có thể coi là sự lựa chọn rất phù hợp cho năm 2022 và 2023 sắp tới. So với những gì mà thiết bị mang lại cho chúng ta thì mức giá bán của điện thoại được xem là cực kỳ xứng đáng.</p>', 100, 0, '\r\n<p>Màn hình: <span class=\"comma\">OLED</span><span class=\"comma\">6.7\"</span><span class=\"\">Super Retina XDR</span></p>\r\n<p class=\"lileft\">Hệ điều hành: <span class=\"\">iOS 16</span></p>\r\n<p class=\"lileft\">Camera sau: <span class=\"\">Chính 48 MP &amp; Phụ 12 MP, 12 MP</span></p>\r\n<p class=\"lileft\">Camera trước: <span class=\"\">12 MP</span></p>\r\n<p class=\"lileft\">Chip: <span class=\"\">Apple A16 Bionic</span></p>\r\n<p class=\"lileft\">RAM: <span class=\"\">6 GB</span></p>\r\n<p class=\"lileft\">Dung lượng lưu trữ: <span class=\"\">128 GB</span></p>\r\n<p class=\"lileft\">SIM: <span class=\"comma\">1 Nano SIM &amp; 1 eSIM</span><span class=\"\">Hỗ trợ 5G</span></p>\r\n<p class=\"lileft\">Pin, Sạc: <span class=\"comma\">4323 mAh</span><span class=\"\">20 W</span></p>', 0, 0, '2023-04-11 12:06:02', '2023-04-11 18:53:35', 1),
(18, 'iPhone 12 Pro Max 128GB', 'iphone-12-pro-max-128gb', 16990000, 0, 2, 'ip12.jpg', 8, '', 98, 0, '<p>Điện thoại iPhone 12 Pro Max 128GB</p>\r\n<p>Màn hình: <span class=\"comma\">OLED</span><span class=\"comma\">6.1\"</span><span class=\"\">Super Retina XDR</span></p>\r\n<p class=\"lileft\">Hệ điều hành: <span class=\"\">iOS 16</span></p>\r\n<p class=\"lileft\">Camera sau: <span class=\"\">Chính 48 MP &amp; Phụ 12 MP, 12 MP</span></p>\r\n<p class=\"lileft\">Camera trước: <span class=\"\">12 MP</span></p>\r\n<p class=\"lileft\">Chip: <span class=\"\">Apple A14 Bionic</span></p>\r\n<p class=\"lileft\">RAM: <span class=\"\">6 GB</span></p>\r\n<p class=\"lileft\">Dung lượng lưu trữ: <span class=\"\">128 GB</span></p>\r\n<p class=\"lileft\">SIM: <span class=\"comma\">1 Nano SIM &amp; 1 eSIM</span><span class=\"\">Hỗ trợ 5G</span></p>\r\n<p class=\"lileft\">Pin, Sạc: <span class=\"comma\">4323 mAh</span><span class=\"\">20 W</span></p>', 0, 0, '2023-04-11 13:59:37', '2023-04-27 00:27:54', 1),
(19, 'iPhone Xs Max 128GB ', 'iphone-xs-max-128gb', 7490000, 0, 0, 'ipXSM.jpg', 8, '<h3>Ho&agrave;n to&agrave;n xứng đ&aacute;ng với những g&igrave; được mong chờ, phi&ecirc;n bản cao cấp nhất&nbsp;<a title=\"Chi tiết điện thoại iPhone XS Max\" href=\"https://www.thegioididong.com/dtdd/iphone-xs-max\" target=\"_blank\" rel=\"noopener\" type=\"Chi tiết điện thoại iPhone XS Max\">iPhone Xs Max 64 GB</a>&nbsp;của Apple năm nay nổi bật với chip A12 Bionic mạnh mẽ, m&agrave;n h&igrave;nh rộng đến 6.5 inch, c&ugrave;ng camera k&eacute;p tr&iacute; tuệ nh&acirc;n tạo v&agrave; Face ID được n&acirc;ng cấp.</h3>\r\n<h3>Thiết kế cao cấp với viền th&eacute;p kh&ocirc;ng gỉ v&agrave; mặt k&iacute;nh cường lực</h3>\r\n<p><a title=\"Tham khảo c&aacute;c d&ograve;ng điện thoại tại Thegioididong.com\" href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" rel=\"noopener\" type=\"Tham khảo c&aacute;c d&ograve;ng điện thoại tại Thegioididong.com\">Điện thoại</a>&nbsp;iPhone Xs Max sở hữu lối thiết kế v&ocirc; c&ugrave;ng đẹp mắt với những đường cong mềm mại được thừa hưởng từ chiếc&nbsp;<a title=\"Tham khảo c&aacute;c d&ograve;ng điện thoại iPhone\" href=\"https://www.thegioididong.com/dtdd-apple-iphone\" target=\"_blank\" rel=\"noopener\" type=\"Tham khảo c&aacute;c d&ograve;ng điện thoại iPhone\">iPhone</a>&nbsp;đời trước đ&oacute;.</p>\r\n<h3><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-5.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Thiết kế điện thoại iPhone Xs Max ch&iacute;nh h&atilde;ng\" src=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-5.jpg\" alt=\"Thiết kế điện thoại iPhone Xs Max ch&iacute;nh h&atilde;ng\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-5.jpg\" /></a></h3>\r\n<p>Tuy nhi&ecirc;n,&nbsp;iPhone Xs Max lại c&oacute; một th&acirc;n h&igrave;nh to bản ngang bằng với k&iacute;ch thước d&ograve;ng Plus nhưng chứa đựng một m&agrave;n h&igrave;nh rộng lớn l&ecirc;n đến 6.5 inch.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-10.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"M&agrave;n h&igrave;nh điện thoại iPhone Xs Max ch&iacute;nh h&atilde;ng\" src=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-10.jpg\" alt=\"M&agrave;n h&igrave;nh điện thoại iPhone Xs Max ch&iacute;nh h&atilde;ng\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-10.jpg\" /></a></p>\r\n<p>Nhờ thế m&agrave; bạn sẽ c&oacute; một kh&ocirc;ng gian trải nghiệm v&ocirc; c&ugrave;ng rộng r&atilde;i để thưởng thức những bộ phim chất lượng cao được trở n&ecirc;n trọn vẹn.</p>\r\n<h3><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-1.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Thiết kế điện thoại iPhone Xs Max ch&iacute;nh h&atilde;ng\" src=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-1.jpg\" alt=\"Thiết kế điện thoại iPhone Xs Max ch&iacute;nh h&atilde;ng\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-1.jpg\" /></a></h3>\r\n<p>Xem th&ecirc;m:&nbsp;<a title=\"Tr&ecirc;n tay v&agrave; đ&aacute;nh gi&aacute; nhanh iPhone Xs Max tại Việt Nam\" href=\"https://www.thegioididong.com/tin-tuc/tren-tay-va-danh-gia-nhanh-iphone-xs-max-tai-viet-nam-1118861\" target=\"_blank\" rel=\"noopener\" type=\"Tr&ecirc;n tay v&agrave; đ&aacute;nh gi&aacute; nhanh iPhone Xs Max tại Việt Nam\">Tr&ecirc;n tay v&agrave; đ&aacute;nh gi&aacute; nhanh iPhone Xs Max tại Việt Nam: Sức h&uacute;t kh&oacute; cưỡng</a></p>\r\n<h3><a title=\"T&igrave;m hiểu về m&agrave;n h&igrave;nh OLED\" href=\"https://www.thegioididong.com/tin-tuc/man-hinh-oled-la-gi-smartphone-nao-co-man-hinh-oled-1048951\" target=\"_blank\" rel=\"noopener\" type=\"T&igrave;m hiểu về m&agrave;n h&igrave;nh OLED\">M&agrave;n h&igrave;nh OLED</a>&nbsp;chất lượng cao rộng 6.5 inch đầu ti&ecirc;n của Apple</h3>\r\n<p>Với c&ocirc;ng nghệ Super Retina kết hợp tấm nền OLED tr&ecirc;n&nbsp;<a title=\"Tham khảo một số mẫu điện thoại đang kinh doanh tại Topzone\" href=\"https://www.topzone.vn/iphone\" target=\"_blank\" rel=\"noopener\">điện thoại&nbsp;iPhone</a>&nbsp;đem lại dải m&agrave;u sắc cực k&igrave; sống động v&agrave; sắc n&eacute;t đến từng chi tiết.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-8.jpg\"><img class=\" lazyloaded\" title=\"M&agrave;n h&igrave;nh điện thoại iPhone Xs Max ch&iacute;nh h&atilde;ng\" src=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-8.jpg\" alt=\"M&agrave;n h&igrave;nh điện thoại iPhone Xs Max ch&iacute;nh h&atilde;ng\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-8.jpg\" /></a></p>\r\n<p>B&ecirc;n cạnh đ&oacute;, Apple c&ograve;n t&iacute;ch hợp th&ecirc;m c&ocirc;ng nghệ HDR10 gi&uacute;p chất lượng h&igrave;nh ảnh được n&acirc;ng cao v&agrave; mượt m&agrave; hơn đ&aacute;ng kể.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-4.jpg\"><img class=\" lazyloaded\" title=\"M&agrave;n h&igrave;nh điện thoại iPhone Xs Max ch&iacute;nh h&atilde;ng\" src=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-4.jpg\" alt=\"M&agrave;n h&igrave;nh điện thoại iPhone Xs Max ch&iacute;nh h&atilde;ng\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-4.jpg\" /></a></p>\r\n<p>Việc sở hữu m&agrave;n h&igrave;nh lớn đem đến cho bạn kh&aacute; nhiều tiện &iacute;ch như dễ d&agrave;ng chỉnh sửa ảnh, xem phim, lướt web nhưng sẽ kh&oacute; khăn hơn trong việc di chuyển.</p>\r\n<h3>Hiệu năng đỉnh của đỉnh với&nbsp;<a title=\"T&igrave;m hiểu chip Apple A12\" href=\"https://www.thegioididong.com/tin-tuc/chi-tiet-chip-apple-a12-bionic-ben-trong-iphone-xs-xs-max-1116982\" target=\"_blank\" rel=\"noopener\" type=\"T&igrave;m hiểu chip Apple A12\">chip Apple A12</a></h3>\r\n<p>L&agrave; một flagship cao cấp,&nbsp;iPhone Xs Max được Apple trang bị cho con chip mới toanh h&agrave;ng đầu của h&atilde;ng mang t&ecirc;n Apple A12 bionic.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-3-1.jpg\"><img class=\" lazyloaded\" title=\"Điểm Antutu Benchmark iPhone Xs Max\" src=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-3-1.jpg\" alt=\"Điểm Antutu Benchmark iPhone Xs Max\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-3-1.jpg\" /></a></p>\r\n<p>Chip A12 bionic được x&acirc;y dựng tr&ecirc;n tiến tr&igrave;nh 7nm đầu ti&ecirc;n m&agrave; h&atilde;ng sản xuất với 6 nh&acirc;n đ&aacute;p ứng vượt trội trong việc xử l&yacute; c&aacute;c t&aacute;c vụ v&agrave; khả năng tiết kiệm năng lượng tối ưu.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-12.jpg\"><img class=\" lazyloaded\" title=\"Chơi game tr&ecirc;n điện thoại iPhone Xs Max ch&iacute;nh h&atilde;ng\" src=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-12.jpg\" alt=\"Chơi game tr&ecirc;n điện thoại iPhone Xs Max ch&iacute;nh h&atilde;ng\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-12.jpg\" /></a></p>\r\n<p>Hơn nữa,&nbsp;iPhone Xs Max c&ograve;n c&oacute; bộ xử l&yacute; đồ họa mạnh mẽ được Apple thiết kế ri&ecirc;ng gi&uacute;p hiệu năng được cải thiện rất lớn về mặt đồ họa của m&aacute;y.</p>\r\n<h2><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-6.jpg\"><img class=\" lazyloaded\" title=\"Trải nghiệm điện thoại iPhone Xs Max ch&iacute;nh h&atilde;ng\" src=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-6.jpg\" alt=\"Trải nghiệm điện thoại iPhone Xs Max ch&iacute;nh h&atilde;ng\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-6.jpg\" /></a></h2>\r\n<p>Chưa dừng lại ở đ&oacute;, m&aacute;y c&ograve;n được t&iacute;ch hợp tr&iacute; th&ocirc;ng minh nh&acirc;n tạo gi&uacute;p phần cứng tối ưu hiệu suất, nhờ đ&oacute; m&agrave; c&aacute;c thao t&aacute;c của bạn được xử l&yacute; một c&aacute;ch nhanh ch&oacute;ng hơn.</p>\r\n<p>Xem th&ecirc;m:&nbsp;<a title=\"Đ&aacute;nh gi&aacute; chi tiết iPhone Xs Max\" href=\"https://www.thegioididong.com/tin-tuc/danh-gia-iphone-xs-max-review-dang-mua-1128868\" target=\"_blank\" rel=\"noopener\" type=\"Đ&aacute;nh gi&aacute; chi tiết iPhone Xs Max\">Đ&aacute;nh gi&aacute; chi tiết iPhone Xs Max: Qu&aacute; x&aacute; đ&atilde;! C&oacute; tiền n&ecirc;n mua liền!</a></p>\r\n<h3>Ấn tượng về&nbsp;<a title=\"T&igrave;m hiểu về cẩm k&eacute;p tr&ecirc;n điện thoại\" href=\"https://www.thegioididong.com/tin-tuc/camera-kep-la-gi-co-phai-la-xu-huong-tren-smartphone-2017-953888\" target=\"_blank\" rel=\"noopener\" type=\"T&igrave;m hiểu về cẩm k&eacute;p tr&ecirc;n điện thoại\">camera k&eacute;p</a>&nbsp;t&iacute;ch hợp tr&iacute; tuệ nh&acirc;n tạo</h3>\r\n<p>D&ugrave; kh&ocirc;ng sở hữu th&ocirc;ng số camera khủng nhưng&nbsp;iPhone XS Max lu&ocirc;n cho thấy sự đẳng cấp của m&igrave;nh về khả năng nhiếp ảnh với cụm camera k&eacute;p c&oacute; c&ugrave;ng độ ph&acirc;n giải 12 MP.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-1-1.jpg\"><img class=\" lazyloaded\" title=\"Ảnh được chụp bằng camera k&eacute;p tr&ecirc;n iPhone Xs Max\" src=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-1-1.jpg\" alt=\"Ảnh được chụp bằng camera k&eacute;p tr&ecirc;n iPhone Xs Max\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-1-1.jpg\" /></a></p>\r\n<p>M&aacute;y được trang bị hệ thống xử l&yacute; h&igrave;nh ảnh chất lượng&nbsp;c&acirc;n bằng s&aacute;ng, giảm nhiễu, tăng cường độ phơi s&aacute;ng, m&agrave;u da sao cho ph&ugrave; hợp v&agrave; tự nhi&ecirc;n nhất.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-3.jpg\"><img class=\" lazyloaded\" title=\"Camera sau điện thoại iPhone Xs Max ch&iacute;nh h&atilde;ng\" src=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-3.jpg\" alt=\"Camera sau điện thoại iPhone Xs Max ch&iacute;nh h&atilde;ng\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-3.jpg\" /></a></p>\r\n<p>C&ugrave;ng với đ&oacute; l&agrave; khả năng điều chỉnh khẩu độ ấn tượng từ f/1.4 đến f/16 ngay tr&ecirc;n bức ảnh sau khi chụp ảnh với chế độ ch&acirc;n dung.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-2-1.jpg\"><img class=\" lazyloaded\" title=\"Ảnh selfie tr&ecirc;n iPhone Xs Max\" src=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-2-1.jpg\" alt=\"Ảnh selfie tr&ecirc;n iPhone Xs Max\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-2-1.jpg\" /></a></p>\r\n<p>Chưa dừng lại ở đ&oacute;, m&aacute;y c&ograve;n được t&iacute;ch hợp th&ecirc;m c&ocirc;ng nghệ&nbsp;Smart HDR gi&uacute;p t&aacute;i tạo h&igrave;nh ảnh v&agrave; cho ra một bức h&igrave;nh với độ s&aacute;ng tốt nhất.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-2.jpg\"><img class=\" lazyloaded\" title=\"Camera sau điện thoại iPhone Xs Max ch&iacute;nh h&atilde;ng\" src=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-2.jpg\" alt=\"Camera sau điện thoại iPhone Xs Max ch&iacute;nh h&atilde;ng\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-2.jpg\" /></a></p>\r\n<p>Ngo&agrave;i ra,&nbsp;iPhone XS Max c&ograve;n được hỗ trợ bởi tr&iacute; th&ocirc;ng minh nh&acirc;n tạo đem đến khả năng tự động điều chỉnh m&agrave;u sắc, độ s&aacute;ng v&agrave; độ tương phản sao cho ph&ugrave; hợp với từng vật thể kh&aacute;c nhau.</p>\r\n<h3>Một số t&iacute;nh năng cao cấp được cập nhật v&agrave; bổ sung</h3>\r\n<p><a title=\"T&igrave;m hiểu về t&iacute;nh năng Face ID tr&ecirc;n điện thoại iPhone\" href=\"https://www.thegioididong.com/hoi-dap/face-id-la-gi-va-cach-hoat-dong-cua-face-id-1021871\" target=\"_blank\" rel=\"noopener\" type=\"T&igrave;m hiểu về t&iacute;nh năng Face ID tr&ecirc;n điện thoại iPhone\">Face ID</a>&nbsp;đ&atilde; được Apple cải tiến về khả năng bảo mật cũng như cho tốc độ mở kh&oacute;a được nhanh hơn nhờ c&aacute;c thuật to&aacute;n mới.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-7.jpg\"><img class=\" lazyloaded\" title=\"Mở kho&aacute; điện thoại iPhone Xs Max ch&iacute;nh h&atilde;ng\" src=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-7.jpg\" alt=\"Mở kho&aacute; điện thoại iPhone Xs Max ch&iacute;nh h&atilde;ng\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-7.jpg\" /></a></p>\r\n<p>B&ecirc;n cạnh đ&oacute;, t&iacute;nh năng&nbsp;<a title=\"T&igrave;m hiểu về Animoji tr&ecirc;n điện thoại Apple\" href=\"https://www.thegioididong.com/tin-tuc/giai-thich-su-khac-nhau-giua-ar-emoji-cua-samsung-va-animoji-cua-apple-1070052\" target=\"_blank\" rel=\"noopener\" type=\"T&igrave;m hiểu về Animoji tr&ecirc;n điện thoại Apple\">Animoji</a>&nbsp;cũng được cập nhật th&ecirc;m một số biểu tượng mới tr&ocirc;ng kh&aacute; ngộ nghĩnh v&agrave; đ&aacute;ng y&ecirc;u.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-11.jpg\"><img class=\" lazyloaded\" title=\"Animojis tr&ecirc;n điện thoại iPhone Xs Max ch&iacute;nh h&atilde;ng\" src=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-11.jpg\" alt=\"Animojis tr&ecirc;n điện thoại iPhone Xs Max ch&iacute;nh h&atilde;ng\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-11.jpg\" /></a></p>\r\n<p>Với&nbsp;hệ thống camera&nbsp;<a title=\"T&igrave;m hiểu về c&ocirc;ng nghệ TrueDepth\" href=\"https://www.thegioididong.com/tin-tuc/mot-su-that-bat-ngo-ve-camera-truedepth-tren-iphone-x-1021510\" target=\"_blank\" rel=\"noopener\" type=\"T&igrave;m hiểu về c&ocirc;ng nghệ TrueDepth\">TrueDepth</a>&nbsp;nay bạn c&oacute; thể tự tạo cho bản th&acirc;n những bức ảnh ấn tượng với c&ocirc;ng nghệ thực tế ảo tăng cường AR.</p>\r\n<p>Ngo&agrave;i ra, hệ thống &acirc;m thanh 2 chiều tr&ecirc;n&nbsp;<a title=\"Đặt gạch si&ecirc;u phẩm mới iPhone 2018 \" href=\"https://www.thegioididong.com/sieu-pham-moi\" target=\"_blank\" rel=\"noopener\" type=\"Đặt gạch si&ecirc;u phẩm mới iPhone 2018 \">si&ecirc;u phẩm mới</a>&nbsp;được Apple tinh chỉnh lại cho dải &acirc;m rộng, &acirc;m thanh sống động hơn hay khả năng&nbsp;<a title=\"Tham khảo điện thoại c&oacute; chống nước, bụi tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd-chong-nuoc-bui\" target=\"_blank\" rel=\"noopener\">chống nước</a>&nbsp;v&agrave; bụi cũng được n&acirc;ng cấp l&ecirc;n th&agrave;nh IP 68 đảm bảo an to&agrave;n hơn cho m&aacute;y.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-9.jpg\"><img class=\" lazyloaded\" title=\"Đ&aacute;nh gi&aacute; điện thoại iPhone Xs Max ch&iacute;nh h&atilde;ng\" src=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-9.jpg\" alt=\"Đ&aacute;nh gi&aacute; điện thoại iPhone Xs Max ch&iacute;nh h&atilde;ng\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/190321/iphone-xs-max-gold-9.jpg\" /></a></p>', 100, 0, '<p class=\"lileft\">Điện thoại iPhone Xs Max 128GB&nbsp;</p>\r\n<p class=\"lileft\">M&agrave;n h&igrave;nh:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span class=\"comma\">OLED</span><span class=\"comma\">6.5\"</span><span class=\"\">Super Retina</span></p>\r\n<p class=\"lileft\">Hệ điều h&agrave;nh:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span class=\"\">iOS 14</span></p>\r\n<p class=\"lileft\">Camera sau:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span class=\"\">2 camera 12 MP</span></p>\r\n<p class=\"lileft\">Camera trước:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span class=\"\">7 MP</span></p>\r\n<p class=\"lileft\">Chip:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span class=\"\">Apple A12 Bionic</span></p>\r\n<p class=\"lileft\">RAM:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span class=\"\">4 GB</span></p>\r\n<p class=\"lileft\">Dung lượng lưu trữ:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span class=\"\">64 GB</span></p>\r\n<p class=\"lileft\">SIM:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span class=\"comma\">1 Nano SIM &amp; 1 eSIM</span><span class=\"\">Hỗ trợ 4G</span></p>\r\n<p class=\"lileft\">Pin, Sạc:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span class=\"\">3174 mAh</span></p>', 0, 0, '2023-04-11 14:16:29', '2023-04-11 14:17:04', 1);
INSERT INTO `product` (`id`, `name`, `slug`, `price`, `sale`, `pay`, `thumbar`, `category_id`, `content`, `number`, `head`, `short_description`, `view`, `hot`, `created_at`, `updated_at`, `home`) VALUES
(20, 'iPad Pro 11 inch M2 Wifi 256GB', 'ipad-pro-11-inch-m2-wifi-256gb', 31990000, 0, 1, 'ipad.jpg', 8, '<h3><a title=\"Tham khảo thông tin máy tính bảng iPad Pro M2 11 inch WiFi 256GB tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/may-tinh-bang/ipad-pro-m2-11-wifi-256gb\" target=\"_blank\" rel=\"noopener\">iPad Pro M2 11 inch WiFi 256GB</a> - chiếc máy tính bảng thuộc dòng cao cấp của Apple, mang trong mình sức mạnh đến từ con chip Apple M2 mới nhất và sử dụng thiết kế đặc trưng dòng iPad Pro, nhiều cải tiến phần mềm cùng một mức giá không đổi.</h3>\r\n<h3>Thiết kế đặc trưng nhà Apple</h3>\r\n<p>iPad Pro M2 11 inch ra mắt với ngoại hình dường như là không đổi khi so với thế hệ <a title=\"Tham khảo thông tin iPad Pro M1 11 inch WiFi 128GB (2021) tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/may-tinh-bang/ipad-pro-m1-11-inch-wifi-128gb-2021\" target=\"_blank\" rel=\"noopener\">iPad Pro M1 11 inch</a>, máy vẫn giữ thiết kế vuông ở các cạnh, bo cong ở các góc và vát phẳng ở hai mặt, và đây cũng là một thiết bị có viền màn hình siêu mỏng cùng độ mỏng thân máy chỉ 5.9 mm cho ra một thiết kế cực kỳ sexy.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/522/295454/ipad-pro-m2-11-wifi-256gb-1.jpeg\"><img class=\" ls-is-cached lazyloaded\" title=\"iPad Pro M2 11 inch WiFi 256GB - Thiết kế hiện đại\" src=\"https://cdn.tgdd.vn/Products/Images/522/295454/ipad-pro-m2-11-wifi-256gb-1.jpeg\" alt=\"iPad Pro M2 11 inch WiFi 256GB - Thiết kế hiện đại\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/295454/ipad-pro-m2-11-wifi-256gb-1.jpeg\" /></a></p>\r\n<p>Chiếc <a title=\"Tham khảo thông tin iPad kinh doanh tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/may-tinh-bang-apple-ipad\" target=\"_blank\" rel=\"noopener\">iPad</a> này được trang bị bốn loa, hai loa ở cạnh trên và hai loa ở cạnh dưới có hỗ trợ công nghệ Dolby Atmos cho bạn trải nghiệm âm thanh sôi động như rạp chiếu phim.</p>\r\n<p>iPad Pro M2 cũng có thể biến thành một chiếc laptop nếu kết nối với Magic Keyboard, ngoài ra máy cũng phù hợp với Apple Pencil 2 cho bạn sử dụng với các tác vụ viết, vẽ trở nên thật hơn.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/522/295454/ipad-pro-m2-11-wifi-256gb-5.jpeg\"><img class=\" ls-is-cached lazyloaded\" title=\"Hỗ trợ Apple Pencil 2 - iPad Pro M2 11 inch WiFi 256GB\" src=\"https://cdn.tgdd.vn/Products/Images/522/295454/ipad-pro-m2-11-wifi-256gb-5.jpeg\" alt=\"Hỗ trợ Apple Pencil 2 - iPad Pro M2 11 inch WiFi 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/295454/ipad-pro-m2-11-wifi-256gb-5.jpeg\" /></a></p>\r\n<h3>Camera kép nhiều tính năng</h3>\r\n<p><a title=\"Tham khảo thông tin máy tính bảng kinh doanh tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\" rel=\"noopener\">Máy tính bảng</a> iPad Pro M2 hỗ trợ cụm hai camera bao gồm một camera góc rộng 12 MP và một camera góc siêu rộng 10 MP, hơn nữa máy còn có thêm một cảm biến LiDAR giúp bạn đo khoảng cách tới mục tiêu bằng cách chiếu sáng mục tiêu đó một tia laser để phục vụ cho các tác vụ như scan các mô hình 3D bằng ứng dụng.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/522/295454/ipad-pro-m2-11-wifi-256gb-3.jpeg\"><img class=\" lazyloaded\" title=\"Máy quét 3D - iPad Pro M2 11 inch WiFi 256GB\" src=\"https://cdn.tgdd.vn/Products/Images/522/295454/ipad-pro-m2-11-wifi-256gb-3.jpeg\" alt=\"Máy quét 3D - iPad Pro M2 11 inch WiFi 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/295454/ipad-pro-m2-11-wifi-256gb-3.jpeg\" /></a></p>\r\n<p>Camera góc siêu rộng có trường nhìn 125 độ, hỗ trợ tính năng hiệu chỉnh ống kính giúp hình ảnh ở các góc không bị bóp méo nhiều, chất lượng cũng đã được nâng cao.</p>\r\n<p>Camera trước trên <a title=\"Tham khảo thêm iPad Pro M2 đang kinh doanh tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/may-tinh-bang-apple-ipad-pro-m2\" target=\"_blank\" rel=\"noopener\">iPad Pro M2</a> đem đến góc nhìn 122 độ nên khi gọi video sẽ sử dụng được tính năng Center Stage, giúp bạn luôn là trung tâm của khung ảnh.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/522/295454/ipad-pro-m2-11-wifi-256gb-8.jpeg\"><img class=\" lazyloaded\" title=\"Center Stage - iPad Pro M2 11 inch WiFi 256GB\" src=\"https://cdn.tgdd.vn/Products/Images/522/295454/ipad-pro-m2-11-wifi-256gb-8.jpeg\" alt=\"Center Stage - iPad Pro M2 11 inch WiFi 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/295454/ipad-pro-m2-11-wifi-256gb-8.jpeg\" /></a></p>\r\n<p>Camera trước và camera sau đều hỗ trợ các tính năng như chống rung, Smart HDR 4, hỗ trợ quay video điện ảnh cho bạn dễ dàng tạo ra những bức ảnh, video đẹp ngay cả khi là người không chuyên.</p>\r\n<h3>Viền màn hình siêu mỏng</h3>\r\n<div>iPad Pro M2 11 inch sử dụng màn hình Liquid Retina độc quyền với độ phân giải 1668 x 2388 Pixels, hỗ trợ công nghệ màn hình thích ứng (ProMotion) giúp tự động điều chỉnh tần số quét từ 10 - 120 Hz tuỳ vào tác vụ sử dụng nhằm tiết kiệm năng lượng cho máy.</div>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/522/295454/ipad-pro-m2-11-wifi-256gb-4.jpeg\"><img class=\" lazyloaded\" title=\"Màn hình lớn - iPad Pro M2 11 inch WiFi 256GB\" src=\"https://cdn.tgdd.vn/Products/Images/522/295454/ipad-pro-m2-11-wifi-256gb-4.jpeg\" alt=\"Màn hình lớn - iPad Pro M2 11 inch WiFi 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/295454/ipad-pro-m2-11-wifi-256gb-4.jpeg\" /></a></p>\r\n<p>Ngoài ra, máy cũng hỗ trợ các công nghệ khác như dải màu rộng DCI-P3, màn hình True Tone cùng độ sáng tối đa 600 nits cho trải nghiệm xem trên iPad đã tốt nay còn tốt hơn.</p>\r\n<h3>Hiệu năng hàng đầu với chip nhà làm</h3>\r\n<p>Năm nay iPad Pro được trang bị con chip Apple M2, đây là thế hệ tiếp theo của Apple M1 - dòng chip do Apple tự phát triển. Apple M2 sở hữu 8 lõi CPU mang lại hiệu suất nhanh hơn 15%* và GPU 10 lõi cung cấp hiệu suất đồ họa nhanh hơn tới 35% khi so với iPad Pro thế hệ trước.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/522/295454/ipad-pro-m2-11-wifi-256gb-2.jpeg\"><img class=\" lazyloaded\" title=\"Chip M2 - iPad Pro M2 11 inch WiFi 256GB\" src=\"https://cdn.tgdd.vn/Products/Images/522/295454/ipad-pro-m2-11-wifi-256gb-2.jpeg\" alt=\"Chip M2 - iPad Pro M2 11 inch WiFi 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/295454/ipad-pro-m2-11-wifi-256gb-2.jpeg\" /></a></p>\r\n<p>Và đây cũng là chiếc <a title=\"Tham khảo máy tính bảng RAM 8 GB tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/may-tinh-bang-ram-8gb\" target=\"_blank\" rel=\"noopener\">máy tính bảng RAM 8 GB</a> nên bạn có thể tạo các thiết kế 3D, xây dựng các mô hình AR phức tạp và chơi trò chơi với đồ họa chất lượng cao ở tốc độ khung hình cao nhanh hơn bao giờ hết.</p>\r\n<h3>Pin thoải mái sử dụng cả ngày</h3>\r\n<p>iPad Pro 11 inch là chiếc <a title=\"Tham khảo máy tính bảng pin khủng kinh doanh tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/may-tinh-bang-pin-khung\" target=\"_blank\" rel=\"noopener\">máy tính bảng pin khủng</a> khi trang bị viên pin đến 28.65 Wh tương đương ~7538 mAh, kết hợp cùng con chip M2 tiết kiệm năng lượng thì máy cho ra thời gian sử dụng khoảng 10 giờ khi xem video, lướt web bằng wifi.</p>\r\n<p>Một điểm khá vui là trong hộp iPad Pro năm nay vẫn sẽ được trang bị đầy đủ phụ kiện như củ sạc 20 W, dây sạc Type-C để bạn sử dụng.</p>\r\n<p>iPad Pro M2 11 inch WiFi 256 GB tuy không có nhiều sự thay đổi về ngoại hình nhưng bên trong về phần cứng, phần mềm đã thay đổi nhiều để phù hợp, hỗ trợ tốt hơn cho người dùng, dù bạn sử dụng cho công việc cơ bản như học sinh, sinh viên hay làm sáng tạo nội dung kể cả quay, dựng video thì máy đều có thể hỗ trợ vô cùng mượt mà.</p>', 99, 0, '<p>Kích thước màn hình:           12.9 inches</p>\r\n<p>Công nghệ màn hình:            Liquid Retina</p>\r\n<p>Camera sau:                          camera góc rộng: 12MP, camera góc siêu rộng: 10MP</p>\r\n<p>Camera trước:                      12MP, 122°, ƒ/2.4</p>\r\n<p>Chipset Apple:                       M2 8 nhân</p>\r\n<p>Dung lượng RAM:                 8 GB</p>\r\n<p>Bộ nhớ trong:                        128 GB</p>\r\n<p>Pin:                                        40.88 Wh (~ 10.835 mAh)</p>\r\n<p>Hệ điều hành:                        iPadOS 16</p>\r\n<p>Độ phân giải màn hình:         2732 x 2048 pixels</p>', 0, 0, '2023-04-11 14:29:41', '2023-04-21 05:26:11', 1),
(21, 'Samsung Galaxy Z Flip4 128GB', 'samsung-galaxy-z-flip4-128gb', 23990000, 25, 0, 'flip.jpg', 9, '<h3><a title=\"Tham khảo điện thoại đang kinh doanh tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-z-flip4\" target=\"_blank\" rel=\"noopener\">Samsung Galaxy Z Flip4 128GB</a>&nbsp;đ&atilde; ch&iacute;nh thức ra mắt thị trường c&ocirc;ng nghệ, đ&aacute;nh dấu sự trở lại của Samsung tr&ecirc;n con đường định hướng người d&ugrave;ng về sự tiện lợi tr&ecirc;n những chiếc điện thoại gập. Với độ bền được gia tăng c&ugrave;ng kiểu thiết kế đẹp mắt gi&uacute;p Flip4 trở th&agrave;nh một trong những t&acirc;m điểm s&aacute;ng gi&aacute; cho nửa cuối năm 2022.</h3>\r\n<h3>Dẫn đầu xu hướng thiết kế mới&nbsp;</h3>\r\n<p>C&oacute; lẽ điện thoại gập đ&atilde; kh&ocirc;ng c&ograve;n l&agrave; c&aacute;i t&ecirc;n qu&aacute; xa lạ bởi nhiều &ocirc;ng lớn trong ng&agrave;nh sản xuất thiết bị di động đ&atilde; cho ra mắt kh&aacute; nhiều sản phẩm c&oacute; thiết kế tương tự, gần đ&acirc;y nhất th&igrave; c&oacute; sự g&oacute;p mặt của chiếc flagship đến từ nh&agrave; Samsung mang t&ecirc;n Galaxy Z Flip4.&nbsp;</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100334.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Thiết kế cao cấp - Samsung Galaxy Z Flip4\" src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100334.jpg\" alt=\"Thiết kế cao cấp - Samsung Galaxy Z Flip4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100334.jpg\" /></a></p>\r\n<p>Ngay từ những gi&acirc;y ph&uacute;t đầu ti&ecirc;n sử dụng chiếc Galaxy Z Flip4 m&igrave;nh đ&atilde; cảm nhận được sự kh&aacute;c biệt của n&oacute; so với thế hệ trước, m&aacute;y b&acirc;y giờ đ&atilde; vu&ocirc;ng vắn hơn nhờ tạo h&igrave;nh v&aacute;t phẳng ở hai mặt v&agrave; c&aacute;c cạnh.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100336.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Ngoại h&igrave;nh vu&ocirc;ng vức - Samsung Galaxy Z Flip4\" src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100336.jpg\" alt=\"Ngoại h&igrave;nh vu&ocirc;ng vức - Samsung Galaxy Z Flip4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100336.jpg\" /></a></p>\r\n<p>Phần mặt lưng của m&aacute;y được phủ một lớp nh&aacute;m nhẹ gi&uacute;p cho m&igrave;nh c&oacute; thể cầm nắm chắc tay hơn, hạn chế được t&igrave;nh trạng b&aacute;m dấu v&acirc;n tay trong qu&aacute; tr&igrave;nh sử dụng.&nbsp;</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100343.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Ho&agrave;n thiện mặt lưng nh&aacute;m - Samsung Galaxy Z Flip4\" src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100343.jpg\" alt=\"Ho&agrave;n thiện mặt lưng nh&aacute;m - Samsung Galaxy Z Flip4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100343.jpg\" /></a></p>\r\n<p>Theo như h&atilde;ng c&ocirc;ng bố th&igrave; phi&ecirc;n bản&nbsp;<a title=\"Tham khảo điện thoại đang kinh doanh tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd-samsung-galaxy-z\" target=\"_blank\" rel=\"noopener\">điện thoại Galaxy Z</a>&nbsp;n&agrave;y c&oacute; thể gập l&ecirc;n đến 200.000 lần li&ecirc;n tục trong ph&ograve;ng th&iacute; nghiệm. Nếu trung b&igrave;nh một ng&agrave;y bạn gập, mở m&aacute;y khoảng 50 lần th&igrave; mất khoảng 10 năm th&igrave; mới c&oacute; thể đạt đến số lần gập n&agrave;y.</p>\r\n<p>&nbsp;<a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100345.jpg\"><img class=\" lazyloaded\" title=\"Gập mở chắc chắn - Samsung Galaxy Z Flip4\" src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100345.jpg\" alt=\"Gập mở chắc chắn - Samsung Galaxy Z Flip4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100345.jpg\" /></a></p>\r\n<p>Bộ khung v&agrave; bản lề của sản phẩm được ho&agrave;n thiện từ vật liệu Armor Aluminum cứng c&aacute;p. Li&ecirc;n kết giữa c&aacute;c chi tiết được l&agrave;m kh&iacute;t lại để gi&uacute;p m&aacute;y c&oacute; thể kh&aacute;ng nước tốt hơn với chuẩn IPX8, từ đ&oacute; gi&uacute;p Galaxy Z Flip4 trở th&agrave;nh chiếc&nbsp;<a title=\"Tham khảo điện thoại đang kinh doanh tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" rel=\"noopener\">điện thoại</a>&nbsp;gập bền bỉ nhất.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100346.jpg\"><img class=\" lazyloaded\" title=\"Hỗ trợ kh&aacute;ng nước - Samsung Galaxy Z Flip4\" src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100346.jpg\" alt=\"Hỗ trợ kh&aacute;ng nước - Samsung Galaxy Z Flip4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100346.jpg\" /></a></p>\r\n<p>Với những th&ocirc;ng tin tr&ecirc;n cho thấy Samsung Galaxy Z Flip4 l&agrave; một chiếc&nbsp;<a title=\"Tham khảo điện thoại kh&aacute;ng nước, bụi đang kianh doanh tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd-chong-nuoc-bui\" target=\"_blank\" rel=\"noopener\">điện thoại&nbsp;kh&aacute;ng nước, bụi</a>&nbsp;c&oacute; độ bền rất cao, v&igrave; thế bạn ho&agrave;n to&agrave;n c&oacute; thể an t&acirc;m trong việc sử dụng thiết bị một khoảng thời gian d&agrave;i.</p>\r\n<h3>N&acirc;ng tầm trải nghiệm smartphone&nbsp;</h3>\r\n<p>Với một người th&iacute;ch sử dụng những chiếc điện thoại m&agrave;n h&igrave;nh lớn như m&igrave;nh th&igrave; trở ngại thường gặp đ&oacute; ch&iacute;nh l&agrave; k&iacute;ch thước m&aacute;y kh&aacute; to, g&acirc;y kh&oacute; khăn cho m&igrave;nh trong những l&uacute;c bỏ v&agrave;o t&uacute;i. Thế nhưng điều n&agrave;y lại được khắc phục ho&agrave;n to&agrave;n tr&ecirc;n chiếc Galaxy Z Flip4 bởi sau khi gập th&igrave; chiều d&agrave;i của m&aacute;y chỉ c&ograve;n khoảng 84.9 mm.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100350.jpg\"><img class=\" lazyloaded\" title=\"Gấp gọn tiện lợi - Samsung Galaxy Z Flip4\" src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100350.jpg\" alt=\"Gấp gọn tiện lợi - Samsung Galaxy Z Flip4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100350.jpg\" /></a></p>\r\n<p>Với k&iacute;ch thước nhỏ gọn như vậy th&igrave; m&aacute;y dễ d&agrave;ng nằm gọn trong l&ograve;ng b&agrave;n tay, đ&acirc;y được xem l&agrave; một sản phẩm rất th&iacute;ch hợp d&agrave;nh cho những bạn nữ mong muốn c&oacute; cho m&igrave;nh một thiết bị c&oacute; m&agrave;n h&igrave;nh lớn nhưng vẫn đ&aacute;p ứng được ti&ecirc;u ch&iacute; nhỏ gọn v&agrave; thời trang.</p>\r\n<p>Một điểm hay tr&ecirc;n phi&ecirc;n bản n&agrave;y l&agrave; về giao diện chụp ảnh chia đ&ocirc;i m&agrave;n h&igrave;nh đ&atilde; hỗ trợ tr&ecirc;n ứng dụng camera b&ecirc;n thứ 3, giờ đ&acirc;y khung h&igrave;nh hiển thị m&agrave;n chụp sẽ được thu nhỏ v&agrave; nằm vừa vặn b&ecirc;n trong nửa m&agrave;n h&igrave;nh c&ograve;n lại để m&igrave;nh dễ d&agrave;ng theo d&otilde;i chất lượng ảnh, từ đ&oacute; c&oacute; thể chủ động hơn trong việc điều chỉnh g&oacute;c chụp.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-f2.jpg\"><img class=\" lazyloaded\" title=\"T&ugrave;y biến giao diện chụp ảnh - Samsung Galaxy Z Flip4\" src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-f2.jpg\" alt=\"T&ugrave;y biến giao diện chụp ảnh - Samsung Galaxy Z Flip4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-f2.jpg\" /></a></p>\r\n<p>Hơn nữa, bạn c&oacute; thể selfie bằng cụm camera sau để n&acirc;ng cao chất lượng ảnh một c&aacute;ch dễ d&agrave;ng nhờ c&oacute; m&agrave;n h&igrave;nh phụ.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100353.jpg\"><img class=\" lazyloaded\" title=\"Selfie dễ d&agrave;ng - Samsung Galaxy Z Flip4\" src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100353.jpg\" alt=\"Selfie dễ d&agrave;ng - Samsung Galaxy Z Flip4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100353.jpg\" /></a></p>\r\n<p>Một điểm hay nữa ở phần m&agrave;n h&igrave;nh phụ m&agrave; m&igrave;nh muốn chia sẻ với c&aacute;c bạn l&agrave; ở phần t&iacute;nh năng hiển thị, Galaxy Z Flip4 c&oacute; thể đem đến nhiều th&ocirc;ng tin v&agrave; tiện &iacute;ch để bạn c&oacute; thể sử dụng nhanh ch&oacute;ng m&agrave; kh&ocirc;ng cần mở thiết bị, nổi bật trong số đ&oacute; c&oacute; thể kể đến như: Nhạc, lịch, thời tiết, gọi trực tiếp,...</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-f3.jpg\"><img class=\" lazyloaded\" title=\"Hỗ trợ nhiều tiện &iacute;ch - Samsung Galaxy Z Flip4\" src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-f3.jpg\" alt=\"Hỗ trợ nhiều tiện &iacute;ch - Samsung Galaxy Z Flip4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-f3.jpg\" /></a></p>\r\n<p>Điều n&acirc;ng cấp ở phi&ecirc;n bản n&agrave;y so với thế hệ trước l&agrave; giờ đ&acirc;y Galaxy Z Flip4 c&oacute; thể gi&uacute;p bạn nhận trực tiếp cuộc gọi từ m&agrave;n h&igrave;nh phụ (hỗ trợ nghe loa ngo&agrave;i), trả lời tin nhắn bằng văn bản soạn sẵn.&nbsp;</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100357.jpg\"><img class=\" lazyloaded\" title=\"Trang bị nhiều tiện &iacute;ch - Samsung Galaxy Z Flip4\" src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100357.jpg\" alt=\"Trang bị nhiều tiện &iacute;ch - Samsung Galaxy Z Flip4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100357.jpg\" /></a></p>\r\n<p>Để m&agrave;n h&igrave;nh phụ trở n&ecirc;n th&uacute; vị hơn th&igrave; h&atilde;ng&nbsp;<a title=\"Tham khảo điện thoại Samsung đang kinh doanh tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd-samsung\" target=\"_blank\" rel=\"noopener\">điện thoại Samsung</a>&nbsp;đ&atilde; bổ sung rất nhiều kiểu m&agrave;n h&igrave;nh để bạn c&oacute; thể t&ugrave;y biến giao diện theo &yacute; th&iacute;ch c&aacute; nh&acirc;n. Kh&ocirc;ng những thế, việc c&agrave;i đặt m&agrave;n h&igrave;nh mang dấu ấn c&aacute; nh&acirc;n cũng đ&atilde; tạo th&ecirc;m điểm nhấn cho chiếc m&aacute;y ở phần mặt lưng.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100358.jpg\"><img class=\" lazyloaded\" title=\"T&ugrave;y biến m&agrave;n h&igrave;nh theo &yacute; th&iacute;ch - Samsung Galaxy Z Flip4\" src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100358.jpg\" alt=\"T&ugrave;y biến m&agrave;n h&igrave;nh theo &yacute; th&iacute;ch - Samsung Galaxy Z Flip4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100358.jpg\" /></a></p>\r\n<h3>Sở hữu m&agrave;n h&igrave;nh cao cấp</h3>\r\n<p>M&agrave;n h&igrave;nh ch&iacute;nh được trang bị một tấm nền Dynamic AMOLED 2X c&oacute; độ ph&acirc;n giải Full HD+ (2640 x 1080 Pixels), ảnh cho ra sẽ c&oacute; m&agrave;u sắc rực rỡ v&agrave; nh&igrave;n rất trong trẻo, mọi nội dung đều được t&aacute;i hiện sắc n&eacute;t.</p>\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/tim-hieu-man-hinh-dynamic-amoled-2x-tren-smartphone-1245213\" target=\"_blank\" rel=\"noopener\">M&agrave;n h&igrave;nh Dynamic AMOLED 2X l&agrave; g&igrave;? Điểm nổi bật? C&oacute; tr&ecirc;n điện thoại n&agrave;o</a></p>\r\n<p>Nếu như chỉ sử dụng điện thoại cho những t&aacute;c vụ cơ bản th&igrave; dường như m&igrave;nh &iacute;t cảm nhận được sự kh&aacute;c biệt r&otilde; rệt, đặc biệt l&agrave; khi so s&aacute;nh với những tấm nền Super AMOLED của c&aacute;c thiết bị đến từ nh&agrave; Samsung trước đ&acirc;y.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100400.jpg\"><img class=\" lazyloaded\" title=\"M&agrave;n h&igrave;nh chất lượng - Samsung Galaxy Z Flip4\" src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100400.jpg\" alt=\"M&agrave;n h&igrave;nh chất lượng - Samsung Galaxy Z Flip4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100400.jpg\" /></a></p>\r\n<p>Tuy nhi&ecirc;n sau khi m&igrave;nh chơi những tựa game c&oacute; đồ họa nhiều m&agrave;u sắc v&agrave; hiệu ứng h&igrave;nh ảnh như Li&ecirc;n Qu&acirc;n Mobile, xem những bộ phim bom tấn đến từ Marvel th&igrave; mọi thứ dường như đ&atilde; l&agrave;m m&igrave;nh cho&aacute;ng ngợp. M&agrave;u sắc phải n&oacute;i l&agrave; rất đ&atilde; mắt v&agrave; ch&acirc;n thực.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100402.jpg\"><img class=\" lazyloaded\" title=\"M&agrave;u sắc rực rỡ - Samsung Galaxy Z Flip4\" src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100402.jpg\" alt=\"M&agrave;u sắc rực rỡ - Samsung Galaxy Z Flip4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100402.jpg\" /></a></p>\r\n<p>M&agrave;n h&igrave;nh ch&iacute;nh c&oacute; k&iacute;ch thước đến 6.7 inch n&ecirc;n ta ho&agrave;n to&agrave;n c&oacute; thể sử dụng đồng thời hai app bằng t&iacute;nh năng chia đ&ocirc;i m&agrave;n h&igrave;nh. Ngo&agrave;i ra khi chơi game m&igrave;nh cũng cảm thấy thoải m&aacute;i hơn bởi c&aacute;c ph&iacute;m chức năng được bố tr&iacute; c&aacute;ch xa với nhau m&agrave; kh&ocirc;ng l&agrave;m che đi qu&aacute; nhiều phần hiển thị, l&uacute;c thao t&aacute;c cũng hạn chế được t&igrave;nh trạng ấn nhầm.</p>\r\n<p>Nhờ c&oacute; độ s&aacute;ng tối đa l&ecirc;n tới 1200 nits n&ecirc;n khi sử dụng ngo&agrave;i trời m&igrave;nh cũng cảm thấy dễ d&agrave;ng hơn, tuy nhi&ecirc;n nếu d&ugrave;ng m&aacute;y ở mức s&aacute;ng n&agrave;y li&ecirc;n tục trong khoảng thời gian d&agrave;i sẽ l&agrave;m giảm thời lượng d&ugrave;ng của vi&ecirc;n pin.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100404.jpg\"><img class=\" lazyloaded\" title=\"Độ s&aacute;ng m&agrave;n h&igrave;nh cao - Samsung Galaxy Z Flip4\" src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100404.jpg\" alt=\"Độ s&aacute;ng m&agrave;n h&igrave;nh cao - Samsung Galaxy Z Flip4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100404.jpg\" /></a></p>\r\n<p>Nhờ c&oacute; tần số qu&eacute;t 120 Hz tr&ecirc;n tấm nền Dynamic AMOLED 2X n&ecirc;n mọi thao t&aacute;c vuốt chạm ở phần m&agrave;n h&igrave;nh được phản hồi nhanh ch&oacute;ng v&agrave; hết sức mượt m&agrave;. Galaxy Z Flip4 c&oacute; khả năng tự động điều chỉnh tần số qu&eacute;t từ 1 - 120 Hz phụ thuộc v&agrave;o t&aacute;c vụ sử dụng, nhằm tiết kiệm điện năng ti&ecirc;u thụ.</p>\r\n<h3>Ảnh chụp bắt trọn vẻ đẹp từ mọi khung cảnh</h3>\r\n<p>Điện thoại được trang bị hai camera sau c&oacute; c&ugrave;ng độ ph&acirc;n giải 12 MP với rất nhiều t&iacute;nh năng chụp ảnh chuy&ecirc;n nghiệp, gi&uacute;p m&igrave;nh c&oacute; thể thỏa sức kh&aacute;m ph&aacute; vẻ đẹp của khung cảnh qua nhiều kiểu ảnh kh&aacute;c nhau.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100405.jpg\"><img class=\" lazyloaded\" title=\"T&iacute;ch hợp hai camera - Samsung Galaxy Z Flip4\" src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100405.jpg\" alt=\"T&iacute;ch hợp hai camera - Samsung Galaxy Z Flip4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100405.jpg\" /></a></p>\r\n<p>Đầu ti&ecirc;n l&agrave; đến với phần chụp ảnh bằng chế độ mặc định, ảnh cho ra c&oacute; độ chi tiết cao n&ecirc;n d&ugrave; m&igrave;nh c&oacute; ph&oacute;ng to l&ecirc;n 30% th&igrave; ảnh vẫn chưa xuất hiện t&igrave;nh trạng vỡ ảnh.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100407.jpg\"><img class=\" lazyloaded\" title=\"Ảnh chụp từ camera - Samsung Galaxy Z Flip4\" src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100407.jpg\" alt=\"Ảnh chụp từ camera - Samsung Galaxy Z Flip4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100407.jpg\" /></a><em>*Ảnh chụp nguy&ecirc;n bản</em></p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100409.jpg\"><img class=\" lazyloaded\" title=\"Ảnh chụp từ camera - Samsung Galaxy Z Flip4\" src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100409.jpg\" alt=\"Ảnh chụp từ camera - Samsung Galaxy Z Flip4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100409.jpg\" /></a></p>\r\n<p><em>*Ảnh chụp sau khi ph&oacute;ng to</em></p>\r\n<p>Với những t&igrave;nh huống chụp ảnh ngo&agrave;i trời khi cường độ &aacute;nh s&aacute;ng cao l&agrave;m cho bức ảnh c&oacute; khả năng bị ch&aacute;y s&aacute;ng th&igrave; Galaxy Z Flip4 sẽ tự động điều chỉnh v&agrave; c&acirc;n bằng &aacute;nh s&aacute;ng, ảnh sau khi chụp sẽ được load trong 1 đến 3 gi&acirc;y để m&aacute;y c&oacute; thể sử dụng thuật to&aacute;n xử l&yacute; ảnh th&ocirc;ng minh gi&uacute;p bức ảnh trở n&ecirc;n h&agrave;i h&ograve;a hơn.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100410.jpg\"><img class=\" lazyloaded\" title=\"Ảnh chụp từ camera - Samsung Galaxy Z Flip4\" src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100410.jpg\" alt=\"Ảnh chụp từ camera - Samsung Galaxy Z Flip4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100410.jpg\" /></a></p>\r\n<p>Ở những khung cảnh c&oacute; v&ugrave;ng tối - s&aacute;ng ch&ecirc;nh lệch nhau qu&aacute; nhiều th&igrave; Galaxy Z Flip4 cũng c&oacute; thể đem đến cho bạn một bức ảnh đẹp mắt, những v&ugrave;ng tối sẽ được n&acirc;ng s&aacute;ng l&ecirc;n đ&ocirc;i ch&uacute;t v&agrave; tương tự v&ugrave;ng s&aacute;ng cũng sẽ được giảm lại để c&acirc;n bằng với nhau.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100412.jpg\"><img class=\" lazyloaded\" title=\"Ảnh chụp từ camera - Samsung Galaxy Z Flip4\" src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100412.jpg\" alt=\"Ảnh chụp từ camera - Samsung Galaxy Z Flip4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100412.jpg\" /></a></p>\r\n<p>T&iacute;nh năng m&agrave; m&igrave;nh ấn tượng nhất ch&iacute;nh l&agrave; chụp ảnh x&oacute;a ph&ocirc;ng tr&ecirc;n camera sau, chủ thể được t&aacute;ch nền cực kỳ mịn ở m&ocirc;i trường đủ s&aacute;ng, với cảnh chụp c&oacute; nhiều chi tiết nhỏ th&igrave; m&aacute;y vẫn c&oacute; thể xử l&yacute; v&agrave; t&aacute;ch nền hiệu quả.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100414.jpg\"><img class=\" lazyloaded\" title=\"Ảnh chụp từ camera - Samsung Galaxy Z Flip4\" src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100414.jpg\" alt=\"Ảnh chụp từ camera - Samsung Galaxy Z Flip4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100414.jpg\" /></a></p>\r\n<p>Chuyển qua phần camera g&oacute;c si&ecirc;u rộng th&igrave; ảnh thu nhiều kh&ocirc;ng gian hơn, ph&ugrave; hợp để chụp những khung ảnh rộng lớn. Chất lượng ảnh ở c&aacute;c g&oacute;c kh&ocirc;ng bị b&oacute;p m&eacute;o qu&aacute; nhiều.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100416.jpg\"><img class=\" lazyloaded\" title=\"Ảnh chụp từ camera - Samsung Galaxy Z Flip4\" src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100416.jpg\" alt=\"Ảnh chụp từ camera - Samsung Galaxy Z Flip4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100416.jpg\" /></a></p>\r\n<p>Nhờ c&oacute; chế độ chụp ảnh zoom n&ecirc;n khi m&igrave;nh chụp c&aacute;c vật thể ở khoảng c&aacute;ch xa bằng Samsung Galaxy Z Flip4 cũng trở n&ecirc;n dễ d&agrave;ng hơn, tiết kiệm thời gian khi kh&ocirc;ng phải thường xuy&ecirc;n di chuyển để chọn g&oacute;c chụp, nhưng chất lượng ảnh cho ra vẫn c&oacute; độ chi tiết cao.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100418.jpg\"><img class=\" lazyloaded\" title=\"Ảnh chụp từ camera - Samsung Galaxy Z Flip4\" src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100418.jpg\" alt=\"Ảnh chụp từ camera - Samsung Galaxy Z Flip4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100418.jpg\" /></a></p>\r\n<p>N&oacute;i đến phần chụp ảnh đ&ecirc;m tr&ecirc;n Galaxy Z Flip4 phải n&oacute;i l&agrave; rất tốt khi hiện tượng nhiễu hạt dường như kh&ocirc;ng xuất hiện tr&ecirc;n bức ảnh (ảnh gốc), t&igrave;nh trạng ch&aacute;y s&aacute;ng ở những nơi c&oacute; độ s&aacute;ng cao cũng được cải thiện so với thế hệ trước.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100420.jpg\"><img class=\" lazyloaded\" title=\"Ảnh chụp từ camera - Samsung Galaxy Z Flip4\" src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100420.jpg\" alt=\"Ảnh chụp từ camera - Samsung Galaxy Z Flip4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100420.jpg\" /></a></p>\r\n<p>Tuy nhi&ecirc;n khả năng thu s&aacute;ng của m&aacute;y theo m&igrave;nh cảm nhận l&agrave; chưa được tốt cho lắm, nh&igrave;n tổng quan vẫn c&ograve;n kh&aacute; tối ở những v&ugrave;ng thiếu s&aacute;ng.</p>\r\n<h3>Sức mạnh đ&aacute;ng kinh ngạc đến từ Qualcomm</h3>\r\n<p>Trang bị tr&ecirc;n chiếc flagship n&agrave;y l&agrave; một bộ vi xử l&yacute; cao cấp mới nhất đến từ nh&agrave; Qualcomm với t&ecirc;n gọi Snapdragon 8+ Gen 1 v&agrave; xung nhịp tối đa đạt v&agrave;o khoảng 3.18 GHz. M&igrave;nh c&oacute; test qua một v&agrave;i phần mềm chấm điểm hiệu năng với những con số sau khi đo được như sau: 1283 (đơn nh&acirc;n), 3748 (đa nh&acirc;n) tr&ecirc;n Benchmark v&agrave; 14223 tr&ecirc;n PCMark, quả l&agrave; một con số qu&aacute; kinh khủng tr&ecirc;n giới smartphone.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100422.jpg\"><img class=\" lazyloaded\" title=\"Hiệu năng mạnh mẽ -  Samsung Galaxy Z Flip4\" src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100422.jpg\" alt=\"Hiệu năng mạnh mẽ -  Samsung Galaxy Z Flip4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100422.jpg\" /></a></p>\r\n<p>Đi k&egrave;m với CPU mạnh mẽ th&igrave; đ&acirc;y cũng l&agrave; chiếc&nbsp;<a title=\"Tham khảo điện thoại c&oacute; RAM 8 GB đang kinh doanh tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd-ram-8gb\" target=\"_blank\" rel=\"noopener\">điện thoại RAM 8 GB</a>*&nbsp;chạy tr&ecirc;n hệ điều h&agrave;nh Android 12 v&agrave; được t&ugrave;y biến ri&ecirc;ng theo nh&agrave; Samsung, gi&uacute;p mang lại một giao diện đẹp mắt cũng như n&acirc;ng cao trải nghiệm tr&ecirc;n một chiếc&nbsp;<a title=\"Tham khảo điện thoại chơi game đang kinh doanh tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd-choi-game\" target=\"_blank\" rel=\"noopener\">điện thoại chơi game</a>.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100423.jpg\"><img class=\" lazyloaded\" title=\"Hiệu năng mạnh mẽ - Samsung Galaxy Z Flip4\" src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100423.jpg\" alt=\"Hiệu năng mạnh mẽ - Samsung Galaxy Z Flip4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100423.jpg\" /></a></p>\r\n<p><em>*C&oacute; hỗ trợ t&iacute;nh năng mở rộng RAM l&ecirc;n tới 8 GB</em></p>\r\n<p>Đến với phần trải nghiệm chơi game th&igrave; Snapdragon 8+ Gen 1 đ&atilde; bộc lộ gần như hết sức mạnh của chip, điều n&agrave;y được minh chứng trong l&uacute;c m&igrave;nh chơi tựa game Li&ecirc;n Qu&acirc;n Mobile ở mức đồ họa cao nhất. H&igrave;nh ảnh cho ra phải n&oacute;i l&agrave; cực kỳ đ&atilde; mắt, m&agrave;u sắc rực rỡ n&ecirc;n m&igrave;nh c&oacute; thể cảm nhận được vẻ đẹp tr&ecirc;n từng hiệu ứng kỹ năng của nh&acirc;n vật.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100425.jpg\"><img class=\" lazyloaded\" title=\"Cấu h&igrave;nh game - Samsung Galaxy Z Flip4\" src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100425.jpg\" alt=\"Cấu h&igrave;nh game - Samsung Galaxy Z Flip4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100425.jpg\" /></a></p>\r\n<p>Về phần trải nghiệm th&igrave; Galaxy Z Flip4 mang đến cho m&igrave;nh một sự ổn định xuy&ecirc;n suốt qu&aacute; tr&igrave;nh chơi, tốc độ khung h&igrave;nh sau khi đo bằng phần mềm th&igrave; kết quả được ghi nhận v&agrave;o khoảng 59.8 FPS.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100427.jpg\"><img class=\" lazyloaded\" title=\"Tốc độ khung h&igrave;nh - Samsung Galaxy Z Flip4\" src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100427.jpg\" alt=\"Tốc độ khung h&igrave;nh - Samsung Galaxy Z Flip4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100427.jpg\" /></a></p>\r\n<p>C&ograve;n đối với tựa game PUBG Mobile ở mức đồ họa HDR c&ugrave;ng tốc độ khung h&igrave;nh cực độ, h&igrave;nh ảnh nh&acirc;n vật v&agrave; quang cảnh c&oacute; đổ b&oacute;ng l&agrave;m cho m&igrave;nh cảm nhận được sự ch&acirc;n thật tr&ecirc;n từng chuyển động. K&egrave;m với đ&oacute; l&agrave; sự hỗ trợ đến từ m&agrave;n h&igrave;nh tần số qu&eacute;t cao n&ecirc;n mọi hiệu ứng di chuyển, giao tranh được xử l&yacute; mượt m&agrave; hơn rất nhiều.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100428.jpg\"><img class=\" lazyloaded\" title=\"Tốc độ khung h&igrave;nh - Samsung Galaxy Z Flip4\" src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100428.jpg\" alt=\"Tốc độ khung h&igrave;nh - Samsung Galaxy Z Flip4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100428.jpg\" /></a></p>\r\n<p>Cảm nhận về phần nhiệt độ của m&aacute;y th&igrave; m&igrave;nh thấy Galaxy Z Flip4 kh&ocirc;ng thực sự qu&aacute; n&oacute;ng khi được trang bị một con chip c&oacute; hiệu suất khủng, mọi thứ chỉ dừng ở mức ấm tay sau khi m&igrave;nh chơi game tầm 40 - 50 ph&uacute;t li&ecirc;n tục. C&ograve;n đối với c&aacute;c t&aacute;c vụ phổ th&ocirc;ng kh&aacute;c th&igrave; nhiệt độ của m&aacute;y gần như kh&ocirc;ng c&oacute; sự thay đổi so với b&igrave;nh thường.</p>\r\n<h3>Hỗ trợ sạc pin nhanh ch&oacute;ng</h3>\r\n<p>M&igrave;nh đ&atilde; từng cho rằng vi&ecirc;n pin c&oacute; dung lượng 3700 mAh sẽ kh&ocirc;ng đủ đ&aacute;p ứng một ng&agrave;y sử dụng của m&igrave;nh, nhưng sau khi kiểm chứng bằng c&aacute;ch d&ugrave;ng m&aacute;y li&ecirc;n tục cho mọi t&aacute;c vụ từ chụp ảnh, xem phim, lướt web v&agrave; chơi game th&igrave; Galaxy Z Flip4 lại c&oacute; thể đ&aacute;p ứng thời lượng sử dụng cho tới 6 tiếng 47 ph&uacute;t* rất ấn tượng.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100430.jpg\"><img class=\" lazyloaded\" title=\"Sử dụng d&agrave;i l&acirc;u - Samsung Galaxy Z Flip4\" src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100430.jpg\" alt=\"Sử dụng d&agrave;i l&acirc;u - Samsung Galaxy Z Flip4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100430.jpg\" /></a></p>\r\n<p><em>*Thời gian c&oacute; thể thay đổi t&ugrave;y v&agrave;o t&aacute;c vụ sử dụng</em></p>\r\n<p>Nhờ c&oacute; c&ocirc;ng nghệ sạc nhanh 25 W n&ecirc;n m&igrave;nh chỉ mất khoảng 1 giờ 30 ph&uacute;t* l&agrave; c&oacute; thể sạc đầy được vi&ecirc;n pin tr&ecirc;n Galaxy Z Flip4. Với một v&agrave;i trường hợp gấp cần sử dụng th&igrave; chỉ cần đợi 30 ph&uacute;t l&agrave; m&igrave;nh đ&atilde; c&oacute; ngay 37% pin để sử dụng (tất cả đều sạc khi m&aacute;y ở trạng th&aacute;i 0% pin). Thực sự l&agrave; một chiếc&nbsp;<a title=\"Tham khảo điện thoại sạc pin nhanh đang kinh doanh tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd-sac-pin-nhanh\" target=\"_blank\" rel=\"noopener\">điện thoại sạc pin nhanh</a>&nbsp;đ&aacute;ng sắm để tối ưu c&ocirc;ng việc nhanh ch&oacute;ng.</p>\r\n<p>Chia sẻ pin kh&ocirc;ng d&acirc;y l&agrave; t&iacute;nh năng kh&aacute; tiện lợi đối với một người đang sử dụng một chiếc smartwatch như m&igrave;nh (c&oacute; hỗ trợ sạc kh&ocirc;ng d&acirc;y), khi đồng hồ hết pin th&igrave; m&igrave;nh chỉ việc đặt thiết bị l&ecirc;n tr&ecirc;n mặt lưng của Galaxy Z Flip4 l&agrave; đ&atilde; c&oacute; thể k&iacute;ch hoạt t&iacute;nh năng tr&ecirc;n (cần k&iacute;ch hoạt t&iacute;nh năng trước ở trong phần c&agrave;i đặt).</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100432.jpg\"><img class=\" lazyloaded\" title=\"Chia sẻ pin kh&ocirc;ng d&acirc;y - Samsung Galaxy Z Flip4\" src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100432.jpg\" alt=\"Chia sẻ pin kh&ocirc;ng d&acirc;y - Samsung Galaxy Z Flip4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/258047/samsung-galaxy-z-flip4-230922-100432.jpg\" /></a></p>\r\n<p>L&agrave; một người d&ugrave;ng trẻ trung năng động hay y&ecirc;u th&iacute;ch c&aacute;c loại điện thoại dẫn đầu xu hướng th&igrave; chiếc Samsung Galaxy Z Flip4 sẽ l&agrave; c&aacute;i t&ecirc;n m&agrave; bạn kh&ocirc;ng n&ecirc;n bỏ qua, nhờ c&oacute; cơ chế gập v&ocirc; c&ugrave;ng độc đ&aacute;o k&egrave;m với bộ th&ocirc;ng số kỹ thuật hết sức mạnh mẽ n&ecirc;n m&aacute;y hứa hẹn sẽ mang đến cho bạn v&ocirc; v&agrave;n trải nghiệm th&uacute; vị.</p>', 100, 0, '<p class=\"lileft\">Cấu h&igrave;nh Samsung Galaxy Z Flip4 128GB</p>\r\n<p class=\"lileft\">M&agrave;n h&igrave;nh:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span class=\"comma\">Ch&iacute;nh: Dynamic AMOLED 2X, Phụ: Super AMOLED</span><span class=\"comma\">Chính 6.7\" &amp; Phụ 1.9\"</span><span class=\"\">Full HD+</span></p>\r\n<p class=\"lileft\">Hệ điều h&agrave;nh:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span class=\"\">Android 12</span></p>\r\n<p class=\"lileft\">Camera sau:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span class=\"\">2 camera 12 MP</span></p>\r\n<p class=\"lileft\">Camera trước:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span class=\"\">10 MP</span></p>\r\n<p class=\"lileft\">Chip:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span class=\"\">Snapdragon 8+ Gen 1</span></p>\r\n<p class=\"lileft\">RAM:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span class=\"\">8 GB </span></p>\r\n<p class=\"lileft\">Dung lượng lưu trữ:&nbsp; &nbsp; &nbsp; &nbsp; <span class=\"\">128 GB</span></p>\r\n<p class=\"lileft\">SIM:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span class=\"comma\">1 Nano SIM &amp; 1 eSIM</span><span class=\"\">Hỗ trợ 5G</span></p>\r\n<p class=\"lileft\">Pin, Sạc:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span class=\"comma\">3700 mAh</span><span class=\"\">25 W</span></p>', 0, 0, '2023-04-11 14:44:46', '2023-04-11 17:58:09', 1);
INSERT INTO `product` (`id`, `name`, `slug`, `price`, `sale`, `pay`, `thumbar`, `category_id`, `content`, `number`, `head`, `short_description`, `view`, `hot`, `created_at`, `updated_at`, `home`) VALUES
(22, 'Samsung Galaxy Z Fold4 256GB', 'samsung-galaxy-z-fold4-256gb', 36990000, 0, 0, 'zflop4.jpg', 9, '<h3>Tại sự kiện Samsung Unpacked thường ni&ecirc;n,&nbsp;<a title=\"Tham khảo th&ocirc;ng tin sản phẩm tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-z-fold4\" target=\"_blank\" rel=\"noopener\">Samsung Galaxy Z Fold4 256GB</a>&nbsp;ch&iacute;nh thức được tr&igrave;nh l&agrave;ng thị trường c&ocirc;ng nghệ, mang tr&ecirc;n m&igrave;nh nhiều cải tiến đ&aacute;ng gi&aacute; gi&uacute;p Galaxy Z Fold trở th&agrave;nh d&ograve;ng điện thoại gập đ&atilde; tốt nay c&agrave;ng tốt hơn.</h3>\r\n<h3>Kiểu d&aacute;ng gập mở tinh tế tạo n&ecirc;n sự kh&aacute;c biệt</h3>\r\n<p>Galaxy Z Fold4 (2022) ra mắt với ngoại h&igrave;nh gần như l&agrave; kh&ocirc;ng đổi khi so với&nbsp;<a title=\"Tham khảo th&ocirc;ng tin sản phẩm tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-z-fold-3\" target=\"_blank\" rel=\"noopener\">Galaxy Z Fold3</a>, nếu bạn chỉ nh&igrave;n bề ngo&agrave;i th&igrave; bạn sẽ kh&oacute; l&ograve;ng ph&acirc;n biệt được 2 sản phẩm n&agrave;y. M&aacute;y vẫn sử dụng khung viền Armor Aluminum bền bỉ, mặt k&iacute;nh m&agrave;n h&igrave;nh phụ l&agrave; Corning Gorilla Glass Victus+, mặt k&iacute;nh m&agrave;n h&igrave;nh ch&iacute;nh l&agrave; Ultra Thin Glass.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-100822-064950.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Tạo kh&aacute;c biệt với m&agrave;n h&igrave;nh gập, mở - Samsung Galaxy Z Fold4 256GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-100822-064950.jpg\" alt=\"Tạo kh&aacute;c biệt với m&agrave;n h&igrave;nh gập, mở - Samsung Galaxy Z Fold4 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-100822-064950.jpg\" /></a></p>\r\n<p>V&agrave; đ&acirc;y cũng l&agrave; chiếc điện thoại gập bền bỉ nhất giới smartphone khi kết hợp với khả năng kh&aacute;ng nước chuẩn IPX8, c&oacute; khả năng ng&acirc;m trong nước ngọt ở độ s&acirc;u tới 1.5 m&eacute;t trong thời gian tối đa 30 ph&uacute;t* (m&aacute;y kh&ocirc;ng c&oacute; khả năng kh&aacute;ng bụi).</p>\r\n<p><em>*Th&ocirc;ng tin h&atilde;ng Samsung cung cấp</em></p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-62.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Điện thoại kh&aacute;ng nước - Samsung Galaxy Z Fold4\" src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-62.jpg\" alt=\"Điện thoại kh&aacute;ng nước - Samsung Galaxy Z Fold4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-62.jpg\" /></a></p>\r\n<p>Năm nay Z Fold4 c&oacute; thiết kế c&acirc;n bằng hơn, bản lề v&agrave; viền m&agrave;n h&igrave;nh được thiết kế mỏng hơn gi&uacute;p tổng thể ở m&agrave;n h&igrave;nh phụ của m&aacute;y rộng th&ecirc;m 2.7 mm v&agrave; m&agrave;n h&igrave;nh ch&iacute;nh rộng hơn 3 mm khi so với thế hệ tiền nhiệm. Tuy con số kh&ocirc;ng qu&aacute; lớn nhưng với trải nghiệm thực tế th&igrave; đ&acirc;y l&agrave; một thay đổi cực kỳ đ&aacute;ng gi&aacute; v&agrave; mang lại hiệu quả sử dụng cao.</p>\r\n<p>Với k&iacute;ch thước m&agrave;n h&igrave;nh được tăng l&ecirc;n th&igrave; m&agrave;n h&igrave;nh phụ của Fold4 đ&atilde; dễ d&agrave;ng thao t&aacute;c v&agrave; g&otilde; văn bản hơn, kh&ocirc;ng c&ograve;n gặp t&igrave;nh trạng d&iacute;nh chữ như tr&ecirc;n thế hệ trước nữa. Với tỉ lệ m&agrave;n h&igrave;nh n&agrave;y th&igrave; n&oacute; vẫn cao hơn smartphone ngo&agrave;i thị thường một x&iacute;u.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-100822-070512.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"M&agrave;n h&igrave;nh phụ lớn hơn - Samsung Galaxy Z Fold4 256GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-100822-070512.jpg\" alt=\"M&agrave;n h&igrave;nh phụ lớn hơn - Samsung Galaxy Z Fold4 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-100822-070512.jpg\" /></a></p>\r\n<p><a title=\"Tham khảo điện thoại Samsung tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd-samsung\" target=\"_blank\" rel=\"noopener\">H&atilde;ng điện thoại Samsung</a>&nbsp;đ&atilde; thay đổi cấu tr&uacute;c phần bản lề tr&ecirc;n Fold4, điều n&agrave;y mang lại một cảm gi&aacute;c đ&oacute;ng - mở cứng c&aacute;p hơn. M&aacute;y c&oacute; thể linh hoạt gập - mở ở c&aacute;c g&oacute;c từ 75 độ đến 115 độ, cho bạn nhiều g&oacute;c độ sử dụng điện thoại hơn.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-260922-115021.jpg\"><img class=\" lazyloaded\" title=\"Thay đổi cấu tr&uacute;c bản lề - Samsung Galaxy Z Fold4\" src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-260922-115021.jpg\" alt=\"Thay đổi cấu tr&uacute;c bản lề - Samsung Galaxy Z Fold4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-260922-115021.jpg\" /></a></p>\r\n<p>Với khả năng gập mở linh hoạt n&agrave;y th&igrave; bạn c&oacute; thể đặt m&aacute;y tr&ecirc;n mặt phẳng để chụp ảnh với c&aacute;c g&oacute;c chụp s&aacute;ng tạo để quay TikTok hay l&agrave;m việc như một chiếc \"laptop mini\".</p>\r\n<p>Xem th&ecirc;m:&nbsp;<a title=\"Flex Mode tr&ecirc;n Galaxy Z Fold4 v&agrave; Z Flip4: Một xu hướng v&agrave; lối sống linh hoạt mới!\" href=\"https://www.thegioididong.com/tin-tuc/flex-mode-dien-thoai-man-hinh-gap-samsung-moi-1467788\" target=\"_blank\" rel=\"noopener\">Flex Mode tr&ecirc;n Galaxy Z Fold4 v&agrave; Z Flip4: Một xu hướng v&agrave; lối sống linh hoạt mới!</a></p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-69.jpg\"><img class=\" lazyloaded\" title=\"Flex Mode tr&ecirc;n Samsung Galaxy Z Fold4\" src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-69.jpg\" alt=\"Flex Mode tr&ecirc;n Samsung Galaxy Z Fold4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-69.jpg\" /></a></p>\r\n<h3>M&agrave;n h&igrave;nh cực đại, trải nghiệm cực đ&atilde;</h3>\r\n<p>Samsung Galaxy Z Fold4 c&oacute; m&agrave;n h&igrave;nh phụ b&ecirc;n ngo&agrave;i k&iacute;ch thước 6.2 inch độ ph&acirc;n giải HD+. M&agrave;n h&igrave;nh ch&iacute;nh 7.6 inch hỗ trợ cơ chế gập, sử dụng c&ocirc;ng nghệ m&agrave;n h&igrave;nh Dynamic AMOLED 2X rực rỡ c&ugrave;ng một số c&ocirc;ng nghệ như hỗ trợ độ s&aacute;ng tối đa 1200 nits, tần số qu&eacute;t 120 Hz c&oacute; khả năng th&iacute;ch ứng t&ugrave;y thuộc v&agrave;o ứng dụng v&agrave; c&agrave;i đặt.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-260922-115019.jpg\"><img class=\" lazyloaded\" title=\"C&ocirc;ng nghệ m&agrave;n h&igrave;nh h&agrave;ng đầu trong giới smartphone - Samsung Galaxy Z Fold4\" src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-260922-115019.jpg\" alt=\"C&ocirc;ng nghệ m&agrave;n h&igrave;nh h&agrave;ng đầu trong giới smartphone - Samsung Galaxy Z Fold4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-260922-115019.jpg\" /></a></p>\r\n<p>Tất cả th&ocirc;ng số tr&ecirc;n gi&uacute;p Fold4 trở th&agrave;nh một thiết bị c&oacute; khả năng hiển thị bật nhất thị trường, m&agrave;u sắc rực rỡ, nịnh mắt, m&agrave;u đen s&acirc;u v&agrave; m&agrave;n h&igrave;nh cũng được chứng nhận khả năng bảo vệ mắt gi&uacute;p bạn y&ecirc;n t&acirc;m hơn khi sử dụng sản phẩm trong thời gian d&agrave;i.</p>\r\n<p>K&iacute;ch thước m&agrave;n h&igrave;nh ch&iacute;nh của Fold4 gần tương đương với một chiếc&nbsp;<a title=\"Tham khảo m&aacute;y t&iacute;nh bảng tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\" rel=\"noopener\">m&aacute;y t&iacute;nh bảng</a>, cho bạn một kh&ocirc;ng gian hiển thị rộng hơn, kết hợp với phần mềm được tối ưu độc quyền, gi&uacute;p Galaxy Z Fold4 trở th&agrave;nh thiết bị 2 trong 1 cực kỳ tiện dụng.</p>\r\n<p>Năm nay Galaxy Z Fold4 tập trung n&acirc;ng cấp về phần mềm v&agrave; trải nghiệm người d&ugrave;ng l&agrave; ch&iacute;nh, với phi&ecirc;n bản Fold mới n&agrave;y khi bạn v&agrave;o một ứng dụng bất kỳ th&igrave; ph&iacute;a dưới m&agrave;n h&igrave;nh sẽ xuất hiện thanh Taskbar tương tự tr&ecirc;n m&aacute;y t&iacute;nh.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-260922-115017.jpg\"><img class=\" lazyloaded\" title=\"Thanh taskbar tr&ecirc;n Galaxy Z Fold thế hệ mới - Samsung Galaxy Z Fold4\" src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-260922-115017.jpg\" alt=\"Thanh taskbar tr&ecirc;n Galaxy Z Fold thế hệ mới - Samsung Galaxy Z Fold4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-260922-115017.jpg\" /></a></p>\r\n<p>Bạn c&oacute; thể t&ugrave;y chỉnh như th&ecirc;m, x&oacute;a ứng dụng tr&ecirc;n thanh c&ocirc;ng cụ n&agrave;y, c&aacute;c ứng dụng vừa được mở cũng sẽ xuất hiện ở đ&acirc;y. N&oacute; sẽ gi&uacute;p bạn đa nhiệm nhanh hơn, chuyển đổi qua lại c&aacute;c ứng dụng trực quan hơn. Bạn c&oacute; thể k&eacute;o thả ứng dụng từ thanh taskbar l&ecirc;n m&agrave;n h&igrave;nh để đa nhiệm đa cửa sổ, chia m&agrave;n h&igrave;nh.</p>\r\n<p>Thanh taskbar n&agrave;y chỉ xuất hiện ở m&agrave;n h&igrave;nh ch&iacute;nh, m&agrave;n h&igrave;nh phụ thanh taskbar sẽ kh&ocirc;ng xuất hiện, bạn vẫn sẽ sử dụng như một chiếc&nbsp;<a title=\"Tham khảo điện thoại tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" rel=\"noopener\">smartphone</a>&nbsp;th&ocirc;ng thường.</p>\r\n<p>Ở m&agrave;n h&igrave;nh ch&iacute;nh, Galaxy Z Fold4 hỗ trợ đa nhiệm chia m&agrave;n h&igrave;nh tối đa 3 ứng dụng (m&agrave;n h&igrave;nh phụ hỗ trợ chia tối đa 2 ứng dụng), nếu bạn sử dụng cửa sổ nổi th&igrave; số lượng cửa sổ sẽ được tăng l&ecirc;n.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-260922-115016.jpg\"><img class=\" lazyloaded\" title=\"Hỗ trợ chia m&agrave;n h&igrave;nh, gi&uacute;p đa nhiệm hiệu quả hơn - Samsung Galaxy Z Fold4\" src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-260922-115016.jpg\" alt=\"Hỗ trợ chia m&agrave;n h&igrave;nh, gi&uacute;p đa nhiệm hiệu quả hơn - Samsung Galaxy Z Fold4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-260922-115016.jpg\" /></a></p>\r\n<p>Với những bạn c&oacute; sở th&iacute;ch viết, vẽ hoặc c&ocirc;ng việc c&oacute; t&iacute;nh chất đặc th&ugrave; l&agrave; ghi ch&eacute;p nhanh hay ph&aacute;c họa th&igrave; Galaxy Z Fold4 c&oacute; lẽ sẽ l&agrave; một lựa chọn tối ưu d&agrave;nh cho c&aacute;c bạn bởi m&aacute;y sẽ c&oacute; chức năng tương tự như một cuốn sổ truyền thống khi sử dụng k&egrave;m với b&uacute;t S Pen*. V&agrave; S Pen vẫn sẽ được tối ưu để sử dụng với c&aacute;c thiết bị gập, vừa tương th&iacute;ch vừa kh&ocirc;ng l&agrave;m ảnh hưởng đến m&agrave;n h&igrave;nh.</p>\r\n<p><em>*S Pen l&agrave; phụ kiện b&aacute;n rời, kh&ocirc;ng k&egrave;m trong hộp khi mua m&aacute;y</em></p>\r\n<h3>D&aacute;m n&acirc;ng cấp, tạo kh&aacute;c biệt</h3>\r\n<p>Cụm camera tr&ecirc;n Fold4 c&oacute; th&ocirc;ng số v&agrave; thiết kế kh&aacute; giống với Galaxy S22+, camera tr&ecirc;n Fold4 được n&acirc;ng cấp rất nhiều so với c&aacute;c thế hệ trước đ&acirc;y, m&aacute;y sở hữu camera ch&iacute;nh g&oacute;c rộng độ ph&acirc;n giải 50 MP, camera g&oacute;c si&ecirc;u rộng 12 MP v&agrave; một camera tele 10 MP.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-54.jpg\"><img class=\" lazyloaded\" title=\"Cụm camera chất lượng - Samsung Galaxy Z Fold4\" src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-54.jpg\" alt=\"Cụm camera chất lượng - Samsung Galaxy Z Fold4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-54.jpg\" /></a></p>\r\n<p>Cụm camera n&agrave;y hỗ trợ zoom quang học 3x cho chất lượng h&igrave;nh ảnh rất tốt, m&igrave;nh kh&aacute; ấn tượng về khả năng chụp của Galaxy Z Fold năm nay, h&igrave;nh ảnh cho ra c&oacute; chiều s&acirc;u, hiệu ứng x&oacute;a ph&ocirc;ng rất mịn, nh&igrave;n tổng quan chất lượng ảnh tương đương chụp bằng chiếc m&aacute;y ảnh DSLR.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-57.jpg\"><img class=\" lazyloaded\" title=\"Ảnh chụp từ camera zoom - Samsung Galaxy Z Fold4\" src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-57.jpg\" alt=\"Ảnh chụp từ camera zoom - Samsung Galaxy Z Fold4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-57.jpg\" /></a></p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-58.jpg\"><img class=\" lazyloaded\" title=\"Ảnh chụp từ camera tr&ecirc;n Samsung Galaxy Z Fold4\" src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-58.jpg\" alt=\"Ảnh chụp từ camera tr&ecirc;n Samsung Galaxy Z Fold4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-58.jpg\" /></a></p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-55.jpg\"><img class=\" lazyloaded\" title=\"Ảnh chụp từ camera - Samsung Galaxy Z Fold4\" src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-55.jpg\" alt=\"Ảnh chụp từ camera - Samsung Galaxy Z Fold4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-55.jpg\" /></a></p>\r\n<p>Galaxy Z Fold 4 l&agrave; chiếc&nbsp;<a title=\"Tham khảo điện thoại c&oacute; chống rung quang học tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd-chong-rung-quang-hoc\" target=\"_blank\" rel=\"noopener\">điện thoại c&oacute; chống rung quang học</a>&nbsp;gi&uacute;p bạn chụp ảnh, quay video mượt m&agrave; hơn, kết hợp c&ugrave;ng chế độ chụp đ&ecirc;m gi&uacute;p hạn chế được nhiễu hạt, tất cả những điều tr&ecirc;n l&agrave; tiền đề để tạo n&ecirc;n một bức ảnh đẹp trong mọi điều kiện &aacute;nh s&aacute;ng.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-56.jpg\"><img class=\" lazyloaded\" title=\"Ảnh chụp trong điều kiện đủ s&aacute;ng - Samsung Galaxy Z Fold4\" src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-56.jpg\" alt=\"Ảnh chụp trong điều kiện đủ s&aacute;ng - Samsung Galaxy Z Fold4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-56.jpg\" /></a></p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-61.jpg\"><img class=\" lazyloaded\" title=\"Ảnh chụp trong điều kiện thiếu s&aacute;ng - Samsung Galaxy Z Fold4\" src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-61.jpg\" alt=\"Ảnh chụp trong điều kiện thiếu s&aacute;ng - Samsung Galaxy Z Fold4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-61.jpg\" /></a></p>\r\n<p>Khả năng zoom kỹ thuật số tr&ecirc;n Fold4 gấp 10 lần so với zoom quang học, khi kết hợp với camera tele Fold4 c&oacute; khả năng zoom tối đa đến 30x, cho bạn thoải m&aacute;i s&aacute;ng tạo để tạo n&ecirc;n t&aacute;c phẩm cho ri&ecirc;ng m&igrave;nh.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-65.jpg\"><img class=\" lazyloaded\" title=\"Camera zoom đến 30 lần - Samsung Galaxy Z Fold4\" src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-65.jpg\" alt=\"Camera zoom đến 30 lần - Samsung Galaxy Z Fold4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-65.jpg\" /></a></p>\r\n<p>Số lượng camera trước tr&ecirc;n Fold4 l&agrave; 2 camera, với một cảm biến thiết kế dạng nốt ruồi ở m&agrave;n h&igrave;nh phụ c&oacute; độ ph&acirc;n giải 10 MP, cảm biến c&ograve;n lại thiết kế ẩn dưới m&agrave;n h&igrave;nh độ ph&acirc;n giải 4 MP.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-63.jpg\"><img class=\" lazyloaded\" title=\"Camera ẩn dưới m&agrave;n h&igrave;nh - Samsung Galaxy Z Fold4\" src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-63.jpg\" alt=\"Camera ẩn dưới m&agrave;n h&igrave;nh - Samsung Galaxy Z Fold4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-63.jpg\" /></a></p>\r\n<p>Camera trước dưới m&agrave;n h&igrave;nh đ&atilde; được n&acirc;ng cao về chất lượng so với thế hệ Fold3, thuật to&aacute;n n&acirc;ng cao gi&uacute;p xử l&yacute; bức ảnh trong trẻo hơn, m&agrave;u sắc tươi tắn, cũng như khả năng chụp ảnh ngược s&aacute;ng được tốt hơn.</p>\r\n<h3>Trải nghiệm mượt m&agrave;, xử l&yacute; đa t&aacute;c vụ</h3>\r\n<p>Galaxy Z Fold4 l&agrave; chiếc điện thoại thuộc d&ograve;ng cao cấp n&ecirc;n hiệu năng của m&aacute;y l&agrave; điều kh&ocirc;ng cần bận t&acirc;m qu&aacute; nhiều, bởi n&oacute; lu&ocirc;n được trang bị con chip mạnh mẽ nhất tại thời điểm m&agrave; n&oacute; ra mắt.<br />CPU Snapdragon 8+ Gen 1 l&agrave; nguồn cung cấp sức mạnh cho Galaxy Z Fold năm nay, GPU t&iacute;ch hợp l&agrave; Adreno 670 mang lại một hiệu năng tổng thể cực kỳ ấn tượng. Vấn đề nhiệt độ khi sử dụng rất được quan t&acirc;m tr&ecirc;n c&aacute;c sản phẩm sử dụng Snapdragon 8 Gen 1 series, năm nay Samsung đ&atilde; l&agrave;m rất tốt, nhiệt độ thiết bị khi sử dụng giảm đ&aacute;ng kể, kh&ocirc;ng c&ograve;n n&oacute;ng ran như c&aacute;c sản phẩm sử dụng c&ugrave;ng con chip.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-64.jpg\"><img class=\" lazyloaded\" title=\"Hiệu năng cực mạnh mẽ với CPU mới nhất đến từ nh&agrave; Qualcomm - Samsung Galaxy Z Fold4\" src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-64.jpg\" alt=\"Hiệu năng cực mạnh mẽ với CPU mới nhất đến từ nh&agrave; Qualcomm - Samsung Galaxy Z Fold4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-64.jpg\" /></a></p>\r\n<p>Xem th&ecirc;m:&nbsp;<a title=\"Đ&aacute;nh gi&aacute; hiệu năng Galaxy Z Fold4: Snapdragon 8+ Gen 1 rất ngon, pin khỏe nhưng cần cải thiện một v&agrave;i điểm\" href=\"https://www.thegioididong.com/tin-tuc/danh-gia-hieu-nang-galaxy-z-fold4-1469282\" target=\"_blank\" rel=\"noopener\">Đ&aacute;nh gi&aacute; hiệu năng Galaxy Z Fold4: Snapdragon 8+ Gen 1 rất ngon, pin khỏe nhưng cần cải thiện một v&agrave;i điểm.</a></p>\r\n<p>Năm nay Samsung cũng đ&atilde; hợp t&aacute;c với c&aacute;c &ocirc;ng lớn như Microsoft, Google v&agrave; Meta nhằm tối ưu c&aacute;c ứng dụng từ l&otilde;i để ph&ugrave; hợp hơn với điện thoại m&agrave;n h&igrave;nh gập. C&aacute;c ứng dụng như Outlook, Google Sheets, YouTube, Telegram, Facebook,... v&agrave; c&aacute;c ứng dụng thuộc Google cũng đ&atilde; hỗ trợ tỉ lệ m&agrave;n h&igrave;nh của Samsung hơn, gi&uacute;p người d&ugrave;ng sử dụng một c&aacute;ch dễ d&agrave;ng v&agrave; tối ưu hơn.</p>\r\n<h3>Pin sử dụng cả ng&agrave;y d&agrave;i</h3>\r\n<p>Về pin th&igrave; sản phẩm năm nay dường như kh&ocirc;ng c&oacute; thay đổi về th&ocirc;ng số,&nbsp;<a title=\"Tham khảo điện thoại sạc nhanh tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd-sac-pin-nhanh\" target=\"_blank\" rel=\"noopener\">điện thoại c&oacute; sạc nhanh</a>&nbsp;25 W v&agrave; dung lượng pin vẫn l&agrave; 4400 mAh, tuy nhi&ecirc;n với sự thay đổi về phần cứng cũng như tối ưu phần mềm th&igrave; thời gian sử dụng của Fold4 sẽ tăng 15-20% so với Fold3.</p>\r\n<p>Với c&aacute;c t&aacute;c vụ như duyệt web bằng Chrome, xem YouTube, chơi Li&ecirc;n Qu&acirc;n Mobile, lướt Facebook bằng m&agrave;n h&igrave;nh ch&iacute;nh th&igrave; m&aacute;y cho thời gian sử dụng khoảng 8 tiếng. Đ&acirc;y l&agrave; một con số kh&aacute; ổn với một thiết bị c&oacute; m&agrave;n h&igrave;nh lớn như vậy. Nếu bạn sử dụng m&agrave;n h&igrave;nh phụ th&igrave; con số n&agrave;y c&ograve;n tăng hơn nữa.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-67.jpg\"><img class=\" lazyloaded\" title=\"Thời gian sử dụng pin tr&ecirc;n Samsung Galaxy Z Fold4\" src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-67.jpg\" alt=\"Thời gian sử dụng pin tr&ecirc;n Samsung Galaxy Z Fold4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-67.jpg\" /></a></p>\r\n<p>Về thời gian nạp năng lượng, trong 30 ph&uacute;t sạc đầu ti&ecirc;n th&igrave; m&aacute;y c&oacute; thể sạc từ 0 - 49% pin v&agrave; mất khoảng 85 ph&uacute;t để sạc đầy 100% pin. Hơn nữa m&aacute;y c&ograve;n hỗ trợ sạc kh&ocirc;ng d&acirc;y 15 W v&agrave; sạc ngược kh&ocirc;ng d&acirc;y cho thiết bị kh&aacute;c.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-69-1.jpg\"><img class=\" lazyloaded\" title=\"Thời gian sạc pin tr&ecirc;n Samsung Galaxy Z Fold4\" src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-69-1.jpg\" alt=\"Thời gian sạc pin tr&ecirc;n Samsung Galaxy Z Fold4\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/250625/samsung-galaxy-z-fold4-69-1.jpg\" /></a></p>\r\n<p>Năm nay Samsung đ&atilde; tập trung v&agrave;o n&acirc;ng cấp phần mềm, camera cũng đ&atilde; được n&acirc;ng cấp mạnh mẽ v&agrave; c&oacute; một tỉ lệ m&agrave;n h&igrave;nh mới n&acirc;ng cao trải nghiệm người d&ugrave;ng. H&atilde;y đến Thế Giới Di Động gần nhất để c&ugrave;ng trải nghiệm si&ecirc;u phẩm mới đến từ Samsung.</p>', 100, 0, '<p class=\"lileft\">Cấu h&igrave;nh Samsung Galaxy Z Fold4 256GB</p>\r\n<p class=\"lileft\">M&agrave;n h&igrave;nh:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span class=\"comma\">Dynamic AMOLED 2X</span><span class=\"comma\">Ch&iacute;nh 7.6\" &amp; Phụ 6.2\"</span><span class=\"\">Quad HD+ (2K+)</span></p>\r\n<p class=\"lileft\">Hệ điều h&agrave;nh:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span class=\"\">Android 12</span></p>\r\n<p class=\"lileft\">Camera sau:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span class=\"\">Ch&iacute;nh 50 MP &amp; Phụ 12 MP, 10 MP</span></p>\r\n<p class=\"lileft\">Camera trước:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span class=\"\">10 MP &amp; 4 MP</span></p>\r\n<p class=\"lileft\">Chip:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span class=\"\">Snapdragon 8+ Gen 1</span></p>\r\n<p class=\"lileft\">RAM:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span class=\"\">12 GB</span></p>\r\n<p class=\"lileft\">Dung lượng lưu trữ:&nbsp; <span class=\"\">256 GB</span></p>\r\n<p class=\"lileft\">SIM:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span class=\"comma\">1 Nano SIM &amp; 1 eSIM</span><span class=\"\">Hỗ trợ 5G</span></p>\r\n<p class=\"lileft\">Pin, Sạc:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span class=\"comma\">4400 mAh</span><span class=\"\">25 W</span></p>', 0, 0, '2023-04-11 14:53:14', '2023-04-11 18:01:26', 1),
(23, 'Samsung Galaxy S23 Ultra 5G 256GB', 'samsung-galaxy-s23-ultra-5g-256gb', 26990000, 0, 0, 's23.jpg', 9, '<h3>Cuối c&ugrave;ng th&igrave; chiếc điện thoại Samsung Galaxy S23 cũng đ&atilde; ch&iacute;nh thức ra mắt tại sự kiện&nbsp;<a title=\"SỰ KIỆN GALAXY UNPACKED 2023\" href=\"https://www.thegioididong.com/tin-tuc/su-kien/su-kien-galaxy-unpacked-2023-925\" target=\"_blank\" rel=\"noopener\">Galaxy Unpacked</a>&nbsp;v&agrave;o đầu th&aacute;ng 2 năm 2023, m&aacute;y nổi bật với camera 200 MP chất lượng, hiệu năng mạnh mẽ nhờ t&iacute;ch hợp con chip Snapdragon 8 Gen 2 v&agrave; được trang bị th&ecirc;m nhiều c&ocirc;ng nghệ h&agrave;ng đầu trong giới&nbsp;<a title=\"Tham khảo điện thoại kinh doanh tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" rel=\"noopener\">smartphone</a>.</h3>\r\n<h3>Bảng so s&aacute;nh th&ocirc;ng số phi&ecirc;n bản S23 Ultra v&agrave; S22 Ultra</h3>\r\n<table border=\"1\" cellspacing=\"1\" cellpadding=\"1\">\r\n<tbody>\r\n<tr>\r\n<td>Ti&ecirc;u ch&iacute;</td>\r\n<td>Samsung Galaxy S23 Ultra</td>\r\n<td>Samsung Galaxy S22 Ultra</td>\r\n</tr>\r\n<tr>\r\n<td>M&agrave;n h&igrave;nh</td>\r\n<td>Dynamic AMOLED 2X, 6.8 inch</td>\r\n<td>Dynamic AMOLED 2X, 6.8 inch</td>\r\n</tr>\r\n<tr>\r\n<td>CPU</td>\r\n<td><strong>Snapdragon 8 Gen 2 8 nh&acirc;n</strong></td>\r\n<td>Snapdragon 8 Gen 1 8 nh&acirc;n</td>\r\n</tr>\r\n<tr>\r\n<td>Camera sau</td>\r\n<td>Ch&iacute;nh 200 MP &amp; Phụ 12 MP, 10 MP, 10 MP</td>\r\n<td>Ch&iacute;nh 108 MP &amp; Phụ 12 MP, 10 MP, 10 MP</td>\r\n</tr>\r\n<tr>\r\n<td>Camera trước</td>\r\n<td>12 MP</td>\r\n<td>40 MP</td>\r\n</tr>\r\n<tr>\r\n<td>Dung lượng</td>\r\n<td><strong>256 GB, 512 GB, 1 TB</strong></td>\r\n<td>128 GB, 256 GB, 512GB</td>\r\n</tr>\r\n<tr>\r\n<td>Pin</td>\r\n<td>5000 mAh</td>\r\n<td>5000 mAh</td>\r\n</tr>\r\n<tr>\r\n<td>M&agrave;u sắc</td>\r\n<td>T&iacute;m, kem, xanh, đen</td>\r\n<td>Hồng, trắng, đen, xanh l&aacute;</td>\r\n</tr>\r\n<tr>\r\n<td>Tốc độ sạc</td>\r\n<td>45 W</td>\r\n<td>45 W</td>\r\n</tr>\r\n<tr>\r\n<td>K&iacute;ch thước</td>\r\n<td>D&agrave;i 163.4 mm - Ngang 78.1 mm - D&agrave;y 8.9 mm</td>\r\n<td>D&agrave;i 163.3 mm - Ngang 77.9 mm - D&agrave;y 8.9 mm</td>\r\n</tr>\r\n<tr>\r\n<td>Trọng lượng</td>\r\n<td>223 g</td>\r\n<td>228 g</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h3>Thiết kế đặc trưng</h3>\r\n<p>Về thiết kế th&igrave;&nbsp;<a title=\"Tham khảo th&ocirc;ng tin Samsung Galaxy S23 Ultra 5G tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd/samsung-galaxy-s23-ultra\" target=\"_blank\" rel=\"noopener\">Samsung Galaxy S23 Ultra</a>&nbsp;sẽ tiếp tục thừa hưởng kiểu d&aacute;ng sang trọng đến từ thế hệ trước, vẫn l&agrave; bộ khung kim loại, mặt lưng k&iacute;nh c&ugrave;ng cạnh b&ecirc;n v&agrave; m&agrave;n h&igrave;nh bo cong. Nhờ kiểu bo cong n&agrave;y m&agrave; trải nghiệm cầm nắm cũng trở n&ecirc;n dễ chịu, viền m&agrave;n h&igrave;nh hai b&ecirc;n cũng sẽ trở n&ecirc;n mỏng hơn.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/2023/02/timerseo/1-1020x570-1.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Thiết kế mặt lưng - Samsung Galaxy S23 Ultra 5G\" src=\"https://cdn.tgdd.vn/2023/02/timerseo/1-1020x570-1.jpg\" alt=\"Thiết kế mặt lưng - Samsung Galaxy S23 Ultra 5G\" data-src=\"https://cdn.tgdd.vn/2023/02/timerseo/1-1020x570-1.jpg\" /></a></p>\r\n<p>Về m&agrave;u sắc, năm nay Samsung cũng đ&atilde; cho ra c&aacute;c phi&ecirc;n bản m&agrave;u như: T&iacute;m, kem, xanh v&agrave; đen. Nh&igrave;n chung th&igrave; đ&acirc;y l&agrave; những m&agrave;u sắc cực kỳ sang trọng v&agrave; lịch l&atilde;m, ph&ugrave; hợp cho c&aacute;c bạn trẻ năng động, mạnh mẽ v&agrave; đặc biệt l&agrave; những kh&aacute;ch h&agrave;ng đang l&agrave; doanh nh&acirc;n bởi ngoại h&igrave;nh đẳng cấp v&agrave; thanh lịch.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-070223-040719.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Thiết kế thanh lịch - Samsung Galaxy S23 Ultra 5G 256GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-070223-040719.jpg\" alt=\"Thiết kế thanh lịch - Samsung Galaxy S23 Ultra 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-070223-040719.jpg\" /></a></p>\r\n<p>V&agrave; kể từ Samsung S22 th&igrave; d&ograve;ng Ultra đ&atilde; được trang bị b&uacute;t S Pen b&ecirc;n trong th&acirc;n m&aacute;y, v&igrave; thế Galaxy S23 Ultra cũng sẽ được trang bị tương tự, điều n&agrave;y gi&uacute;p cho kh&aacute;ch h&agrave;ng c&oacute; thể ghi ch&uacute; nhanh, đ&aacute;nh dấu v&agrave; chỉnh sửa văn bản hay h&igrave;nh ảnh dưới dạng chữ viết tay hoặc ph&aacute;c thảo một c&aacute;ch thuận tiện.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/2023/02/timerseo/2-1020x570-1.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Thiết kế mặt lưng - Samsung Galaxy S23 Ultra 5G\" src=\"https://cdn.tgdd.vn/2023/02/timerseo/2-1020x570-1.jpg\" alt=\"Thiết kế mặt lưng - Samsung Galaxy S23 Ultra 5G\" data-src=\"https://cdn.tgdd.vn/2023/02/timerseo/2-1020x570-1.jpg\" /></a></p>\r\n<h3>M&agrave;n h&igrave;nh lớn, hiển thị tốt mọi h&igrave;nh ảnh</h3>\r\n<p>Đối với phần m&agrave;n h&igrave;nh th&igrave; Galaxy S23 Ultra sử dụng tấm nền Dynamic AMOLED 2X c&oacute; k&iacute;ch thước 6.8 inch với tần số qu&eacute;t 120 Hz, v&agrave; nổi bật kh&ocirc;ng k&eacute;m đ&oacute; l&agrave; độ ph&acirc;n giải m&agrave;n h&igrave;nh QHD+ (1400 x 3088 Pixels).</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-070223-040758.jpg\"><img class=\" lazyloaded\" title=\"Sử dụng m&agrave;n h&igrave;nh chất lượng - Samsung Galaxy S23 Ultra 5G 256GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-070223-040758.jpg\" alt=\"Sử dụng m&agrave;n h&igrave;nh chất lượng - Samsung Galaxy S23 Ultra 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-070223-040758.jpg\" /></a></p>\r\n<p>Nhờ đ&oacute; m&agrave; kh&ocirc;ng gian hiển thị hết sức rộng r&atilde;i để người d&ugrave;ng c&oacute; thể xem phim thỏa th&iacute;ch, thao t&aacute;c vuốt chạm giờ đ&acirc;y cũng trở n&ecirc;n trơn tru v&agrave; mượt mắt hơn gi&uacute;p cho mọi cử chỉ trong l&uacute;c chơi game của bạn được phản hồi nhanh ch&oacute;ng.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/2023/02/timerseo/3-1020x570-1.jpg\"><img class=\" lazyloaded\" title=\"M&agrave;n h&igrave;nh lớn, hiển thị tốt - Samsung Galaxy S23 Ultra\" src=\"https://cdn.tgdd.vn/2023/02/timerseo/3-1020x570-1.jpg\" alt=\"M&agrave;n h&igrave;nh lớn, hiển thị tốt - Samsung Galaxy S23 Ultra\" data-src=\"https://cdn.tgdd.vn/2023/02/timerseo/3-1020x570-1.jpg\" /></a></p>\r\n<p>Với tấm nền&nbsp;Dynamic AMOLED 2X cao cấp gi&uacute;p&nbsp;mang đến cho bạn một g&oacute;c nh&igrave;n rộng, m&agrave;u sắc tươi mới đầy rực rỡ để biến mọi nội dung bạn xem trở n&ecirc;n ngập tr&agrave;n sức sống, sự sắc n&eacute;t nhờ độ ph&acirc;n giải cao cũng sẽ đem lại cho bạn trải nghiệm v&ocirc; c&ugrave;ng tuyệt vời bởi n&oacute; gi&uacute;p t&aacute;i hiện lại c&aacute;c bộ phim được ch&acirc;n thực.</p>\r\n<h3>Samsung Galaxy S23 Ultra c&oacute; hiệu năng vượt mong đợi</h3>\r\n<p>Galaxy S23 Ultra sử dụng con chip mới nhất đến từ nh&agrave; Qualcomm l&agrave; Snapdragon 8 Gen 2 với hiệu suất tối đa đến 3.36 GHz, khi kết hợp với GPU Adreno 740 sẽ mang lại hiệu suất ấn tượng. Ngo&agrave;i ra, hệ điều h&agrave;nh m&agrave; m&aacute;y sử dụng sẽ l&agrave; Android thế hệ 13 mới nhất trong giới&nbsp;<a title=\"Tham khảo điện thoại Android kinh doanh tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd?g=android\" target=\"_blank\" rel=\"noopener\">điện thoại Android</a>, điều n&agrave;y đem đến cho bạn những thay đổi về giao diện cũng như t&iacute;nh năng tốt nhất đến từ Google.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-070223-040827.jpg\"><img class=\" lazyloaded\" title=\"Hiệu năng vượt trội - Samsung Galaxy S23 Ultra 5G 256GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-070223-040827.jpg\" alt=\"Hiệu năng vượt trội - Samsung Galaxy S23 Ultra 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-070223-040827.jpg\" /></a></p>\r\n<p>Được biết th&igrave; con chip lần n&agrave;y c&ograve;n c&oacute; t&ecirc;n gọi độc quyền kh&aacute;c l&agrave;&nbsp;Snapdragon 8 Gen 2 For Galaxy, đ&acirc;y l&agrave; phi&ecirc;n bản d&agrave;nh ri&ecirc;ng cho c&aacute;c sản phẩm thuộc d&ograve;ng Galaxy được cải tiến về mặt hiệu suất cũng như sự tương th&iacute;ch với thiết bị tốt hơn so với phi&ecirc;n bản th&ocirc;ng thường, v&igrave; thế m&agrave; hiệu năng mang lại l&agrave; gần như v&ocirc; đối tr&ecirc;n thị trường điện thoại Android hiện tại.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-070223-041017.jpg\"><img class=\" lazyloaded\" title=\"Sử dụng con chip độc quyền cho d&ograve;ng Galaxy - Samsung Galaxy S23 Ultra 5G 256GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-070223-041017.jpg\" alt=\"Sử dụng con chip độc quyền cho d&ograve;ng Galaxy - Samsung Galaxy S23 Ultra 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-070223-041017.jpg\" /></a></p>\r\n<p>Nhiệt độ l&agrave; vấn đề đ&aacute;ng lo ngại tr&ecirc;n những chiếc flagship hiện nay, tuy nhi&ecirc;n với S22 series th&igrave; Samsung đ&atilde; l&agrave;m rất tốt, với S23 series bạn d&ugrave;ng cũng c&oacute; thể tin tưởng Samsung sẽ cải tiến v&agrave; mang đến một sản phẩm mang hiệu năng ổn định tr&ecirc;n một chiếc&nbsp;<a title=\"Tham khảo điện thoại chơi game tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd-choi-game\" target=\"_blank\" rel=\"noopener\">điện thoại chơi game</a>&nbsp;c&oacute; nhiệt độ kh&ocirc;ng qu&aacute; cao.</p>\r\n<h3>Camera tập trung v&agrave;o chất lượng v&agrave; chế độ chuy&ecirc;n nghiệp</h3>\r\n<p>Camera ch&iacute;nh của m&aacute;y th&igrave; h&atilde;ng vẫn giữ nguy&ecirc;n số lượng l&agrave; 4 camera v&agrave; cảm biến ch&iacute;nh c&oacute; độ ph&acirc;n giải 200 MP, k&egrave;m theo đ&oacute; l&agrave; ba cảm biến phụ c&oacute; độ ph&acirc;n giải lần lượt l&agrave;&nbsp;12 MP, 10 MP, 10 MP. Đ&acirc;y được xem l&agrave; một bước tiến rất lớn đến từ nh&agrave; Samsung khi n&acirc;ng từ&nbsp;108 MP l&ecirc;n 200 MP so với thế hệ cũ, nhờ vậy m&agrave; độ chi tiết của mỗi bức ảnh chụp ra đều v&ocirc; c&ugrave;ng r&otilde; n&eacute;t, xứng tầm với một v&agrave;i d&ograve;ng m&aacute;y ảnh chuy&ecirc;n nghiệp DSLR.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-070223-041140.jpg\"><img class=\" lazyloaded\" title=\"Sử dụng bộ 4 ống k&iacute;nh - Samsung Galaxy S23 Ultra 5G 256GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-070223-041140.jpg\" alt=\"Sử dụng bộ 4 ống k&iacute;nh - Samsung Galaxy S23 Ultra 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-070223-041140.jpg\" /></a></p>\r\n<p>Năm nay th&igrave; h&atilde;ng&nbsp;<a title=\"Tham khảo điện thoại Samsung kinh doanh tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd-samsung\" target=\"_blank\" rel=\"noopener\">điện thoại Samsung</a>&nbsp;vẫn sẽ tiếp tục cải tiến phần mềm để tối ưu hơn cho việc chụp ảnh qua c&aacute;c t&iacute;nh năng như:&nbsp;Chụp đ&ecirc;m, ch&acirc;n dung đ&ecirc;m, chụp zoom,chế độ chuy&ecirc;n nghiệp c&ugrave;ng nhiều chế độ đặc biệt kh&aacute;c.&nbsp;</p>\r\n<p>Với cụm camera n&agrave;y bạn c&oacute; thể quay video đến 8K, cũng như hỗ trợ tốt hơn ở chế độ điện ảnh, điều n&agrave;y gi&uacute;p c&aacute;c t&iacute;n đồ chụp ảnh chuy&ecirc;n nghiệp thỏa m&atilde;n hơn khi sử dụng Samsung Galaxy S23 Ultra. V&agrave; cũng nhờ sự n&acirc;ng cấp tr&ecirc;n camera n&ecirc;n khả năng quay video ban đ&ecirc;m cũng đ&atilde; được cải thiện chất lượng, ph&ugrave; hợp cho những bạn đam m&ecirc; sản xuất những nội dung, thước phim chuy&ecirc;n v&agrave;o ban đ&ecirc;m hay trong m&ocirc;i trường c&oacute; điều kiện &aacute;nh s&aacute;ng yếu.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/2023/02/timerseo/4-1020x570-1.jpg\"><img class=\" lazyloaded\" title=\"Cụm camera nổi bật - Samsung Galaxy S23 Ultra 5G\" src=\"https://cdn.tgdd.vn/2023/02/timerseo/4-1020x570-1.jpg\" alt=\"Cụm camera nổi bật - Samsung Galaxy S23 Ultra 5G\" data-src=\"https://cdn.tgdd.vn/2023/02/timerseo/4-1020x570-1.jpg\" /></a></p>\r\n<p>Camera trước của S23 Ultra c&oacute; độ ph&acirc;n giải 12 MP, m&aacute;y sẽ mang lại bức ảnh selfie chất lượng cao nhất cũng như hỗ trợ quay video với định dạng 4K, c&ugrave;ng bạn ghi lại mọi khoảnh khắc ở mọi điều kiện một c&aacute;ch dễ d&agrave;ng.</p>\r\n<h3>Sở hữu nhiều c&ocirc;ng nghệ pin</h3>\r\n<p>Samsung Galaxy S23 Ultra được trang bị vi&ecirc;n pin 5000 mAh tương tự thế hệ tiền nhiệm, cho đến thời điểm ra mắt th&igrave; đ&acirc;y vẫn l&agrave; mức dung lượng rất ấn tượng với thời gian sử dụng c&oacute; thể đ&aacute;p ứng tối đa khoảng 8 tiếng với c&aacute;c t&aacute;c vụ th&ocirc;ng thường, ngo&agrave;i ra th&igrave; m&aacute;y cũng hỗ trợ sạc nhanh đến 45 W gi&uacute;p tối ưu thời gian lấp đầy vi&ecirc;n pin chỉ c&ograve;n trong t&iacute;ch tắc.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-070223-041305.jpg\"><img class=\" lazyloaded\" title=\"Thoải m&aacute;i sử dụng d&agrave;i l&acirc;u - Samsung Galaxy S23 Ultra 5G 256GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-070223-041305.jpg\" alt=\"Thoải m&aacute;i sử dụng d&agrave;i l&acirc;u - Samsung Galaxy S23 Ultra 5G 256GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/249948/samsung-galaxy-s23-ultra-070223-041305.jpg\" /></a></p>\r\n<p>B&ecirc;n cạnh c&aacute;c c&ocirc;ng nghệ về pin hay c&ocirc;ng suất sạc th&igrave; h&atilde;ng c&ograve;n trang bị th&ecirc;m cho m&aacute;y c&aacute;c c&ocirc;ng nghệ đi k&egrave;m như: Sạc kh&ocirc;ng d&acirc;y, sạc ngược kh&ocirc;ng d&acirc;y đầy tiện lợi nhằm gi&uacute;p đa dạng h&oacute;a phương thức sạc để ph&ugrave; hợp với nhu cầu v&agrave; kh&ocirc;ng gian l&agrave;m việc của bạn.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/2023/02/timerseo/5-1020x570-1.jpg\"><img class=\" lazyloaded\" title=\"Sạc pin qua cổng kết nối Type C - Samsung Galaxy S23 Ultra 5G\" src=\"https://cdn.tgdd.vn/2023/02/timerseo/5-1020x570-1.jpg\" alt=\"Sạc pin qua cổng kết nối Type C - Samsung Galaxy S23 Ultra 5G\" data-src=\"https://cdn.tgdd.vn/2023/02/timerseo/5-1020x570-1.jpg\" /></a></p>\r\n<h3><strong>V&igrave; sao bạn n&ecirc;n chọn mua Samsung Galaxy S23 Ultra tại Thế Giới Di Động?</strong></h3>\r\n<p>Thế Giới Di Động lu&ocirc;n l&agrave; c&aacute;i t&ecirc;n được c&acirc;n nhắc đầu ti&ecirc;n khi người d&ugrave;ng c&oacute; nhu cầu mua sản phẩm di động mới bởi đ&acirc;y l&agrave; hệ thống kinh doanh những sản phẩm ch&iacute;nh h&atilde;ng c&oacute; khuyến m&atilde;i, hậu m&atilde;i v&ocirc; c&ugrave;ng lớn cũng như dịch vụ chăm s&oacute;c kh&aacute;ch h&agrave;ng tận t&acirc;m như người nh&agrave;.</p>\r\n<p>Hiện tại khi qu&yacute; kh&aacute;ch h&agrave;ng cọc trước sản phẩm&nbsp;Samsung Galaxy S23 Ultra tại Thế Giới Di Động th&igrave; sẽ nhận được ưu đ&atilde;i giảm gi&aacute; 5 triệu, một g&oacute;i Microsoft Office 365 Personal 1 năm, c&oacute; cơ hội tr&uacute;ng g&oacute;i bảo h&agrave;nh 2 năm d&agrave;nh cho 2000 suất đặt sớm nhất c&ugrave;ng&nbsp;600 suất giảm gi&aacute; 2 triệu qua VNPAY. Ngo&agrave;i ra th&igrave; c&ograve;n c&oacute; một v&agrave;i ch&iacute;nh s&aacute;ch d&agrave;nh cho những bạn muốn l&ecirc;n đời sản phẩm như&nbsp;t&agrave;i trợ 3 triệu&nbsp;khi thu cũ đổi mới hay hỗ trợ trả g&oacute;p 0% hết sức hấp dẫn.</p>\r\n<h3><strong>Tổng hợp c&aacute;c c&acirc;u hỏi xung quanh sản phẩm Samsung Galaxy S23 Ultra</strong></h3>\r\n<h4><strong>Samsung Galaxy S23 Ultra c&oacute; bao nhi&ecirc;u m&agrave;u?</strong></h4>\r\n<p>Samsung Galaxy S23 Ultra sẽ mang 4 m&agrave;u sắc đặc trưng như: T&iacute;m, kem, xanh v&agrave; đen. C&aacute;c m&agrave;u s&aacute;ng tr&ocirc;ng kh&aacute; trẻ trung, m&agrave;u tối kh&aacute; lịch l&atilde;m, ph&ugrave; hợp cho đại đa số người d&ugrave;ng.</p>\r\n<p>Đến hết ng&agrave;y 28/02/2023, khi mua Samsung Galaxy S23 series tại Thế Giới Di Động bạn sẽ được giảm ngay 3 triệu với Galaxy S23, 4 triệu với Galaxy S23+ v&agrave; 5 triệu cho Galaxy S23 Ultra. Nhanh tay đặt ngay!</p>\r\n<h4><strong>Samsung Galaxy S23 Ultra c&oacute; mấy loại dung lượng?</strong></h4>\r\n<p>Samsung Galaxy S23 Ultra được ra mắt với 3 phi&ecirc;n bản dung lượng:</p>\r\n<p>- Samsung Galaxy S23 Ultra (8GB/256GB) phi&ecirc;n bản ti&ecirc;u chuẩn</p>\r\n<p>-&nbsp;Samsung Galaxy S23 Ultra (12GB/512GB)</p>\r\n<p>-&nbsp;Samsung Galaxy S23 Ultra&nbsp;(12GB/1TB)</p>\r\n<h4><strong>B&uacute;t S-Pen c&oacute; đi k&egrave;m với Samsung Galaxy S23 Ultra?</strong></h4>\r\n<p>Từ năm 2022 khi ra mắt Galaxy S22 Ultra th&igrave; Samsung đ&atilde; trang bị b&uacute;t S Pen b&ecirc;n trong th&acirc;n m&aacute;y, năm nay cũng c&oacute; thể S23 Ultra được trang bị b&uacute;t S Pen để hỗ trợ người d&ugrave;ng được tốt hơn.</p>\r\n<h4><strong>Samsung Galaxy S23 Ultra khi n&agrave;o ra mắt?</strong></h4>\r\n<p>Samsung đ&atilde; x&aacute;c nhận Samsung Galaxy S23 series sẽ được ra mắt tại sự kiện Galaxy Unpacked diễn ra v&agrave;o đầu th&aacute;ng 2 năm nay, trong đ&oacute; sẽ c&oacute; Samsung Galaxy S23 Ultra v&agrave; một số sản phẩm kh&aacute;c của h&atilde;ng.</p>', 100, 0, '\r\n<p class=\"lileft\">M&agrave;n h&igrave;nh:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span class=\"comma\">Dynamic AMOLED 2X</span><span class=\"comma\">6.8\"</span><span class=\"\">Quad HD+ (2K+)</span></p>\r\n<p class=\"lileft\">Hệ điều h&agrave;nh:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span class=\"\">Android 13</span></p>\r\n<p class=\"lileft\">Camera sau:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span class=\"\">Ch&iacute;nh 200 MP &amp; Phụ 12 MP, 10 MP, 10 MP</span></p>\r\n<p class=\"lileft\">Camera trước:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span class=\"\">12 MP</span></p>\r\n<p class=\"lileft\">Chip:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span class=\"\">Snapdragon 8 Gen 2 8 nh&acirc;n</span></p>\r\n<p class=\"lileft\">RAM:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span class=\"\">8 GB</span></p>\r\n<p class=\"lileft\">Dung lượng lưu trữ:&nbsp; &nbsp;<span class=\"\">256 GB</span></p>\r\n<p class=\"lileft\">SIM:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span class=\"comma\">2 Nano SIM hoặc 1 Nano SIM + 1 eSIM</span><span class=\"\">Hỗ trợ 5G</span></p>\r\n<p class=\"lileft\">Pin, Sạc:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span class=\"comma\">5000 mAh</span><span class=\"\">45 W</span></p>', 0, 0, '2023-04-11 14:59:16', '2023-04-11 15:04:42', 1);
INSERT INTO `product` (`id`, `name`, `slug`, `price`, `sale`, `pay`, `thumbar`, `category_id`, `content`, `number`, `head`, `short_description`, `view`, `hot`, `created_at`, `updated_at`, `home`) VALUES
(24, 'Samsung Galaxy Tab S8 Ultra 5G', 'samsung-galaxy-tab-s8-ultra-5g', 30990000, 0, 0, 'samtab.jpg', 9, '<h3><a href=\"https://www.thegioididong.com/may-tinh-bang/samsung-galaxy-tab-s8-ultra\" target=\"_blank\" rel=\"noopener\">Samsung Galaxy Tab S8 Ultra</a>&nbsp;ra mắt với k&iacute;ch thước m&agrave;n h&igrave;nh si&ecirc;u to c&ugrave;ng một cấu h&igrave;nh mạnh mẽ, được xem l&agrave; thiết bị ph&ugrave; hợp đối với những ai thường xuy&ecirc;n l&agrave;m c&aacute;c c&ocirc;ng việc thiết kế hay thao t&aacute;c tr&ecirc;n tr&igrave;nh duyệt web, b&ecirc;n cạnh đ&oacute; Tab S8 Ultra c&ograve;n mang đến những trải nghiệm tương tự một chiếc laptop khi sử dụng k&egrave;m với b&agrave;n ph&iacute;m.</h3>\r\n<h3>Kh&ocirc;ng gian l&agrave;m việc rộng lớn</h3>\r\n<p>Samsung Galaxy Tab S8 Ultra trang bị tấm nền Super AMOLED với k&iacute;ch thước l&ecirc;n đến 14.6 inch, điều n&agrave;y gi&uacute;p cho h&igrave;nh ảnh hiển thị được rực rỡ v&agrave; bắt mắt hơn khi trải nghiệm tr&ecirc;n một chiếc m&agrave;n h&igrave;nh khủng.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/522/247513/samsung-galaxy-tab-s8-ultra-300522-104912.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Tấm nền chất lượng - Samsung Galaxy Tab S8 Ultra\" src=\"https://cdn.tgdd.vn/Products/Images/522/247513/samsung-galaxy-tab-s8-ultra-300522-104912.jpg\" alt=\"Tấm nền chất lượng - Samsung Galaxy Tab S8 Ultra\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/247513/samsung-galaxy-tab-s8-ultra-300522-104912.jpg\" /></a></p>\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/man-hinh-super-amoled-hd-la-gi-905777\" target=\"_blank\" rel=\"noopener\">M&agrave;n h&igrave;nh Super AMOLED l&agrave; g&igrave;? Thiết bị n&agrave;o c&oacute; m&agrave;n h&igrave;nh SUPER AMOLED?</a></p>\r\n<p>B&ecirc;n cạnh đ&oacute; Galaxy Tab S8 Ultra c&ograve;n được hỗ trợ tần số qu&eacute;t l&ecirc;n đến 120 Hz, giờ đ&acirc;y người d&ugrave;ng c&oacute; thể cảm nhận độ mượt m&agrave; tr&ecirc;n mọi thao t&aacute;c vuốt chạm, hay chơi những tựa game FPS cao được đ&atilde; mắt hơn bao giờ hết.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/522/247513/samsung-galaxy-tab-s8-ultra-300522-104914.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Thao t&aacute;c mượt m&agrave; - Samsung Galaxy Tab S8 Ultra\" src=\"https://cdn.tgdd.vn/Products/Images/522/247513/samsung-galaxy-tab-s8-ultra-300522-104914.jpg\" alt=\"Thao t&aacute;c mượt m&agrave; - Samsung Galaxy Tab S8 Ultra\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/247513/samsung-galaxy-tab-s8-ultra-300522-104914.jpg\" /></a></p>\r\n<p>Với k&iacute;ch thước m&agrave;n h&igrave;nh lớn gi&uacute;p cho c&aacute;c t&aacute;c vụ vẽ vời hay chơi game trở n&ecirc;n tốt hơn, đ&acirc;y được xem l&agrave; điều được c&aacute;c nh&agrave; thiết kế, kiến tr&uacute;c sư hay n&oacute;i chung l&agrave; người d&ugrave;ng l&agrave;m việc thuộc khối văn ph&ograve;ng cực kỳ quan t&acirc;m v&agrave; y&ecirc;u th&iacute;ch.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/522/247513/samsung-galaxy-tab-s8-ultra-220822-015614.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"M&agrave;n h&igrave;nh k&iacute;ch thước lớn - Samsung Galaxy Tab S8 Ultra\" src=\"https://cdn.tgdd.vn/Products/Images/522/247513/samsung-galaxy-tab-s8-ultra-220822-015614.jpg\" alt=\"M&agrave;n h&igrave;nh k&iacute;ch thước lớn - Samsung Galaxy Tab S8 Ultra\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/247513/samsung-galaxy-tab-s8-ultra-220822-015614.jpg\" /></a></p>\r\n<p>Ngo&agrave;i ra người d&ugrave;ng c&oacute; thể mở đồng thời nhiều cửa sổ với nhiều ứng dụng kh&aacute;c nhau qua t&iacute;nh năng chia đ&ocirc;i m&agrave;n h&igrave;nh - Multi Window. Giờ đ&acirc;y bạn cũng c&oacute; thể vừa xem văn bản cuộc họp v&agrave; vừa c&oacute; thể thuyết tr&igrave;nh một c&aacute;ch dễ d&agrave;ng.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/522/247513/samsung-galaxy-tab-s8-ultra-220822-015612.jpg\"><img class=\" lazyloaded\" title=\"M&agrave;n h&igrave;nh lớn - Samsung Galaxy Tab S8 Ultra\" src=\"https://cdn.tgdd.vn/Products/Images/522/247513/samsung-galaxy-tab-s8-ultra-220822-015612.jpg\" alt=\"M&agrave;n h&igrave;nh lớn - Samsung Galaxy Tab S8 Ultra\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/247513/samsung-galaxy-tab-s8-ultra-220822-015612.jpg\" /></a></p>\r\n<h3>Cấu h&igrave;nh mạnh mẽ h&agrave;ng đầu</h3>\r\n<p>Galaxy Tab S8 Ultra được&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang-samsung\" target=\"_blank\" rel=\"noopener\">Samsung</a>&nbsp;trang bị b&ecirc;n trong con chip c&oacute; hiệu năng mạnh mẽ h&agrave;ng đầu tr&ecirc;n c&aacute;c thiết bị Android (05/2022) c&oacute; t&ecirc;n Snapdragon 8 Gen 1, gi&uacute;p xử l&yacute; c&aacute;c c&ocirc;ng việc như chỉnh sửa video nhanh ch&oacute;ng hay chiến game đồ họa cao một c&aacute;ch mượt m&agrave;.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/522/247513/samsung-galaxy-tab-s8-ultra-300522-104916.jpg\"><img class=\" lazyloaded\" title=\"Hiệu năng mạnh mẽ - Samsung Galaxy Tab S8 Ultra\" src=\"https://cdn.tgdd.vn/Products/Images/522/247513/samsung-galaxy-tab-s8-ultra-300522-104916.jpg\" alt=\"Hiệu năng mạnh mẽ - Samsung Galaxy Tab S8 Ultra\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/247513/samsung-galaxy-tab-s8-ultra-300522-104916.jpg\" /></a></p>\r\n<p>Nhằm mang đến sự tối ưu d&agrave;nh cho những bạn l&agrave;m việc tr&ecirc;n c&aacute;c tr&igrave;nh duyệt web, Samsung trang bị cho m&aacute;y 8 GB RAM để gi&uacute;p bạn c&oacute; thể l&agrave;m việc v&agrave; thao t&aacute;c nhiều tab c&ugrave;ng một l&uacute;c m&agrave; &iacute;t khi gặp phải t&igrave;nh trạng tải lại từ đầu.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/522/247513/samsung-galaxy-tab-s8-ultra-300522-104918.jpg\"><img class=\" lazyloaded\" title=\"Trang bị 8 GB RAM - Samsung Galaxy Tab S8 Ultra\" src=\"https://cdn.tgdd.vn/Products/Images/522/247513/samsung-galaxy-tab-s8-ultra-300522-104918.jpg\" alt=\"Trang bị 8 GB RAM - Samsung Galaxy Tab S8 Ultra\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/247513/samsung-galaxy-tab-s8-ultra-300522-104918.jpg\" /></a></p>\r\n<h3>Lưu trữ th&ocirc;ng tin dữ liệu r&otilde; n&eacute;t</h3>\r\n<p>Mặt sau của Galaxy Tab S8 Ultra sẽ l&agrave; cụm 2 camera c&oacute; độ ph&acirc;n giải lần lượt l&agrave; 12 MP v&agrave; 6 MP gi&uacute;p c&aacute;c t&aacute;c vụ chụp văn bản c&oacute; độ chi tiết cao, ngo&agrave;i ra bạn cũng c&oacute; thể ghi được những thước phim chất lượng th&ocirc;ng qua t&iacute;nh năng quay video chuẩn 4K.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/522/247513/samsung-galaxy-tab-s8-ultra-300522-104920.jpg\"><img class=\" lazyloaded\" title=\"Camera chất lượng - Samsung Galaxy Tab S8 Ultra\" src=\"https://cdn.tgdd.vn/Products/Images/522/247513/samsung-galaxy-tab-s8-ultra-300522-104920.jpg\" alt=\"Camera chất lượng - Samsung Galaxy Tab S8 Ultra\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/247513/samsung-galaxy-tab-s8-ultra-300522-104920.jpg\" /></a></p>\r\n<p>Ph&iacute;a trước phần m&agrave;n h&igrave;nh sẽ l&agrave; cụm 2 camera c&oacute; chung độ ph&acirc;n giải 12 MP, Galaxy Tab S8 Ultra mang lại cho bạn khả năng selfie tốt hơn hay thực hiện c&aacute;c t&aacute;c vụ video call được r&otilde; r&agrave;ng.</p>\r\n<h3>Thiết kế hợp xu hướng</h3>\r\n<p>Galaxy Tab S8 Ultra c&oacute; một diện mạo vu&ocirc;ng vức với c&aacute;c cạnh v&agrave; mặt lưng được gia c&ocirc;ng phẳng, đem đến một c&aacute;i nh&igrave;n thanh lịch, sang trọng v&agrave; v&ocirc; c&ugrave;ng hiện đại.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/522/247513/samsung-galaxy-tab-s8-ultra-300522-104922.jpg\"><img class=\" lazyloaded\" title=\"Diện mạo hợp xu hướng - Samsung Galaxy Tab S8 Ultra\" src=\"https://cdn.tgdd.vn/Products/Images/522/247513/samsung-galaxy-tab-s8-ultra-300522-104922.jpg\" alt=\"Diện mạo hợp xu hướng - Samsung Galaxy Tab S8 Ultra\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/247513/samsung-galaxy-tab-s8-ultra-300522-104922.jpg\" /></a></p>\r\n<p>Với chất liệu nh&ocirc;m nguy&ecirc;n khối v&agrave; được ho&agrave;n thiện mặt lưng c&oacute; độ nh&aacute;m nhẹ, nhờ vậy m&agrave; Galaxy Tab S8 Ultra sở hữu một độ bền bỉ cao c&ugrave;ng khả năng hạn chế được c&aacute;c vết b&aacute;m mồ h&ocirc;i v&agrave; dấu v&acirc;n tay trong qu&aacute; tr&igrave;nh sử dụng.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/522/247513/samsung-galaxy-tab-s8-ultra-220822-015608.jpg\"><img class=\" lazyloaded\" title=\"Mặt lưng nh&aacute;m - Samsung Galaxy Tab S8 Ultra\" src=\"https://cdn.tgdd.vn/Products/Images/522/247513/samsung-galaxy-tab-s8-ultra-220822-015608.jpg\" alt=\"Mặt lưng nh&aacute;m - Samsung Galaxy Tab S8 Ultra\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/247513/samsung-galaxy-tab-s8-ultra-220822-015608.jpg\" /></a></p>\r\n<p>Nổi bật ở mặt lưng l&agrave; một vệt đen được đặt ph&iacute;a dưới cụm camera tr&ocirc;ng hết sức mới lạ. Đ&acirc;y ch&iacute;nh l&agrave; nơi m&agrave; Samsung bố tr&iacute; để người d&ugrave;ng c&oacute; thể đặt b&uacute;t S Pen l&ecirc;n phần mặt lưng, c&oacute; hỗ trợ nam ch&acirc;m từ t&iacute;nh để vừa c&oacute; thể cố định được b&uacute;t cũng như t&iacute;ch hợp khả năng sạc kh&ocirc;ng d&acirc;y.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/522/247513/samsung-galaxy-tab-s8-ultra-220822-015610.jpg\"><img class=\" lazyloaded\" title=\"Mặt lưng cao cấp - Samsung Galaxy Tab S8 Ultra\" src=\"https://cdn.tgdd.vn/Products/Images/522/247513/samsung-galaxy-tab-s8-ultra-220822-015610.jpg\" alt=\"Mặt lưng cao cấp - Samsung Galaxy Tab S8 Ultra\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/247513/samsung-galaxy-tab-s8-ultra-220822-015610.jpg\" /></a></p>\r\n<p>M&aacute;y được bao bọc bởi một loại vật liệu cao cấp mang t&ecirc;n Armor Aluminum. Vừa mang đến khả năng gia tăng độ bền bỉ cao v&agrave; vừa gi&uacute;p m&aacute;y tối ưu khối lượng so với c&aacute;c loại hợp kim th&ocirc;ng thường được sử dụng tr&ecirc;n c&aacute;c d&ograve;ng smartphone.</p>\r\n<h3>Trang bị vi&ecirc;n pin cực khủng</h3>\r\n<p>B&ecirc;n trong một thiết bị c&oacute; k&iacute;ch thước si&ecirc;u mỏng chỉ với 5.5 mm l&agrave; một vi&ecirc;n pin lớn c&oacute; dung lượng 11200 mAh, đ&acirc;y được xem l&agrave; điểm vượt trội tr&ecirc;n Galaxy Tab S8 Ultra so với những chiếc&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\" rel=\"noopener\">tablet</a>&nbsp;kh&aacute;c tr&ecirc;n thị trường, vậy n&ecirc;n giờ đ&acirc;y người d&ugrave;ng c&oacute; thể thoải m&aacute;i sử dụng cả ng&agrave;y m&agrave; kh&ocirc;ng qu&aacute; lo lắng đến vấn đề hết pin.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/522/247513/samsung-galaxy-tab-s8-ultra-300522-104924.jpg\"><img class=\" lazyloaded\" title=\"Sử dụng d&agrave;i l&acirc;u  -Samsung Galaxy Tab S8 Ultra\" src=\"https://cdn.tgdd.vn/Products/Images/522/247513/samsung-galaxy-tab-s8-ultra-300522-104924.jpg\" alt=\"Sử dụng d&agrave;i l&acirc;u  -Samsung Galaxy Tab S8 Ultra\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/247513/samsung-galaxy-tab-s8-ultra-300522-104924.jpg\" /></a></p>\r\n<p>Với một kh&ocirc;ng gian hiển thị rộng lớn v&agrave; một cấu h&igrave;nh mạnh mẽ, Galaxy Tab S8 Ultra sẽ l&agrave; một sự lựa chọn kh&ocirc;ng thể bỏ qua đối với những ai đang băn khoăn muốn chọn mua cho m&igrave;nh một thiết bị phục vụ cho c&ocirc;ng việc, hay d&ugrave;ng để chơi game cấu h&igrave;nh cao.</p>', 100, 0, '<p class=\"lileft\">M&agrave;n h&igrave;nh:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span class=\"comma\">14.6\"</span><span class=\"\">Super AMOLED</span></p>\r\n<p class=\"lileft\">Hệ điều h&agrave;nh:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span class=\"\">Android 12</span></p>\r\n<p class=\"lileft\">Chip:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span class=\"\">Snapdragon 8 Gen 1</span></p>\r\n<p class=\"lileft\">RAM:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span class=\"\">8 GB</span></p>\r\n<p class=\"lileft\">Dung lượng lưu trữ:&nbsp; &nbsp; &nbsp; &nbsp;<span class=\"\">128 GB</span></p>\r\n<p class=\"lileft\">Kết nối:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span class=\"comma\">5G</span><span class=\"\">C&oacute; nghe gọi</span></p>\r\n<p class=\"lileft\">SIM:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span class=\"\">1 Nano SIM</span></p>\r\n<p class=\"lileft\">Camera sau:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span class=\"\">Ch&iacute;nh 13 MP &amp; Phụ 6 MP</span></p>\r\n<p class=\"lileft\">Camera trước:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span class=\"\">2 Camera 12 MP</span></p>\r\n<p class=\"lileft\">Pin, Sạc:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span class=\"comma\">11200 mAh</span><span class=\"\">45 W</span></p>', 0, 0, '2023-04-11 17:55:16', '2023-04-11 17:56:41', 1),
(25, 'Xiaomi 13 5G', 'xiaomi-13-5g', 22990000, 22, 0, 'xiaomi13.jpg', 10, '<h3>h&ocirc;ng tin ra mắt về&nbsp;<a title=\"Tham khảo điện thoại Xiaomi 13 đang kinh doanh tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd/xiaomi-13\" target=\"_blank\" rel=\"noopener\">Xiaomi 13</a>&nbsp;hiện đang l&agrave; chủ đề cực kỳ hot tr&ecirc;n c&aacute;c diễn đ&agrave;n bởi lần ra mắt n&agrave;y Xiaomi mang đến cho ch&uacute;ng ta kh&aacute; nhiều điều mới mẻ, từ con chip Snapdragon 8 Gen 2 cho đến camera hợp t&aacute;c với h&atilde;ng Leica cũng đủ để thu h&uacute;t c&aacute;c Mi fan hay t&iacute;n đồ đam m&ecirc; c&ocirc;ng nghệ.</h3>\r\n<h3>Thiết kế với th&acirc;n h&igrave;nh sang trọng</h3>\r\n<p>Năm nay, nh&agrave; Xiaomi kho&aacute;c l&ecirc;n m&igrave;nh sản phẩm mới với một vẻ ngo&agrave;i ho&agrave;n to&agrave;n kh&aacute;c so với thế hệ cũ, từ m&agrave;u sắc cho đến c&aacute;ch bố tr&iacute; cụm camera tr&ecirc;n Xiaomi 13 đều thể hiện l&ecirc;n một điểm độc lạ chưa từng c&oacute;.&nbsp;</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/267984/xiaomi-13-thiet-ke.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Thiết kế sang trọng - Xiaomi 13\" src=\"https://cdn.tgdd.vn/Products/Images/42/267984/xiaomi-13-thiet-ke.jpg\" alt=\"Thiết kế sang trọng - Xiaomi 13\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/267984/xiaomi-13-thiet-ke.jpg\" /></a></p>\r\n<p>Sử dụng kiểu d&aacute;ng vu&ocirc;ng vức c&ugrave;ng tone m&agrave;u hiện đại, cả hai điều n&agrave;y đem đến cho chiếc m&aacute;y một c&aacute;i nh&igrave;n cực kỳ sang trọng v&agrave; hợp thời, đ&acirc;y kh&ocirc;ng đơn thuần l&agrave; một chiếc&nbsp;<a title=\"Tham khảo điện thoại di động đang kinh doanh tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" rel=\"noopener\">điện thoại</a>&nbsp;th&ocirc;ng thường m&agrave; người d&ugrave;ng cũng c&oacute; thể sử dụng như một m&oacute;n phụ kiện thời trang cao cấp mỗi khi cầm nắm tr&ecirc;n tay.</p>\r\n<h3>Chụp ảnh tốt hơn với ống k&iacute;nh được tinh chỉnh từ nh&agrave; Leica</h3>\r\n<p>Nếu để &yacute; kỹ tr&ecirc;n th&acirc;n m&aacute;y của Xiaomi 13 th&igrave; ta c&oacute; thể dễ d&agrave;ng nhận thấy logo của Leica được in thẳng l&ecirc;n cụm camera, điều n&agrave;y cho thấy đ&acirc;y ch&iacute;nh l&agrave; mẫu&nbsp;<a title=\"Tham khảo điện thoại Xiaomi đang kinh doanh tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd-xiaomi\" target=\"_blank\" rel=\"noopener\">điện thoại Xiaomi</a>&nbsp;được kỳ vọng rất nhiều về khả năng chụp ảnh, bởi đ&acirc;y l&agrave; sản phẩm c&oacute; sự hỗ trợ sản xuất đến từ thương hiệu m&aacute;y ảnh - ống k&iacute;nh h&agrave;ng đầu thế giới.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/267984/xiaomi-13-camera.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Camera chuy&ecirc;n nghiệp - Xiaomi 13\" src=\"https://cdn.tgdd.vn/Products/Images/42/267984/xiaomi-13-camera.jpg\" alt=\"Camera chuy&ecirc;n nghiệp - Xiaomi 13\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/267984/xiaomi-13-camera.jpg\" /></a></p>\r\n<p>Xiaomi 13 được trang bị 3 camera v&agrave; trong đ&oacute; độ ph&acirc;n giải camera ch&iacute;nh đạt tới tới 50 MP, c&oacute; sử dụng cảm biến IMX 800 đến từ Sony c&oacute; k&iacute;ch thước 1/49 inch gi&uacute;p thu lại bức ảnh c&oacute; nhiều &aacute;nh s&aacute;ng hơn cũng như tăng cường độ chi tiết.</p>\r\n<p>C&oacute; sự hợp t&aacute;c của Leica n&ecirc;n người d&ugrave;ng c&oacute; thể trải nghiệm được nhiều t&iacute;nh năng th&uacute; vị cũng như bộ lọc m&agrave;u độc quyền đến từ h&atilde;ng, nhờ đ&oacute; m&agrave; mỗi bức h&igrave;nh chụp ra từ Xiaomi 13 đều mang một n&eacute;t đẹp ri&ecirc;ng biệt gi&uacute;p thu h&uacute;t người xem.</p>\r\n<h3>Sở hữu con chip mạnh mẽ từ nh&agrave; Qualcomm</h3>\r\n<p>Xiaomi 13 sẽ được t&iacute;ch hợp con chip h&agrave;ng đầu giới&nbsp;<a title=\"Tham khảo điện thoại Android đang kinh doanh tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd?g=android\" target=\"_blank\" rel=\"noopener\">điện thoại Android</a>&nbsp;hiện tại v&agrave; c&aacute;i t&ecirc;n được nhắc đến ở đ&acirc;y ch&iacute;nh l&agrave; mẫu chip Snapdragon 8 Gen 2 sở hữu cấu h&igrave;nh vượt trội với mức xung nhịp tối đa l&ecirc;n tới 3.2 GHz v&agrave; được sản xuất tr&ecirc;n tiến tr&igrave;nh 4 nm hiện đại.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/267984/xiaomi-13-333.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Sở hữu chipset h&agrave;ng đầu trong ng&agrave;nh - Xiaomi 13\" src=\"https://cdn.tgdd.vn/Products/Images/42/267984/xiaomi-13-333.jpg\" alt=\"Sở hữu chipset h&agrave;ng đầu trong ng&agrave;nh - Xiaomi 13\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/267984/xiaomi-13-333.jpg\" /></a></p>\r\n<p>Với những nhu cầu sử dụng cơ bản từ nghe gọi, xem phim thường ng&agrave;y cho đến việc thực hiện c&aacute;c t&aacute;c vụ chơi game nặng th&igrave; Xiaomi 13 cũng c&oacute; thể dễ d&agrave;ng c&acirc;n tất gi&uacute;p bạn c&oacute; được những ph&uacute;t gi&acirc;y thư gi&atilde;n trong những trận game mượt m&agrave; v&agrave; ổn định.</p>\r\n<h3>M&agrave;n h&igrave;nh sắc n&eacute;t c&ugrave;ng kh&ocirc;ng gian hiển thị rộng r&atilde;i</h3>\r\n<p>M&agrave;n h&igrave;nh của Xiaomi 13 được trang bị tấm nền AMOLED với tần số qu&eacute;t 120 Hz si&ecirc;u mượt m&agrave; c&ugrave;ng với đ&oacute; l&agrave; độ ph&acirc;n giải Full HD+ (1080 x 2400 pixels) tr&ecirc;n m&agrave;n h&igrave;nh c&oacute; k&iacute;ch thước 6.36 inch gi&uacute;p n&acirc;ng tổng số mật độ điểm ảnh, điều n&agrave;y đem đến chất lượng h&igrave;nh ảnh c&oacute; độ chi tiết cao hơn để mọi nội dung bạn xem đều được trở n&ecirc;n sắc n&eacute;t.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/267984/xiaomi-13-4.jpg\"><img class=\" lazyloaded\" title=\"Trang bị m&agrave;n h&igrave;nh chất lượng - Xiaomi 13\" src=\"https://cdn.tgdd.vn/Products/Images/42/267984/xiaomi-13-4.jpg\" alt=\"Trang bị m&agrave;n h&igrave;nh chất lượng - Xiaomi 13\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/267984/xiaomi-13-4.jpg\" /></a></p>\r\n<h3>R&uacute;t ngắn thời gian nạp đầy năng lượng với c&ocirc;ng suất sạc 67 W</h3>\r\n<p>Đối với Xiaomi 13 th&igrave; m&aacute;y được trang bị một vi&ecirc;n pin c&oacute; dung lượng 4500 mAh với thời lượng d&ugrave;ng mang lại vừa đủ cho một ng&agrave;y d&agrave;i sử dụng đa t&aacute;c vụ, ngo&agrave;i ra đ&acirc;y c&ograve;n l&agrave; mẫu&nbsp;<a title=\"Tham khảo điện thoại sạc si&ecirc;u nhanh đang kinh doanh tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd-sac-sieu-nhanh\" target=\"_blank\" rel=\"noopener\">điện thoại sạc nhanh</a>&nbsp;c&oacute; c&ocirc;ng suất 67 W n&ecirc;n mọi vấn đề về thời gian sạc sẽ kh&ocirc;ng c&ograve;n ảnh hưởng qu&aacute; lớn l&agrave;m gi&aacute;n đoạn trải nghiệm của bạn.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/267984/xiaomi-13-5.jpg\"><img class=\" lazyloaded\" title=\"Hỗ trợ sạc si&ecirc;u nhanh 67W - Xiaomi 13\" src=\"https://cdn.tgdd.vn/Products/Images/42/267984/xiaomi-13-5.jpg\" alt=\"Hỗ trợ sạc si&ecirc;u nhanh 67W - Xiaomi 13\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/267984/xiaomi-13-5.jpg\" /></a></p>\r\n<h3>So s&aacute;nh th&ocirc;ng số kỹ thuật giữa Xiaomi 13 v&agrave; Xiaomi 13 Pro</h3>\r\n<p>Dưới đ&acirc;y l&agrave; bảng tổng hợp c&aacute;c danh mục về th&ocirc;ng số kỹ thuật của bộ đ&ocirc;i Xiaomi 13 series vừa được c&ocirc;ng bố:</p>\r\n<table border=\"1\" cellspacing=\"1\" cellpadding=\"1\">\r\n<tbody>\r\n<tr>\r\n<td><strong>Thuộc t&iacute;nh</strong></td>\r\n<td><strong>Xiaomi 13</strong></td>\r\n<td><strong>Xiaomi 13 Pro</strong></td>\r\n</tr>\r\n<tr>\r\n<td>M&agrave;n h&igrave;nh</td>\r\n<td>AMOLED 6.36 inch</td>\r\n<td>AMOLED 6.73 inch</td>\r\n</tr>\r\n<tr>\r\n<td>Chip xử l&yacute;</td>\r\n<td>Snapdragon 8 Gen 2 8 nh&acirc;n</td>\r\n<td>Snapdragon 8 Gen 2 8 nh&acirc;n</td>\r\n</tr>\r\n<tr>\r\n<td>Camera sau</td>\r\n<td>Ch&iacute;nh 50 MP &amp; Phụ 12 MP, 10 MP</td>\r\n<td>Ch&iacute;nh 50 MP &amp; Phụ 50 MP, 50 MP</td>\r\n</tr>\r\n<tr>\r\n<td>Camera trước</td>\r\n<td>32 MP</td>\r\n<td>32 MP</td>\r\n</tr>\r\n<tr>\r\n<td>RAM&nbsp;</td>\r\n<td>8 GB</td>\r\n<td>12 GB</td>\r\n</tr>\r\n<tr>\r\n<td>Dung lượng pin</td>\r\n<td>4500 mAh v&agrave; sạc si&ecirc;u nhanh 67 W</td>\r\n<td>4820 mAh v&agrave; sạc si&ecirc;u nhanh 120 W</td>\r\n</tr>\r\n<tr>\r\n<td>M&agrave;u sắc</td>\r\n<td>Xanh v&agrave; đen</td>\r\n<td>Trắng v&agrave; đen</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h3>Video đ&aacute;nh gi&aacute; chi tiết Xiaomi 13 tại Thế Giới Di Động</h3>\r\n<p>Để biết th&ecirc;m th&ocirc;ng tin chi tiết về những trải nghiệm thực tế tr&ecirc;n chiếc Xiaomi 13 th&igrave; người d&ugrave;ng c&oacute; thể tham khảo video đ&aacute;nh gi&aacute; tại đ&acirc;y:</p>\r\n<p>&nbsp;</p>\r\n<div class=\"article__content__video-container\"><iframe id=\"411267088\" title=\"Chi tiết Xiaomi 13 5G: Ngon hơn iPhone, Camera LEICA 10đ, Snap 8 gen 2 c&ograve;n N&Oacute;NG | Thế Giới Di Động\" src=\"https://www.youtube.com/embed/txbDxX_0q0I?rel=0&amp;enablejsapi=1&amp;origin=https%3A%2F%2Fwww.thegioididong.com\" width=\"560\" height=\"315\" frameborder=\"0\" allowfullscreen=\"allowfullscreen\" data-gtm-yt-inspected-1070012_61=\"true\" data-gtm-yt-inspected-1070012_79=\"true\" data-gtm-yt-inspected-1070012_187=\"true\" data-gtm-yt-inspected-10=\"true\" data-mce-fragment=\"1\"></iframe></div>\r\n<p>&nbsp;</p>\r\n<h3>Một số c&acirc;u hỏi thường gặp trước khi chọn mua Xiaomi 13?</h3>\r\n<p><strong>Xiaomi 13 c&oacute; được sự hỗ trợ tinh chỉnh ống k&iacute;nh đến từ Leica?</strong></p>\r\n<p>Đối với bộ đ&ocirc;i sản phẩm Xiaomi 13 v&agrave;&nbsp;<a title=\"Tham khảo điện thoại Xiaomi  13 Pro tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd/xiaomi-13-pro\" target=\"_blank\" rel=\"noopener\">Xiaomi 13 Pro</a>&nbsp;lần n&agrave;y th&igrave; cả hai đều được nh&agrave; Leica tham gia sản xuất, tinh chỉnh phần cụm camera v&agrave; phần mềm hỗ trợ chụp ảnh. Điều n&agrave;y đem tới một thiết bị với h&agrave;ng loạt c&aacute;c t&iacute;nh năng chụp c&ugrave;ng bộ lọc m&agrave;u hết sức đa dạng đến từ Leica, rất th&iacute;ch hợp cho những bạn đam m&ecirc; chụp ảnh tr&ecirc;n một chiếc điện thoại.</p>\r\n<p><strong>Xiaomi 13 hỗ trợ t&iacute;ch hợp mấy khe SIM?</strong></p>\r\n<p>Ở Xiaomi 13 m&aacute;y được trang bị 2 khe thẻ lắp nano SIM, phần n&agrave;y sẽ kh&ocirc;ng được hỗ trợ th&ecirc;m khe gắn thẻ Micro SD n&ecirc;n m&aacute;y chỉ sở hữu phần dung lượng bộ nhớ tối đa bằng với con số mặc định m&agrave; h&atilde;ng đ&atilde; cung cấp l&agrave; 256 GB.</p>\r\n<p><strong>Điểm kh&aacute;c biệt về bề ngo&agrave;i giữa Xiaomi 13 v&agrave; Xiaomi 13 Pro?</strong></p>\r\n<p>Thoạt nh&igrave;n phần lớn c&aacute;c bạn sẽ nghĩ rằng đ&acirc;y l&agrave; hai phi&ecirc;n bản c&oacute; thiết kế tương đồng c&ugrave;ng với chất liệu ho&agrave;n thiện y chang nhau, thế nhưng đ&acirc;y l&agrave; điều ho&agrave;n to&agrave;n sai khi Xiaomi 13 sở hữu một vẻ ngo&agrave;i vu&ocirc;ng vức c&ograve;n Xiaomi 13 Pro th&igrave; lại bo cong mềm mại ở mặt lưng v&agrave; m&agrave;n h&igrave;nh. Ở Xiaomi 13 th&igrave; mặt lưng được l&agrave;m từ k&iacute;nh c&ograve;n Xiaomi 13 Pro sẽ l&agrave; vật liệu gốm cao cấp.</p>\r\n<p>Với những ai l&agrave; người h&acirc;m mộ của nh&agrave; Xiaomi hay h&atilde;ng m&aacute;y ảnh Leica th&igrave; Xiaomi 13 chắc hẳn l&agrave; c&aacute;i t&ecirc;n kh&ocirc;ng thể bỏ qua ở thời điểm hiện tại, đ&acirc;y rất c&oacute; thể l&agrave; mẫu điện thoại đột ph&aacute; trong năm 2023 ở mọi nhu cầu sử dụng từ chơi game cho đến chụp ảnh chuy&ecirc;n nghiệp.</p>', 100, 0, '<p class=\"lileft\">M&agrave;n h&igrave;nh:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span class=\"comma\">AMOLED</span><span class=\"comma\">6.36\"</span><span class=\"\">Full HD+</span></p>\r\n<p class=\"lileft\">Hệ điều h&agrave;nh:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span class=\"\">Android 13</span></p>\r\n<p class=\"lileft\">Camera sau:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span class=\"\">Ch&iacute;nh 50 MP &amp; Phụ 12 MP, 10 MP</span></p>\r\n<p class=\"lileft\">Camera trước:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span class=\"\">32 MP</span></p>\r\n<p class=\"lileft\">Chip:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span class=\"\">Snapdragon 8 Gen 2 8 nh&acirc;n</span></p>\r\n<p class=\"lileft\">RAM:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span class=\"\">8 GB</span></p>\r\n<p class=\"lileft\">Dung lượng lưu trữ:&nbsp; &nbsp;<span class=\"\">256 GB</span></p>\r\n<p class=\"lileft\">SIM:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span class=\"comma\">2 Nano SIM</span><span class=\"\">Hỗ trợ 5G</span></p>\r\n<p class=\"lileft\">Pin, Sạc:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span class=\"comma\">4500 mAh</span><span class=\"\">67 W</span></p>', 0, 0, '2023-04-11 18:06:20', '2023-04-11 18:08:02', 1),
(26, 'Xiaomi 12T 5G 128GB', 'xiaomi-12t-5g-128gb', 9990000, 0, 0, 'xiaomi12t.jpg', 10, '<h3>Xiaomi 12T series đã ra mắt trong sự kiện của Xiaomi vào ngày 4/10, trong đó có Xiaomi 12T 5G 128GB - máy sở hữu nhiều công nghệ hàng đầu trong giới smartphone tiêu biểu như: Chipset mạnh mẽ đến từ MediaTek, camera 108 MP sắc nét cùng khả năng sạc 120 W siêu nhanh.</h3>\r\n<h3>Ấn tượng với diện mạo sang trọng</h3>\r\n<p><a title=\"Tham khảo điện thoại Xiaomi 12T tại Thế Giới Di Động \" href=\"https://www.thegioididong.com/dtdd/xiaomi-12t\" target=\"_blank\" rel=\"noopener\">Xiaomi 12T</a> có thiết kế khá tương đồng với thế hệ tiền nhiệm, mặt lưng được làm bo cong ở hai cạnh kèm theo một dòng chữ “Xiaomi” bố trí ở góc dưới phần thân máy.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/279065/xiaomi-12t-051022-094839.jpg\"><img class=\" lazyloaded\" title=\"Thiết kế sang trọng - Xiaomi 12T 128GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/279065/xiaomi-12t-051022-094839.jpg\" alt=\"Thiết kế sang trọng - Xiaomi 12T 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/279065/xiaomi-12t-051022-094839.jpg\" /></a></p>\r\n<p>Một thay đổi rõ rệt để người dùng có thể nhận biết được chiếc Xiaomi 12T nằm ở phần cụm camera, ống kính chính sẽ không được làm hình tròn như trên phiên bản <a title=\"Tham khảo điện thoại Xiaomi 12 tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd/xiaomi-mi-12\" target=\"_blank\" rel=\"noopener\">Xiaomi 12</a> mà giờ đây hãng đã thiết kế nó dưới dạng một ô vuông trông khá là cứng cáp.</p>\r\n<h3>Đáp ứng tốt mọi nhu cầu giải trí nhờ màn hình chất lượng</h3>\r\n<p>Về phần màn hình thì có thể Xiaomi 12T sẽ được trang bị một tấm nền AMOLED có kích thước 6.67 inch và độ phân giải nằm ở mức 1220 x 2712 Pixels. Là một chiếc điện thoại flagship nên việc trang bị một màn hình có tần số quét 120 Hz là điều hiển nhiên trên chiếc Xiaomi 12T.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/279065/xiaomi-12t-051022-094841.jpg\"><img class=\" lazyloaded\" title=\"Màn hình chất lượng - Xiaomi 12T 128GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/279065/xiaomi-12t-051022-094841.jpg\" alt=\"Màn hình chất lượng - Xiaomi 12T 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/279065/xiaomi-12t-051022-094841.jpg\" /></a></p>\r\n<p>Ở Xiaomi 12T thì ta có thể dễ dàng nhận thấy phần màn hình của máy sẽ không còn làm bo cong như thế hệ trước, có lẽ đây là cách mà nhà Xiaomi đã tối ưu đi để cắt giảm quy trình sản xuất nhằm mang đến một mức giá bán phù hợp hơn đối với người dùng <a href=\"https://www.thegioididong.com/dtdd?g=android\" target=\"_blank\" rel=\"noopener\">điện thoại Android</a>.</p>\r\n<h3>Thỏa sức sáng tạo nội dung hình ảnh với camera đầy chất lượng</h3>\r\n<p>Ở phiên bản Xiaomi 12 trước đó với camera chính có độ phân giải tuy chỉ nằm ở mức 50 MP nhưng lại mang đến chất lượng ảnh chụp cực kỳ sắc nét, còn đối với phiên bản Xiaomi 12T lần này sẽ được nâng cấp độ phân giải lên đến 108 MP giúp cho chiếc máy mang lại sự đột phá.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/279065/xiaomi-12t-051022-094842.jpg\"><img class=\" lazyloaded\" title=\"Hỗ trợ chụp ảnh sắc nét - Xiaomi 12T 128GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/279065/xiaomi-12t-051022-094842.jpg\" alt=\"Hỗ trợ chụp ảnh sắc nét - Xiaomi 12T 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/279065/xiaomi-12t-051022-094842.jpg\" /></a></p>\r\n<h3>Hiệu năng cực khủng đến từ chipset nhà MediaTek</h3>\r\n<p>Lần ra mắt sản phẩm sắp tới đây của nhà Xiaomi không chỉ giới thiệu đến cho người dùng một siêu phẩm smartphone, mà đây cũng là một dịp để ta có thể chiêm ngưỡng được sức mạnh cực khủng trên con chip Dimensity 8100 Ultra mới nhất đến từ nhà MediaTek.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/279065/xiaomi-12t-051022-094844.jpg\"><img class=\" lazyloaded\" title=\"Hiệu năng mạnh mẽ - Xiaomi 12T 128GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/279065/xiaomi-12t-051022-094844.jpg\" alt=\"Hiệu năng mạnh mẽ - Xiaomi 12T 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/279065/xiaomi-12t-051022-094844.jpg\" /></a></p>\r\n<h3>Hỗ trợ sạc siêu tốc</h3>\r\n<p>Ở thế hệ <a href=\"https://www.thegioididong.com/dtdd-xiaomi-mi\" target=\"_blank\" rel=\"noopener\">điện thoại Xiaomi dòng Mi</a> trước đó cụ thể như chiếc 11T Pro, khi được ra mắt vào tháng 10 năm ngoái thì máy đã khiến đông đảo các tín đồ công nghệ tại thời điểm đó không khỏi ngỡ ngàng trước sự nâng cấp đáng kể về công suất sạc lên tới 120 W.</p>\r\n<p>Và hiển nhiên trên phiên bản dòng T mới nhất này vẫn được tích hợp công nghệ sạc pin siêu tốc, Xiaomi 12T quả là một chiếc điện thoại cực kỳ đáng sắm dành cho các tín đồ công nghệ thường xuyên chơi game hay làm việc trên <a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" rel=\"noopener\">điện thoại</a>, chỉ cần mất một khoảng thời gian ngắn là máy đã có thể sạc đầy được viên pin.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/279065/xiaomi-12t-051022-094846.jpg\"><img class=\" lazyloaded\" title=\"Sạc pin nhanh chóng - Xiaomi 12T 128GB\" src=\"https://cdn.tgdd.vn/Products/Images/42/279065/xiaomi-12t-051022-094846.jpg\" alt=\"Sạc pin nhanh chóng - Xiaomi 12T 128GB\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/279065/xiaomi-12t-051022-094846.jpg\" /></a></p>\r\n<p>Trong buổi ra mắt này, có thêm phiên bản <a title=\"Tham khảo một số mẫu điện thoại Xiaomi 12T Pro 5G đang kinh doanh tại Thế Giới Di Động \" href=\"https://www.thegioididong.com/dtdd/xiaomi-12t-pro\" target=\"_blank\" rel=\"noopener\">Xiaomi 12T Pro 5G</a>, với cấu hình nổi bật không kém như camera chính có độ phân giải đến 200 MP tích hợp OIS, sử dụng CPU Snapdragon 8+ Gen 1 cùng viên pin 5000 mAh và công nghệ sạc nhanh 120 W.</p>\r\n<p>Là một người dùng đam mê công nghệ hay đang mong muốn tìm mua cho mình một chiếc điện thoại ưng ý để phục vụ tốt cho mọi tác vụ, Xiaomi 12T cũng như Xiaomi 12T Pro chắc hẳn là cái tên mà bạn không nên bỏ qua.</p>', 99, 0, '<p class=\"lileft\">Màn hình:                      <span class=\"comma\">AMOLED</span><span class=\"comma\">6.67\"</span><span class=\"\">1.5K</span></p>\r\n<p class=\"lileft\">Hệ điều hành:               <span class=\"\">Android 12</span></p>\r\n<p class=\"lileft\">Camera sau:                 <span class=\"\">Chính 108 MP &amp; Phụ 8 MP, 2 MP</span></p>\r\n<p class=\"lileft\">Camera trước:              <span class=\"\">20 MP</span></p>\r\n<p class=\"lileft\">Chip:                             <span class=\"\">MediaTek Dimensity 8100 Ultra 8 nhân</span></p>\r\n<p class=\"lileft\">RAM:                             <span class=\"\">8 GB</span></p>\r\n<p class=\"lileft\">Dung lượng lưu trữ:     <span class=\"\">128 GB</span></p>\r\n<p class=\"lileft\">SIM:                              <span class=\"comma\">2 Nano SIM</span><span class=\"\">Hỗ trợ 5G</span></p>\r\n<p class=\"lileft\">Pin, Sạc:                       <span class=\"comma\">5000 mAh</span><span class=\"\">120 W</span></p>', 0, 0, '2023-04-11 18:12:01', '2023-04-11 18:13:50', 1),
(27, 'Xiaomi Redmi Note 11 Pro', 'xiaomi-redmi-note-11-pro', 5790000, 0, 0, 'xiaominote11.jpg', 10, '<h3><a title=\"Tham khảo th&ocirc;ng tin sản phẩm tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd/xiaomi-redmi-note-11-pro-4g\" target=\"_blank\" rel=\"noopener\">Xiaomi Redmi Note 11 Pro 4G</a>&nbsp;mang trong m&igrave;nh kh&aacute; nhiều những n&acirc;ng cấp cực k&igrave; s&aacute;ng gi&aacute;. L&agrave; chiếc&nbsp;<a title=\"Tham khảo điện thoại kinh doanh tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" rel=\"noopener\">điện thoại</a>&nbsp;c&oacute; m&agrave;n h&igrave;nh lớn, tần số qu&eacute;t 120 Hz, hiệu năng ổn định c&ugrave;ng một vi&ecirc;n pin si&ecirc;u tr&acirc;u.</h3>\r\n<h3>Thiết kế cứng c&aacute;p, cầm nắm rất đầm tay</h3>\r\n<p>Điểm nổi bật ở phần thiết kế của Redmi Note 11 Pro ch&iacute;nh l&agrave; cụm camera kh&aacute; lớn v&agrave; lồi so với mặt lưng, mặt lưng c&oacute; chất liệu bằng k&iacute;nh đ&atilde; được l&agrave;m phẳng đi. Khung viền bằng nhựa cũng được bo tr&ograve;n v&agrave; v&aacute;t phẳng rất liền mạch, mức độ ho&agrave;n thiện tốt, kh&ocirc;ng c&oacute; hiện tượng ọp ẹp khi m&igrave;nh sử dụng chiếc m&aacute;y n&agrave;y..</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/270471/xiaomi-redmi-note-11-pro-4g-2-1.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Khung viền phẳng - Xiaomi Redmi Note 11 Pro 4G\" src=\"https://cdn.tgdd.vn/Products/Images/42/270471/xiaomi-redmi-note-11-pro-4g-2-1.jpg\" alt=\"Khung viền phẳng - Xiaomi Redmi Note 11 Pro 4G\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/270471/xiaomi-redmi-note-11-pro-4g-2-1.jpg\" /></a></p>\r\n<p>Mặt trước ch&uacute;ng ta sẽ c&oacute; một camera selfie dạng đục lỗ đặt ở giữa phần m&agrave;n h&igrave;nh. Cạnh viền c&oacute; ph&iacute;m tăng giảm &acirc;m lượng, ph&iacute;m nguồn t&iacute;ch hợp cảm biến v&acirc;n tay cho tốc độ nhận diện nhanh ch&oacute;ng v&agrave; được đặt ở vị tr&iacute; kh&aacute; dễ thao t&aacute;c.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/270471/xiaomi-redmi-note-11-pro-4g-15.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Cạnh viền - Xiaomi Redmi Note 11 Pro 4G\" src=\"https://cdn.tgdd.vn/Products/Images/42/270471/xiaomi-redmi-note-11-pro-4g-15.jpg\" alt=\"Cạnh viền - Xiaomi Redmi Note 11 Pro 4G\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/270471/xiaomi-redmi-note-11-pro-4g-15.jpg\" /></a></p>\r\n<p>Cạnh dưới được trang bị một d&atilde;y loa, micro, khe sim c&ugrave;ng với cổng sạc Type C thường thấy tr&ecirc;n c&aacute;c sản phẩm hiện nay.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/270471/xiaomi-redmi-note-11-pro-4g-4-1.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Cạnh dưới - Xiaomi Redmi Note 11 Pro 4G\" src=\"https://cdn.tgdd.vn/Products/Images/42/270471/xiaomi-redmi-note-11-pro-4g-4-1.jpg\" alt=\"Cạnh dưới - Xiaomi Redmi Note 11 Pro 4G\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/270471/xiaomi-redmi-note-11-pro-4g-4-1.jpg\" /></a></p>\r\n<h3>Hiệu năng ổn định cho mọi t&aacute;c vụ</h3>\r\n<p><a title=\"Tham khảo điện thoại Xiaomi kinh doanh tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd-xiaomi\" target=\"_blank\" rel=\"noopener\">Xiaomi</a>&nbsp;trang bị cho Note 11 Pro bộ vi xử l&yacute;&nbsp;<a title=\"Tham khảo th&ocirc;ng tin CPU G96\" href=\"https://www.thegioididong.com/tin-tuc/mediatek-ra-mat-helio-g96-helio-g88-1368559\" target=\"_blank\" rel=\"noopener\">Mediatek Helio G96 8 nh&acirc;n</a>&nbsp;mạnh mẽ. Đ&acirc;y l&agrave; con chip thuộc d&ograve;ng G chuy&ecirc;n Game n&ecirc;n c&oacute; thể gi&uacute;p cho ch&uacute;ng ta c&oacute; thể xử l&yacute; trơn tru hầu hết c&aacute;c t&aacute;c vụ cơ bản h&agrave;ng ng&agrave;y cũng như l&agrave; một số tựa game nhẹ nh&agrave;ng cho đến những game y&ecirc;u cầu đồ họa.</p>\r\n<p>M&igrave;nh c&oacute; kiểm tra bằng phần mềm đo hiệu năng Benchmark (b&ecirc;n tr&aacute;i) v&agrave; c&oacute; kết quả điểm đơn nh&acirc;n 495, điểm đa nh&acirc;n 1640, PCMark (b&ecirc;n phải) đạt 9125 điểm.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/270471/xiaomi-redmi-note-11-pro-4g-5-1.jpg\"><img class=\" lazyloaded\" title=\"Đo sức mạnh bằng phần mềm - Xiaomi Redmi Note 11 Pro 4G\" src=\"https://cdn.tgdd.vn/Products/Images/42/270471/xiaomi-redmi-note-11-pro-4g-5-1.jpg\" alt=\"Đo sức mạnh bằng phần mềm - Xiaomi Redmi Note 11 Pro 4G\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/270471/xiaomi-redmi-note-11-pro-4g-5-1.jpg\" /></a></p>\r\n<p><a title=\"Tham khảo điện thoại c&oacute; 8 GB RAM kinh doanh tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd-xiaomi\" target=\"_blank\" rel=\"noopener\">RAM 8 GB</a>&nbsp;l&agrave; một con số ổn trong thời điểm hiện nay, m&aacute;y c&oacute; thể đa nhiệm ổn, &iacute;t xảy ra hiện tượng giật lag khi m&igrave;nh bật nhiều ứng dụng.</p>\r\n<p>M&aacute;y cũng được trang bị c&ocirc;ng nghệ tản nhiệt LiquidCool đa chiều mới, gi&uacute;p tản nhiệt một c&aacute;ch kh&aacute; hiệu quả. M&igrave;nh c&oacute; trải nghiệm thực tế khi m&igrave;nh chơi 2 tựa game quen thuộc l&agrave; Li&ecirc;n Qu&acirc;n v&agrave; PUBG Mobile v&agrave; c&oacute; một số th&ocirc;ng tin sau:</p>\r\n<p>Với Li&ecirc;n Qu&acirc;n, m&aacute;y c&oacute; phần đồ họa mượt, ổn định. Từ kh&acirc;u chọn tướng cho đến l&uacute;c v&agrave;o trận m&aacute;y &iacute;t xảy ra hiện tượng giật lag. L&uacute;c bắt đầu combat m&aacute;y kh&ocirc;ng xảy ra hiện tượng drop FPS v&agrave; lu&ocirc;n c&oacute; mức FPS ổn định l&agrave; 60. Sau khi chơi tầm 15 ph&uacute;t m&aacute;y sẽ kh&aacute; n&oacute;ng ở phần mặt lưng, gần cụm camera.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/270471/xiaomi-redmi-note-11-pro-4g-6-1.jpg\"><img class=\" lazyloaded\" title=\"Li&ecirc;n Qu&acirc;n Mobile - Xiaomi Redmi Note 11 Pro 4G\" src=\"https://cdn.tgdd.vn/Products/Images/42/270471/xiaomi-redmi-note-11-pro-4g-6-1.jpg\" alt=\"Li&ecirc;n Qu&acirc;n Mobile - Xiaomi Redmi Note 11 Pro 4G\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/270471/xiaomi-redmi-note-11-pro-4g-6-1.jpg\" /></a></p>\r\n<p>C&ograve;n về PUBG Mobile, khi m&igrave;nh bật mức đồ họa HD tốc độ khung h&igrave;nh cao, m&aacute;y cho ra một c&aacute;i trải nghiệm chưa ổn. Khung h&igrave;nh c&ograve;n kh&aacute; l&agrave; giật lag ở mức 30 FPS c&oacute; t&igrave;nh trạng bị tụt xuống mức 26, 27 FPS, di chuyển nh&acirc;n vật hay thao t&aacute;c nhặt đồ c&ograve;n kh&aacute; kh&oacute;, l&uacute;c giao tranh chưa chuẩn x&aacute;c cho lắm.</p>\r\n<p>Khi m&igrave;nh chuyển qua mức đồ họa C&acirc;n bằng với tốc độ khung h&igrave;nh Cực cao m&aacute;y hầu như giải quyết được c&aacute;c vấn đề tr&ecirc;n. Đồ hoạ ổn định, v&agrave;o đấu s&uacute;ng thao t&aacute;c kh&aacute; mượt, di chuyển hay đổi s&uacute;ng nhặt đồ kh&ocirc;ng c&ograve;n giật lag v&agrave; ổn định ở mức 40 FPS.</p>\r\n<p>Xiaomi Redmi Note 11 Pro c&oacute; một chiếc m&agrave;n h&igrave;nh k&iacute;ch thước kh&aacute; lớn l&ecirc;n đến 6.67 inch, sử dụng tấm nền AMOLED cho ra chất lượng hiển thị ổn, m&agrave;u sắc tươi v&agrave; độ chi tiết cao với độ ph&acirc;n giải Full HD+, độ tương phản tương đối cao v&agrave; nịnh mắt.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/270471/xiaomi-redmi-note-11-pro-4g-8-1.jpg\"><img class=\" lazyloaded\" title=\"M&agrave;n h&igrave;nh rực rỡ - Xiaomi Redmi Note 11 Pro 4G\" src=\"https://cdn.tgdd.vn/Products/Images/42/270471/xiaomi-redmi-note-11-pro-4g-8-1.jpg\" alt=\"M&agrave;n h&igrave;nh rực rỡ - Xiaomi Redmi Note 11 Pro 4G\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/270471/xiaomi-redmi-note-11-pro-4g-8-1.jpg\" /></a></p>\r\n<p>K&iacute;ch thước m&agrave;n h&igrave;nh lớn kết hợp với phần viền m&agrave;n h&igrave;nh được l&agrave;m mỏng hơn mang lại kh&ocirc;ng gian hiển thị rộng r&atilde;i, hỗ trợ c&ocirc;ng việc cũng như giải tr&iacute; sẽ tốt hơn, hiển thị một l&uacute;c nhiều th&ocirc;ng tin hơn.&nbsp;</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/270471/xiaomi-redmi-note-11-pro-4g-9-1.jpg\"><img class=\" lazyloaded\" title=\"Viền m&agrave;n h&igrave;nh mỏng - Xiaomi Redmi Note 11 Pro 4G\" src=\"https://cdn.tgdd.vn/Products/Images/42/270471/xiaomi-redmi-note-11-pro-4g-9-1.jpg\" alt=\"Viền m&agrave;n h&igrave;nh mỏng - Xiaomi Redmi Note 11 Pro 4G\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/270471/xiaomi-redmi-note-11-pro-4g-9-1.jpg\" /></a></p>\r\n<p>Ngo&agrave;i ra, Redmi Note 11 Pro c&ograve;n hỗ trợ dải m&agrave;u rộng DCI-P3, chế độ bảo vệ mắt, trải nghiệm giải tr&iacute; chơi game lướt web đều mượt m&agrave; nhờ v&agrave;o việc m&aacute;y được trang bị tần số qu&eacute;t 120 Hz cũng l&agrave; một điểm cộng lớn tr&ecirc;n phi&ecirc;n bản lần n&agrave;y.</p>\r\n<h3>Camera AI đến 108 MP</h3>\r\n<p>Camera tr&ecirc;n Redmi Note 11 Pro cũng kh&aacute; ấn tượng khi m&aacute;y được trang bị ở mặt sau cụm 4 camera AI, cảm biến ch&iacute;nh 108 MP, camera g&oacute;c si&ecirc;u rộng 8 MP, camera đo chiều s&acirc;u 2 MP v&agrave; camera macro 2 MP.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/270471/xiaomi-redmi-note-11-pro-4g-10-1.jpg\"><img class=\" lazyloaded\" title=\"Camera đến 108 MP - Xiaomi Redmi Note 11 Pro 4G\" src=\"https://cdn.tgdd.vn/Products/Images/42/270471/xiaomi-redmi-note-11-pro-4g-10-1.jpg\" alt=\"Camera đến 108 MP - Xiaomi Redmi Note 11 Pro 4G\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/270471/xiaomi-redmi-note-11-pro-4g-10-1.jpg\" /></a></p>\r\n<p>Với camera 108 MP trong điều kiện đủ s&aacute;ng m&aacute;y cho ra những bức ảnh v&ocirc; c&ugrave;ng l&agrave; chi tiết, m&agrave;u sắc tươi, t&aacute;i hiện lại gần như l&agrave; ho&agrave;n hảo v&agrave; đầy đủ chi tiết, kh&ocirc;ng bị bệt m&agrave;u.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/270471/xiaomi-redmi-note-11-pro-4g-13-1.jpg\"><img class=\" lazyloaded\" title=\"Ảnh chụp từ camera ch&iacute;nh - Xiaomi Redmi Note 11 Pro 4G\" src=\"https://cdn.tgdd.vn/Products/Images/42/270471/xiaomi-redmi-note-11-pro-4g-13-1.jpg\" alt=\"Ảnh chụp từ camera ch&iacute;nh - Xiaomi Redmi Note 11 Pro 4G\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/270471/xiaomi-redmi-note-11-pro-4g-13-1.jpg\" /></a></p>\r\n<p>Những bức ảnh trong điều kiện chụp đ&ecirc;m hay l&agrave; chụp thiếu s&aacute;ng th&igrave; chất lượng ảnh cho ra cũng đ&atilde; được cải thiện hơn kh&aacute; nhiều, chi tiết được t&aacute;i hiện gần như l&agrave; đầy đủ v&agrave; những phần qu&aacute; tối sẽ được n&acirc;ng s&aacute;ng hơn để gi&uacute;p cho tổng thể bức ảnh trở n&ecirc;n h&agrave;i h&ograve;a hơn v&agrave; hạn chế được t&igrave;nh trạng bị nhiễu ảnh.</p>\r\n<p>C&ograve;n về phần chụp ch&acirc;n dung m&aacute;y cũng l&agrave;m kh&aacute; tốt khi phần chủ thể gần như l&agrave; được t&aacute;ch kh&ocirc;ng bị lem v&agrave; gi&uacute;p cho phần chủ thể của bức ảnh tr&ocirc;ng sẽ nổi bật hơn rất l&agrave; nhiều.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/270471/xiaomi-redmi-note-11-pro-4g-14.jpg\"><img class=\" lazyloaded\" title=\"Ảnh chụp x&oacute;a ph&ocirc;ng - Xiaomi Redmi Note 11 Pro 4G\" src=\"https://cdn.tgdd.vn/Products/Images/42/270471/xiaomi-redmi-note-11-pro-4g-14.jpg\" alt=\"Ảnh chụp x&oacute;a ph&ocirc;ng - Xiaomi Redmi Note 11 Pro 4G\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/270471/xiaomi-redmi-note-11-pro-4g-14.jpg\" /></a></p>\r\n<p>Ở mặt trước c&oacute; một chiếc camera selfie dạng đục lỗ độ ph&acirc;n giải 16 MP gi&uacute;p cho ra những bức ảnh chụp kh&aacute; l&agrave; chi tiết, da chủ thể trở n&ecirc;n hồng h&agrave;o v&agrave; tươi tắn hơn kh&aacute; nhiều.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/270471/xiaomi-redmi-note-11-pro-4g-12-1.jpg\"><img class=\" lazyloaded\" title=\"Ảnh chụp selfie - Xiaomi Redmi Note 11 Pro 4G\" src=\"https://cdn.tgdd.vn/Products/Images/42/270471/xiaomi-redmi-note-11-pro-4g-12-1.jpg\" alt=\"Ảnh chụp selfie - Xiaomi Redmi Note 11 Pro 4G\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/270471/xiaomi-redmi-note-11-pro-4g-12-1.jpg\" /></a></p>\r\n<h3>Pin lớn, sạc si&ecirc;u nhanh</h3>\r\n<p>Xiaomi Redmi Note 11 Pro 4G trang bị một dung lượng pin kh&aacute; l&agrave; tr&acirc;u l&agrave; 5000 mAh c&ugrave;ng với c&ocirc;ng suất sạc đến 67 W. M&igrave;nh c&oacute; sử dụng qua những c&aacute;i t&aacute;c vụ chơi game, lướt web, xem YouTube hay những c&aacute;i t&aacute;c vụ h&agrave;ng ng&agrave;y kh&aacute;c một c&aacute;ch xen kẽ nhau th&igrave; m&aacute;y c&oacute; thời gian sử dụng l&agrave; 9 tiếng 43 ph&uacute;t.*</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/270471/xiaomi-redmi-note-11-pro-4g-11-1.jpg\"><img class=\" lazyloaded\" title=\"Thời gian sử dụng Pin - Xiaomi Redmi Note 11 Pro 4G\" src=\"https://cdn.tgdd.vn/Products/Images/42/270471/xiaomi-redmi-note-11-pro-4g-11-1.jpg\" alt=\"Thời gian sử dụng Pin - Xiaomi Redmi Note 11 Pro 4G\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/270471/xiaomi-redmi-note-11-pro-4g-11-1.jpg\" /></a></p>\r\n<p>Với c&ocirc;ng suất sạc cao như vậy th&igrave; khi m&agrave; m&igrave;nh sạc từ 0 - 100% cũng chỉ mất khoảng 55 ph&uacute;t*. Điều n&agrave;y gi&uacute;p cho những hoạt động giải tr&iacute; cũng như l&agrave; l&agrave;m việc, học tập với chiếc Redmi Note 11 Pro trong 1 ng&agrave;y sẽ kh&ocirc;ng bị gi&aacute;n đoạn.</p>\r\n<p>*Thời gian sử dụng c&oacute; thể ch&ecirc;nh lệch t&ugrave;y theo nhu cầu, t&aacute;c vụ sử dụng.</p>\r\n<p>Xiaomi Redmi Note 11 Pro với những c&aacute;i điểm s&aacute;ng như l&agrave; m&agrave;n h&igrave;nh lớn, chất lượng hiển thị sắc n&eacute;t, một thời lượng pin sử dụng l&acirc;u c&ugrave;ng với tốc độ sạc cực k&igrave; nhanh ch&oacute;ng, camera ổn, hiệu năng mượt m&agrave; th&igrave; m&igrave;nh nghĩ đ&acirc;y sẽ l&agrave; một ứng cử vi&ecirc;n kh&aacute; nặng k&yacute; trong ph&acirc;n kh&uacute;c tầm trung so với c&aacute;c đối thủ kh&aacute;c trong c&ugrave;ng tầm gi&aacute;.</p>', 57, 0, '<p class=\"lileft\">M&agrave;n h&igrave;nh:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span class=\"comma\">AMOLED</span><span class=\"comma\">6.67\"</span><span class=\"\">Full HD+</span></p>\r\n<p class=\"lileft\">Hệ điều h&agrave;nh:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span class=\"\">Android 11</span></p>\r\n<p class=\"lileft\">Camera sau:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span class=\"\">Ch&iacute;nh 108 MP &amp; Phụ 8 MP, 2 MP, 2 MP</span></p>\r\n<p class=\"lileft\">Camera trước:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span class=\"\">16 MP</span></p>\r\n<p class=\"lileft\">Chip:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span class=\"\">MediaTek Helio G96</span></p>\r\n<p class=\"lileft\">RAM:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span class=\"\">8 GB</span></p>\r\n<p class=\"lileft\">Dung lượng lưu trữ:&nbsp; &nbsp; &nbsp;<span class=\"\">128 GB</span></p>\r\n<p class=\"lileft\">SIM:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span class=\"comma\">2 Nano SIM (SIM 2 chung khe thẻ nhớ)</span><span class=\"\">Hỗ trợ 4G</span></p>\r\n<p class=\"lileft\">Pin, Sạc:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span class=\"comma\">5000 mAh</span><span class=\"\">67 W</span></p>', 0, 0, '2023-04-11 18:17:20', '2023-04-11 18:19:07', 1);
INSERT INTO `product` (`id`, `name`, `slug`, `price`, `sale`, `pay`, `thumbar`, `category_id`, `content`, `number`, `head`, `short_description`, `view`, `hot`, `created_at`, `updated_at`, `home`) VALUES
(28, 'Xiaomi Redmi Pad (4GB/128GB)', 'xiaomi-redmi-pad-4gb128gb', 6990000, 0, 0, 'RedmiPad.jpg', 10, '<h3><a title=\"Tham khảo một số mẫu m&aacute;y t&iacute;nh bảng Xiaomi Redmi Pad (4GB/128GB) đang kinh doanh tại Thế Giới Di Động \" href=\"https://www.thegioididong.com/may-tinh-bang/xiaomi-redmi-pad-4gb\" target=\"_blank\" rel=\"noopener\">Xiaomi Redmi Pad (4GB/128GB)</a>&nbsp;được xem l&agrave; c&aacute;i t&ecirc;n rất đ&aacute;ng mong đợi tr&ecirc;n thị trường m&aacute;y t&iacute;nh bảng gi&aacute; rẻ hiện nay, m&aacute;y được ra mắt v&agrave;o đầu th&aacute;ng 10/2022 với một mức gi&aacute; b&aacute;n kh&aacute; ấn tượng, đi k&egrave;m l&agrave; m&agrave;n h&igrave;nh k&iacute;ch thước lớn, hiệu năng ổn định c&ugrave;ng một vi&ecirc;n pin dung lượng khủng.</h3>\r\n<h3>Hiển thị chi tiết tr&ecirc;n một thiết kế đẹp mắt</h3>\r\n<p>Lần n&agrave;y Xiaomi mang đến cho người d&ugrave;ng mẫu m&aacute;y t&iacute;nh bảng c&oacute; vẻ ngo&agrave;i đẹp mắt với kiểu thiết kế phẳng thời thượng. M&aacute;y được ho&agrave;n thiện&nbsp;ho&agrave;n to&agrave;n&nbsp;từ kim loại nguy&ecirc;n khối, đồng thời khung phẳng v&agrave; c&aacute;c g&oacute;c bo tr&ograve;n gi&uacute;p Redmi Pad tạo n&ecirc;n sự trang nh&atilde; v&agrave; cầm nắm dễ chịu khi sử dụng.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/522/291769/xiaomi-redmi-pad-4gb-241122-114215.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Thiết kế đẹp mắt - Xiaomi Redmi Pad (4GB/128GB)\" src=\"https://cdn.tgdd.vn/Products/Images/522/291769/xiaomi-redmi-pad-4gb-241122-114215.jpg\" alt=\"Thiết kế đẹp mắt - Xiaomi Redmi Pad (4GB/128GB)\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/291769/xiaomi-redmi-pad-4gb-241122-114215.jpg\" /></a></p>\r\n<p>Redmi Pad được nh&agrave; Xiaomi trang bị một m&agrave;n h&igrave;nh IPS LCD k&iacute;ch thước 10.61 inch, quả thực l&agrave; th&ocirc;ng số rất ấn tượng về phần m&agrave;n h&igrave;nh tr&ecirc;n một chiếc tablet c&oacute; mức gi&aacute; th&agrave;nh kh&ocirc;ng qu&aacute; cao.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/522/291769/xiaomi-redmi-pad-4gb-241122-114217.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Giải tr&iacute; thoải m&aacute;i - Xiaomi Redmi Pad (4GB/128GB)\" src=\"https://cdn.tgdd.vn/Products/Images/522/291769/xiaomi-redmi-pad-4gb-241122-114217.jpg\" alt=\"Giải tr&iacute; thoải m&aacute;i - Xiaomi Redmi Pad (4GB/128GB)\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/291769/xiaomi-redmi-pad-4gb-241122-114217.jpg\" /></a></p>\r\n<p>Kh&ocirc;ng chỉ dừng lại ở việc sở hữu một m&agrave;n h&igrave;nh đẹp m&agrave; m&aacute;y c&ograve;n được trang bị tần số qu&eacute;t 90 Hz, đi c&ugrave;ng với độ ph&acirc;n giải 1200 x 2000 Pixels hứa hẹn sẽ cho bạn những ph&uacute;t gi&acirc;y thư gi&atilde;n tuyệt vời c&ugrave;ng gia đ&igrave;nh sau một ng&agrave;y l&agrave;m việc cũng như học tập căng thẳng.&nbsp;</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/522/291769/xiaomi-redmi-pad-4gb-241122-114218.jpg\"><img class=\" ls-is-cached lazyloaded\" title=\"Trải nghiệm m&agrave;n ảnh đẹp - Xiaomi Redmi Pad (4GB/128GB)\" src=\"https://cdn.tgdd.vn/Products/Images/522/291769/xiaomi-redmi-pad-4gb-241122-114218.jpg\" alt=\"Trải nghiệm m&agrave;n ảnh đẹp - Xiaomi Redmi Pad (4GB/128GB)\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/291769/xiaomi-redmi-pad-4gb-241122-114218.jpg\" /></a></p>\r\n<p>Xiaomi rất ch&uacute; trọng đến &acirc;m thanh tr&ecirc;n mỗi sản phẩm của m&igrave;nh khi tung ra thị trường n&ecirc;n Redmi Pad cũng kh&ocirc;ng phải l&agrave; trường hợp ngoại lệ, khi m&aacute;y c&oacute; đến 4 loa đi k&egrave;m với c&ocirc;ng nghệ &acirc;m thanh Hi-Res Audio v&agrave; Dolby Atmos mang đến &acirc;m thanh mạnh mẽ, ch&acirc;n thực như ở rạp chiếu phim.</p>\r\n<h3>Hỗ trợ quay chụp cơ bản</h3>\r\n<p>M&aacute;y c&oacute; camera sau độ ph&acirc;n giải 8 MP v&agrave; c&oacute; th&ecirc;m nhiều t&iacute;nh năng hiện đại, hứa hẹn mang đến cho bạn những bức ảnh c&oacute; độ chi tiết tốt, lưu lại mọi khoảnh khắc một c&aacute;ch trọn vẹn.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/522/291769/xiaomi-redmi-pad-4gb-241122-114906.jpg\"><img class=\" lazyloaded\" title=\"Camera chụp ảnh cơ bản - Xiaomi Redmi Pad (4GB/128GB)\" src=\"https://cdn.tgdd.vn/Products/Images/522/291769/xiaomi-redmi-pad-4gb-241122-114906.jpg\" alt=\"Camera chụp ảnh cơ bản - Xiaomi Redmi Pad (4GB/128GB)\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/291769/xiaomi-redmi-pad-4gb-241122-114906.jpg\" /></a></p>\r\n<p><a title=\"Tham khảo một số mẫu m&aacute;y t&iacute;nh bảng đang kinh doanh tại Thế Giới Di Động \" href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\" rel=\"noopener\">M&aacute;y t&iacute;nh bảng</a>&nbsp;camera selfie c&oacute; độ ph&acirc;n giải 8 MP&nbsp;điều n&agrave;y c&oacute; thể gi&uacute;p người d&ugrave;ng thực hiện c&aacute;c cuộc gọi video chất lượng hay tự chụp cho bản th&acirc;n những tấm h&igrave;nh sắc n&eacute;t.</p>\r\n<h3>Xử l&yacute; tốt c&aacute;c c&ocirc;ng việc hằng ng&agrave;y</h3>\r\n<p>MediaTek Helio G99 l&agrave; CPU được trang bị tr&ecirc;n chiếc&nbsp;<a title=\"Tham khảo một số mẫu m&aacute;y t&iacute;nh bảng Xiaomi đang kinh doanh tại Thế Giới Di Động \" href=\"https://www.thegioididong.com/may-tinh-bang-xiaomi\" target=\"_blank\" rel=\"noopener\">m&aacute;y t&iacute;nh bảng Xiaomi</a>, với một hiệu năng mạnh mẽ như th&igrave; việc xử l&yacute; tốt c&aacute;c t&aacute;c vụ từ chơi game đến c&ocirc;ng việc th&igrave; Redmi Pad đều c&oacute; thể đ&aacute;p ứng dễ d&agrave;ng. L&agrave; một chiếc&nbsp;<a title=\"Tham khảo một số mẫu m&aacute;y t&iacute;nh bảng RAM 4 GB đang kinh doanh tại Thế Giới Di Động \" href=\"https://www.thegioididong.com/may-tinh-bang-ram-4gb\" target=\"_blank\" rel=\"noopener\">m&aacute;y t&iacute;nh bảng RAM 4 GB</a>&nbsp;n&ecirc;n giờ đ&acirc;y việc chuyển đổi qua lại giữa c&aacute;c ứng dụng sẽ được diễn ra mượt m&agrave; v&agrave; hiếm khi gặp hiện tượng giật lag.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/522/291769/xiaomi-redmi-pad-4gb-241122-114220.jpg\"><img class=\" lazyloaded\" title=\"Hiệu năng ổn định - Xiaomi Redmi Pad (4GB/128GB)\" src=\"https://cdn.tgdd.vn/Products/Images/522/291769/xiaomi-redmi-pad-4gb-241122-114220.jpg\" alt=\"Hiệu năng ổn định - Xiaomi Redmi Pad (4GB/128GB)\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/291769/xiaomi-redmi-pad-4gb-241122-114220.jpg\" /></a></p>\r\n<p>T&iacute;nh năng chia nhỏ m&agrave;n h&igrave;nh tr&ecirc;n Redmi Pad rất tiện lợi cho c&ocirc;ng việc cũng như học tập khi giờ đ&acirc;y bạn c&oacute; thể xử l&yacute; một c&aacute;ch nhanh ch&oacute;ng mọi th&ocirc;ng tin v&agrave; vấn đề quan trọng. Với t&iacute;nh năng n&agrave;y bạn c&oacute; thể nhanh ch&oacute;ng k&eacute;o v&agrave; thả c&aacute;c ứng dụng m&agrave; kh&ocirc;ng cần chuyển đổi qua lại.</p>\r\n<h3>Vi&ecirc;n pin dung lượng lớn</h3>\r\n<p>Thường th&igrave; người d&ugrave;ng sẽ sử dụng tablet để phục vụ c&ocirc;ng việc kh&aacute; nhiều n&ecirc;n về thời gian sử dụng cũng như th&ocirc;ng số sạc c&oacute; lẽ l&agrave; điều được kh&aacute; nhiều kh&aacute;ch h&agrave;ng quan t&acirc;m, Xiaomi đ&atilde; chiều l&ograve;ng người d&ugrave;ng với khả năng sạc c&oacute; c&ocirc;ng suất tối đa 18 W tr&ecirc;n vi&ecirc;n pin 8000 mAh.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/522/291769/xiaomi-redmi-pad-4gb-241122-114222.jpg\"><img class=\" lazyloaded\" title=\"Vi&ecirc;n pin lớn - Xiaomi Redmi Pad (4GB/128GB)\" src=\"https://cdn.tgdd.vn/Products/Images/522/291769/xiaomi-redmi-pad-4gb-241122-114222.jpg\" alt=\"Vi&ecirc;n pin lớn - Xiaomi Redmi Pad (4GB/128GB)\" data-src=\"https://cdn.tgdd.vn/Products/Images/522/291769/xiaomi-redmi-pad-4gb-241122-114222.jpg\" /></a></p>\r\n<p>Theo như th&ocirc;ng tin từ h&atilde;ng th&igrave; với dung lượng pin 8000 mAh th&igrave; bạn c&oacute; thể sử dụng m&aacute;y 21 giờ khi xem video v&agrave; 12 giờ chơi game li&ecirc;n tục,* đ&acirc;y quả thực l&agrave; một khoảng thời gian d&agrave;i v&agrave; đảm bảo phục vụ cho bạn đủ một ng&agrave;y với mọi t&aacute;c vụ.</p>\r\n<p>*Th&ocirc;ng tin trong ph&ograve;ng th&iacute; nghiệm Xiaomi</p>\r\n<p>Xiaomi Redmi Pad (4GB/128GB) phi&ecirc;n bản tablet với nhiều c&ocirc;ng nghệ ti&ecirc;n tiến cũng như hiệu năng mạnh mẽ, đ&acirc;y hứa hẹn sẽ l&agrave; sản phẩm th&agrave;nh c&ocirc;ng của Xiaomi trong năm 2022.</p>', 69, 0, '<p class=\"lileft\">M&agrave;n h&igrave;nh:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span class=\"comma\">10.61\"</span><span class=\"\">IPS LCD</span></p>\r\n<p class=\"lileft\">Hệ điều h&agrave;nh:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span class=\"\">Android 12</span></p>\r\n<p class=\"lileft\">Chip:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span class=\"\">MediaTek Helio G99</span></p>\r\n<p class=\"lileft\">RAM:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span class=\"\">4 GB</span></p>\r\n<p class=\"lileft\">Dung lượng lưu trữ:&nbsp; &nbsp; <span class=\"\">128 GB</span></p>\r\n<p class=\"lileft\">Camera sau:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span class=\"\">8 MP</span></p>\r\n<p class=\"lileft\">Camera trước:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span class=\"\">8 MP</span></p>\r\n<p class=\"lileft\">Pin, Sạc:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span class=\"comma\">8000 mAh</span><span class=\"\">18 W</span></p>', 0, 0, '2023-04-11 18:22:37', '2023-04-11 18:23:14', 1),
(29, 'OPPO Find N2 Flip', 'oppo-find-n2-flip', 19990000, 0, 0, 'oppoflip.jpg', 11, '<h3><a title=\"Tham khảo điện thoại OPPO Find N2 Flip tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd/oppo-find-n2-flip\" target=\"_blank\" rel=\"noopener\">OPPO Find N2 Flip</a>, chiếc điện thoại gập đầu ti&ecirc;n của OPPO được giới thiệu ch&iacute;nh thức v&agrave;o th&aacute;ng 03/2023. Với cấu h&igrave;nh mạnh mẽ bao gồm con chip Dimensity 9000+ v&agrave; bộ camera nổi trội, đ&acirc;y được xem l&agrave; một trong những mẫu điện thoại đ&aacute;ng ch&uacute; &yacute; ở thời điểm hiện tại khi sở hữu bộ cấu h&igrave;nh tốt trong tầm gi&aacute;.</h3>\r\n<h3>Sử dụng ng&ocirc;n ngữ thiết kế gập hiện đại</h3>\r\n<p>Về thiết kế của m&aacute;y, Find N2 Flip sẽ được l&agrave;m theo cơ chế gập ấn tượng với tỷ lệ khung h&igrave;nh rộng v&agrave; viền mỏng. Ngo&agrave;i ra,&nbsp;<a title=\"Tham khảo điện thoại đang kinh doanh tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" rel=\"noopener\">điện thoại</a>&nbsp;c&ograve;n được trang bị một m&agrave;n h&igrave;nh phụ nhỏ ở mặt sau gi&uacute;p người d&ugrave;ng tiện lợi trong việc chụp ảnh selfie hoặc kiểm tra th&ocirc;ng b&aacute;o.&nbsp;</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/299034/oppo-find-n2-flip-150223-051048.jpg\"><img class=\" lazyloaded\" title=\"Thiết kế đẹp mắt - OPPO Find N2 Flip\" src=\"https://cdn.tgdd.vn/Products/Images/42/299034/oppo-find-n2-flip-150223-051048.jpg\" alt=\"Thiết kế đẹp mắt - OPPO Find N2 Flip\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/299034/oppo-find-n2-flip-150223-051048.jpg\" /></a></p>\r\n<p>Với cơ chế gấp gọn tiện lợi n&ecirc;n người d&ugrave;ng c&oacute; thể biến chiếc m&aacute;y của m&igrave;nh trở n&ecirc;n nhỏ nhắn hơn, ph&ugrave; hợp để bạn c&oacute; thể dễ d&agrave;ng mang theo hay bỏ t&uacute;i một c&aacute;ch gọn g&agrave;ng.</p>\r\n<h3>N&acirc;ng cấp với m&agrave;n h&igrave;nh chất lượng</h3>\r\n<p>Ở mặt trước,&nbsp;OPPO Find N2 Flip được trang bị tấm nền m&agrave;n h&igrave;nh AMOLED với ưu điểm t&aacute;i hiện h&igrave;nh ảnh c&oacute; chiều s&acirc;u, m&agrave;u sắc sinh động đầy rực rỡ. Độ ph&acirc;n giải Full HD+ tr&ecirc;n khung h&igrave;nh c&oacute; k&iacute;ch thước 6.8 inch cũng sẽ mang đến kh&ocirc;ng gian hiển thị rộng lớn, độ chi tiết tăng cường để mọi nội dung đều trở n&ecirc;n sắc n&eacute;t.</p>\r\n<p>OPPO đ&atilde; tạo ấn tượng với Find N2 Flip khi trang bị m&agrave;n h&igrave;nh phụ lớn ph&iacute;a sau, cho ph&eacute;p người d&ugrave;ng xem th&ocirc;ng b&aacute;o v&agrave; thao t&aacute;c trả lời dễ d&agrave;ng. Ngo&agrave;i ra, m&agrave;n h&igrave;nh phụ c&ograve;n gi&uacute;p bạn xem trước h&igrave;nh ảnh khi chụp ảnh selfie bằng camera sau một c&aacute;ch tiện lợi, tạo trải nghiệm sử dụng th&uacute; vị cho người d&ugrave;ng.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/299034/oppo-find-n2-flip-150223-051057.jpg\"><img class=\" lazyloaded\" title=\"N&acirc;ng cấp với m&agrave;n h&igrave;nh phụ - OPPO Find N2 Flip\" src=\"https://cdn.tgdd.vn/Products/Images/42/299034/oppo-find-n2-flip-150223-051057.jpg\" alt=\"N&acirc;ng cấp với m&agrave;n h&igrave;nh phụ - OPPO Find N2 Flip\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/299034/oppo-find-n2-flip-150223-051057.jpg\" /></a></p>\r\n<h3>Hiệu năng mạnh mẽ c&acirc;n tốt mọi t&aacute;c vụ</h3>\r\n<p>Về hiệu năng của Find N2 Flip, m&aacute;y được trang bị con chip Mediatek Dimensity 9000+ với mức xung nhịp tối đa đạt được l&agrave; 3.20 GHz, nhờ đ&oacute; m&agrave; chiếc&nbsp;<a title=\"Tham khảo điện thoại đang kinh doanh tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd-oppo\" target=\"_blank\" rel=\"noopener\">điện thoại OPPO</a>&nbsp;n&agrave;y c&oacute; thể trở n&ecirc;n ph&ugrave; hợp hơn để người d&ugrave;ng chơi được c&aacute;c tựa game đồ họa cao mượt m&agrave; hơn, thực hiện c&aacute;c t&aacute;c vụ xử l&yacute; chỉnh sửa video/h&igrave;nh ảnh với thời gian xuất cực kỳ nhanh ch&oacute;ng.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/299034/oppo-find-n2-flip-150223-051103.jpg\"><img class=\" lazyloaded\" title=\"Hiệu năng mạnh mẽ - OPPO Find N2 Flip\" src=\"https://cdn.tgdd.vn/Products/Images/42/299034/oppo-find-n2-flip-150223-051103.jpg\" alt=\"Hiệu năng mạnh mẽ - OPPO Find N2 Flip\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/299034/oppo-find-n2-flip-150223-051103.jpg\" /></a></p>\r\n<h3>Thỏa sức đam m&ecirc; nhiếp ảnh chất lượng cao</h3>\r\n<p>Nổi bật ở phần mặt sau của chiếc&nbsp;điện thoại&nbsp;n&agrave;y sẽ l&agrave; 2 ống k&iacute;nh với độ ph&acirc;n giải tối đa tr&ecirc;n camera ch&iacute;nh l&ecirc;n đến 50 MP, k&egrave;m theo đ&oacute; l&agrave; camera g&oacute;c si&ecirc;u rộng 8 MP để bạn c&oacute; thể chụp được những bức ảnh c&oacute; độ bao qu&aacute;t lớn, ph&ugrave; hợp cho những t&aacute;c vụ chụp ảnh nh&oacute;m hay muốn ghi h&igrave;nh ở những khung cảnh bao la h&ugrave;ng vĩ.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/299034/oppo-find-n2-flip-150223-051125.jpg\"><img class=\" lazyloaded\" title=\"Camera chụp ảnh g&oacute;c si&ecirc;u rộng - OPPO Find N2 Flip\" src=\"https://cdn.tgdd.vn/Products/Images/42/299034/oppo-find-n2-flip-150223-051125.jpg\" alt=\"Camera chụp ảnh g&oacute;c si&ecirc;u rộng - OPPO Find N2 Flip\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/299034/oppo-find-n2-flip-150223-051125.jpg\" /></a></p>\r\n<h3>D&ugrave;ng l&acirc;u hơn với pin lớn hơn</h3>\r\n<p>Nhằm mang đến thời gian sử dụng d&agrave;i l&acirc;u hơn h&atilde;ng cũng đ&atilde; trang bị cho m&aacute;y một vi&ecirc;n pin c&oacute; dung lượng 4300 mAh, đ&acirc;y được xem l&agrave; mức dung lượng đủ để bạn thực hiện c&aacute;c t&aacute;c vụ th&ocirc;ng thường cả ng&agrave;y m&agrave; kh&ocirc;ng cần lo lắng đến vấn đề hết thời pin giữa chừng.</p>\r\n<p><a class=\"preventdefault\" href=\"https://cdn.tgdd.vn/Products/Images/42/299034/oppo-find-n2-flip-150223-051132.jpg\"><img class=\" lazyloaded\" title=\"Sử dụng d&agrave;i l&acirc;u - OPPO Find N2 Flip\" src=\"https://cdn.tgdd.vn/Products/Images/42/299034/oppo-find-n2-flip-150223-051132.jpg\" alt=\"Sử dụng d&agrave;i l&acirc;u - OPPO Find N2 Flip\" data-src=\"https://cdn.tgdd.vn/Products/Images/42/299034/oppo-find-n2-flip-150223-051132.jpg\" /></a></p>\r\n<h3>So s&aacute;nh th&ocirc;ng số kỹ thuật giữa OPPO Find N2 Flip v&agrave; OPPO Find X5 Pro</h3>\r\n<p>Dưới đ&acirc;y l&agrave; bảng tổng hợp c&aacute;c danh mục về th&ocirc;ng số kỹ thuật của OPPO Find N2 Flip v&agrave;&nbsp;<a title=\"Tham khảo điện thoại OPPO Find X5 Pro đang kinh doanh tại Thế Giới Di Động\" href=\"https://www.thegioididong.com/dtdd/oppo-find-x5-pro\" target=\"_blank\" rel=\"noopener\">OPPO Find X5 Pro</a>&nbsp;vừa được c&ocirc;ng bố:</p>\r\n<table border=\"1\" cellspacing=\"1\" cellpadding=\"1\">\r\n<tbody>\r\n<tr>\r\n<td><strong>&nbsp;Thuộc t&iacute;nh</strong></td>\r\n<td><strong>&nbsp;OPPO Find N2 Flip</strong></td>\r\n<td><strong>&nbsp;OPPO Find X5 Pro</strong></td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;M&agrave;n h&igrave;nh</td>\r\n<td>&nbsp;AMOLED 6.8 inch</td>\r\n<td>&nbsp;AMOLED 6.7 inch</td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;Kiểu m&agrave;n h&igrave;nh</td>\r\n<td>&nbsp;M&agrave;n h&igrave;nh gập</td>\r\n<td>&nbsp;M&agrave;n h&igrave;nh phẳng</td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;Chip xử l&yacute;</td>\r\n<td>&nbsp;MediaTek Dimensity 9000+ 8 nh&acirc;n</td>\r\n<td>&nbsp;Snapdragon 8 Gen 1 8 nh&acirc;n</td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;Camera sau</td>\r\n<td>&nbsp;Ch&iacute;nh 50 MP &amp; Phụ 8 MP</td>\r\n<td>&nbsp;Ch&iacute;nh 50 MP &amp; Phụ 50 MP, 13 MP</td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;Camera trước</td>\r\n<td>&nbsp;32 MP</td>\r\n<td>&nbsp;32 MP</td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;RAM&nbsp;</td>\r\n<td>&nbsp;8 GB</td>\r\n<td>&nbsp;12 GB</td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;Dung lượng pin</td>\r\n<td>&nbsp;4300 mAh v&agrave; sạc si&ecirc;u nhanh 44 W</td>\r\n<td>&nbsp;5000 mAh v&agrave; sạc si&ecirc;u nhanh 80 W</td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;M&agrave;u sắc</td>\r\n<td>&nbsp;T&iacute;m v&agrave; đen</td>\r\n<td>&nbsp;Trắng v&agrave; đen</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h3>Video đ&aacute;nh gi&aacute; chi tiết OPPO Find N2 Flip</h3>\r\n<p>Dưới đ&acirc;y sẽ l&agrave; video đ&aacute;nh gi&aacute; chi tiết&nbsp;OPPO Find N2 Flip với đầy đủ th&ocirc;ng tin từ h&igrave;nh ảnh cho đến trải nghiệm thực tế người d&ugrave;ng:</p>\r\n<p>&nbsp;</p>\r\n<div class=\"article__content__video-container\"><iframe id=\"162826283\" title=\"Unbox OPPO Find N2 Flip: GI&Aacute; SI&Ecirc;U TỐT, gập kh&ocirc;ng nếp gấp | Thế Giới Di Động\" src=\"https://www.youtube.com/embed/oolSFK5csNM?rel=0&amp;enablejsapi=1&amp;origin=https%3A%2F%2Fwww.thegioididong.com\" width=\"560\" height=\"315\" frameborder=\"0\" allowfullscreen=\"allowfullscreen\" data-gtm-yt-inspected-1070012_61=\"true\" data-gtm-yt-inspected-1070012_79=\"true\" data-gtm-yt-inspected-1070012_187=\"true\" data-gtm-yt-inspected-10=\"true\" data-mce-fragment=\"1\"></iframe></div>\r\n<p>&nbsp;</p>\r\n<h3>C&acirc;u hỏi thường gặp trước khi mua OPPO Find N2 Flip l&agrave; g&igrave;?</h3>\r\n<p><strong>C&oacute; tạo ra khoảng hở lớn giữa hai phần m&agrave;n h&igrave;nh khi gập m&aacute;y kh&ocirc;ng?</strong></p>\r\n<p>C&acirc;u trả lời ở đ&acirc;y l&agrave; kh&ocirc;ng, ở phi&ecirc;n bản điện thoại gập mới n&agrave;y của OPPO, h&atilde;ng đ&atilde; n&acirc;ng cấp phần bản lề rất tốt c&ugrave;ng kiểu cấu tạo m&agrave;n h&igrave;nh ở nếp gấp th&ocirc;ng minh gi&uacute;p hai phần m&agrave;n h&igrave;nh tiếp gi&aacute;p s&aacute;t lại với nhau v&agrave; gần như kh&ocirc;ng tạo ra khoảng hở, đ&acirc;y cũng được xem l&agrave; điều &iacute;t c&oacute; chiếc điện thoại gập n&agrave;o hiện đang tr&ecirc;n thị trường c&oacute; được.</p>\r\n<p><strong>OPPO Find N2 Flip&nbsp;c&oacute; thể gập tối đa bao nhi&ecirc;u lần?</strong></p>\r\n<p>Theo như th&ocirc;ng tin từ h&atilde;ng c&ocirc;ng bố tại sự kiện ra mắt,&nbsp;OPPO Find N2 Flip đ&atilde; được T&Uuml;V Rheinland chứng nhận về độ bền với khả năng gập tối đa l&ecirc;n đến 400.000 lần ở nhiệt độ ph&ograve;ng, đ&acirc;y quả thực l&agrave; một con số hết sức ấn tượng gi&uacute;p cho người d&ugrave;ng c&oacute; thể sử dụng trong 10 năm&nbsp;với khoảng 100 lần một ng&agrave;y.</p>\r\n<p><strong>C&oacute; thể d&ugrave;ng m&agrave;n h&igrave;nh phụ của&nbsp;OPPO Find N2 Flip&nbsp;để thực hiện c&aacute;c t&aacute;c vụ g&igrave;?</strong></p>\r\n<p>Ở&nbsp;OPPO Find N2 Flip, m&aacute;y c&oacute; một m&agrave;n h&igrave;nh phụ rất lớn với k&iacute;ch thước l&ecirc;n đến 3.26 inch, nhờ vậy m&agrave; người d&ugrave;ng c&oacute; thể d&ugrave;ng m&agrave;n h&igrave;nh phụ n&agrave;y để sử dụng c&aacute;c t&aacute;c vụ như xem v&agrave; trả lời tin nhắn bằng c&aacute;c đoạn văn bản t&ugrave;y chỉnh sẵn, hiển thị m&atilde; QR code hay mở nhanh c&aacute;c tiện &iacute;ch như ghi &acirc;m, thời tiết, bộ đếm thời gian hay camera.</p>\r\n<p><strong>OPPO Find N2 Flip c&oacute; tự động gập m&agrave;n h&igrave;nh chỉ khi đ&oacute;ng nhẹ m&agrave;n h&igrave;nh.</strong></p>\r\n<p>Một điểm n&acirc;ng cấp hết sức đ&aacute;ng gi&aacute; tr&ecirc;n m&aacute;y l&agrave; chế độ&nbsp;FlexForm của OPPO, t&iacute;nh năng n&agrave;y cho ph&eacute;p m&agrave;n h&igrave;nh của m&aacute;y tự động đứng vững trong khoảng gấp 45 -110 độ m&agrave; kh&ocirc;ng bị tự động gấp lại mỗi khi đ&oacute;ng nhẹ như tr&ecirc;n một v&agrave;i mẫu điện thoại gập đến từ c&aacute;c h&atilde;ng kh&aacute;c.</p>\r\n<p>OPPO Find N2 Flip được xem l&agrave; một trong những đối thủ cạnh tranh đ&aacute;ng gờm trong thị trường điện thoại gập hiện nay, với m&agrave;n h&igrave;nh chất lượng c&ugrave;ng con chip hiệu năng cao n&ecirc;n m&aacute;y ho&agrave;n to&agrave;n ph&ugrave; hợp để bất kỳ ai cũng c&oacute; thể sử dụng tốt từ chụp ảnh, quay phim cho đến việc chơi game đồ họa cao.</p>', 19, 0, '<p class=\"lileft\">M&agrave;n h&igrave;nh:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span class=\"comma\">AMOLED</span><span class=\"comma\">Ch&iacute;nh 6.8\" &amp; Phụ 3.26\"</span><span class=\"\">Full HD+</span></p>\r\n<p class=\"lileft\">Hệ điều h&agrave;nh:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span class=\"\">Android 13</span></p>\r\n<p class=\"lileft\">Camera sau:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span class=\"\">Ch&iacute;nh 50 MP &amp; Phụ 8 MP</span></p>\r\n<p class=\"lileft\">Camera trước:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span class=\"\">32 MP</span></p>\r\n<p class=\"lileft\">Chip:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span class=\"\">MediaTek Dimensity 9000+ 8 nh&acirc;n</span></p>\r\n<p class=\"lileft\">RAM:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span class=\"\">8 GB</span></p>\r\n<p class=\"lileft\">Dung lượng lưu trữ:&nbsp; &nbsp; <span class=\"\">256 GB</span></p>\r\n<p class=\"lileft\">SIM:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span class=\"comma\">2 Nano SIM</span><span class=\"\">Hỗ trợ 5G</span></p>\r\n<p class=\"lileft\">Pin, Sạc:&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <span class=\"comma\">4300 mAh</span><span class=\"\">44 W</span></p>', 0, 0, '2023-04-11 18:26:54', '2023-04-11 18:27:58', 1);
